INSERT INTO `games` (`game_ID`, `title`, `description`, `created`, `owner`, `category_ID`) VALUES
(6, 'Rūķis prātotājs', 'Spēle visai ģimenei - arī pieaugušajiem. Tās vadītājs uzdod visiem kopīgi dažādus jautājumus par un ap Ziemassvētkiem. Katrs dalībnieks pieraksta atbildes uz savas lapiņas. Beigās lapiņas tiek savāktas un izskaitļots, kuram ir visvairāk pareizo atbilžu. Pēc tam tās kopīgi pārrunā - tādējādi spēle ir arī interesants veids, kā bērniem uzzināt vairāk par Ziemassvētku vēsturi un tradīcijām. Grūtākiem jautājumiem var dot atbilžu variantus.', '2012-08-07 08:31:13', '89.201.84.146', 3),
(7, 'Laimes aka', 'Jau gadiem interesants veids, kā apmainīties ar dāvanām. No visām dāvanām tiek izveidota "laimes aka" aiz aizslietņa, kur katrs izvelk kādu dāvaniņu ar makšķeres kātu, tādā veidā saglabājot pārsteiguma momentu – kas tad tur īsti pieķersies! Pēc tam ar dāvanām iespējams savā starpā samanīties, ja ir tāda abpusēja vēlme starp kādiem diviem “makšķerniekiem”.', '2012-08-07 08:31:41', '89.201.84.146', 3),
(8, 'Sniegavīra deguns', 'Šī ir jautra spēle jaunākiem bērniem. Izgrieziet no sarkana, kartonveidīga materiāla vairākus sarkanus aplīšus (tik, cik bērnu piedalīsies spēlē), kas līdzināsies Sniegavīra degunam. Tad katram bērnam ar apsēju uz acīm jāmēģina uzlikt savu sarkano "degunu" uzzīmēta sniegavīra attēlā. Tas, kuram to izdodas novietot vistuvāk paredzētajai ( atzīmētajai) vietai, būs uzvarējis.', '2012-08-07 08:32:01', '89.201.84.146', 3),
(9, 'Personāžu spēle', 'Aizraujoša spēle visa vecuma viesiem! Pierakstiet uz lapiņām visus personāžus, kas nāk prātā Ziemassvētku sakarā - ziemeļbriedis, Sniegbaltīte, rūķis, Sniegavīrs, Grinčs, Ziemassvētku vecītis u.tml. Katrs spēlētājs izvelk savu personāža aprakstu no cepures, un iejūtas personāža raksturīpašībās, žestos. Savukārt, pārējie mēģina atminēt, kas tas par tēlu ar dažādu jautājumu palīdzību.', '2012-08-07 08:32:35', '89.201.84.146', 3),
(10, 'Ziemassvētku atribūtu medības.', 'Telpās jānovieto dažādi objekti, kas saistīti ar Ziemassvētkiem. Piemēram, tipiskā Ziemassvētku vecīša zeķe, cepure, eglītes mantiņa un citas lietas. Tad visiem bērniem  uzdevums sameklēt apslēptos "dārgumus". Tas, kurš pirmais atnes trīs mantas spēles vadītājam, ir uzvarētājs.', '2012-08-07 08:33:11', '89.201.84.146', 3),
(11, 'Skanīgās dziesmas', 'Spēlētāji jānosēdina aplī. Spēles vadītājs aizsāk dziedāt kādu svētku dziesmu - plaši zināmu, kā, piemēram, "Zvaniņš skan". Katram nākošajam spēlētājam jānodzied pa vienai dziesmas rindiņai. Tas, kuram pirmajam piemirstās tālākie vārdi, izkrīt no spēles līdz nākošai dziesmai. Šajā spēlē nav būtiski noteikt uzvarētāju, bet gan no sirds izdziedāties! Vislabākajam dziedātājam var izsludināt kādu balviņu, kas kalpos par labu motivāciju.', '2012-08-07 08:41:44', '89.201.84.146', 3),
(12, 'Gardo piparkūku spēle', 'Jeb vienkārši sakot, kopīga piparkūku dekorēšana. Apbruņojušies ar garnēšanas "tūtiņām", katrs bērns glazē savu lielo piparkūku ar kādu interesantu, pašizdomātu zīmējumu. Pēc tam visi var apmainīties piparkūkām ar blakus biedru vai notiesāt katrs savējo. <br />\r\nGardu un omulīgu svinēšanu!', '2012-08-07 08:42:09', '89.201.84.146', 3),
(13, 'Atvelc lūpu', 'Spēles vadītājs kādam spēlētājam aizsien acis un atvelk lūpu. Uz atvilktās lūpas citi spēles dalībnieki liek visu, kas tiem kabatās, piem., cigareti, 5 latus 20 sant., diegu spolīti, kredītkarti utt. Jāuzmin, kas uz lūpas. Ja uzmin, tad mainās.', '2012-08-07 11:53:13', '89.201.84.146', 2),
(14, 'Paslēpes', 'Cilvēki slēpjas viens no otra', '2012-08-13 19:35:06', '89.201.84.146', 2);
