INSERT INTO `regards` (`regard_ID`, `poem`, `author`, `created`) VALUES
(2, 'Katra diena ir diena,<br />\r\nlai atpūstos un sapņotu,<br />\r\nlai smietos un brīnītos,<br />\r\nlai mīlētu un baudītu mīlu,<br />\r\nlai runātu un klausītos,<br />\r\nlai priecātos un justos laimīgs.<br />\r\nKatra diena ir diena,<br />\r\nlai dzīvotu! <br />\r\nDaudz laimes dzimšanas dienā!', '', '2012-07-25 21:27:01'),
(3, 'Lai viss ko vēlies - PIEPILDĀS.<br />\r\nLai viss ko dari - IZDODĀS,<br />\r\nLai viss ko gribi ir SASNIEDZAMS.<br />\r\nBet nekad lai nav tā, ka nav NEKĀ!', '', '2012-07-25 21:27:19'),
(4, 'Savos gados tu vari atļauties.....<br />\r\n- iedzert mazliet vairāk nekā drīkst<br />\r\n- dejot mazliet nerātnāk nekā pieklājas<br />\r\n-dziedāt mazliet skaļāk nekā citi<br />\r\n-un ja kļūst karstāk nekā vari izturēt...<br />\r\ndroši izģērbies mazliet vairāk nekā vajag.', '', '2012-07-25 21:27:37'),
(5, 'Būt vienmēr veselai un jaunai,<br />\r\nUn just, ka spēks pār malām iet,<br />\r\nAiz spēka pārpilnības raudāt,<br />\r\nAiz spēka pārpilnības smiet.<br />\r\nZvaigznes izdejot no grīdas,<br />\r\nNo griestiem sauli izdziedāt.<br />\r\nUn magonīšu gultu taisīt,<br />\r\nUn rožu paladziņu klāt.<br />\r\nNo rītiem pamosties aiz laimes,<br />\r\nar basām kājām dārzā skriet.<br />\r\nBūt vienmēr veselai un jaunai,<br />\r\nkad pašas sirds Tev zied.', '', '2012-07-25 21:27:53'),
(6, 'Novēlu Tev pārsteigumu, kas rada prieku,<br />\r\nPrieku, kas rada iedvesmu;<br />\r\nIedvesmu, kas iepriecina draugus,<br />\r\nDraugus, kas sagādā pārsteigumus !', '', '2012-07-25 21:28:17'),
(7, 'Es nevēlēšu Tev zvaigžņu lietu, kad vēlēšanos ir daudz, tām nav jēgas. <br />\r\nEs novēlu Tev vienu vienīgo zvaigzni, bet tādu, kura izpildīs Tavu viskvēlāko vēlēšanos. <br />\r\nKad dzīvē daudz saldumu, tad zūd dzīves garša, tāpēc es novēlu tev vienu vienīgo torti, bet ar svecēm, kuras nopūšot, Tev piepildīsies iecerētais. <br />\r\nKad apkārt daudz cilvēku, tas rada drūzmu, tāpēc nevēlēšu Tev daudz draugu. <br />\r\nEs novēlēšu Tev, lai tie draugi, kas tev ir pašlaik, ir tādi, kuri nāk kad tie vajadzīgi, iet, kad tie traucē un nelien ar padomiem, kad Tev pašam jāgūst pieredze.<br />\r\nTāpēc tā: DAUDZ LAIMES DZIMŠANAS DIENĀ !!!!!', '', '2012-07-25 21:28:29'),
(8, 'Līksmai dienai - putnu dziesma,<br />\r\nAukstai - ugunskura liesmu, <br />\r\nLabam miegam - jūras šalku, <br />\r\nLielām slāpēm - rasas malku, <br />\r\nKarstai dienai - vēju lēnu, <br />\r\nNogurumam - vēsu ēnu, <br />\r\nSkumjām - pūpolmīkstu glāstu, <br />\r\nTukšiem brīžiem - brīnumstāstu, <br />\r\nTumšai naktij - zvaigžņu lietu, <br />\r\nSirdī mīlai - siltu vietu!', '', '2012-07-25 21:29:03'),
(9, 'Šodien un vienmēr vēlu Tev mīlēt to, ko ir vērts mīlēt, aizmirst to, ko nav vērts atcerēties, lepoties ar to, kas ir lepnuma vērts, gūt atelpu arī steigas brīdī un uzcelt žogu starp sevi un raizēm. Un vēl - lai nesteidzīgs laiks Tavos laimes brīžos un lai ātri aizzib tās dienas, kas nav laipnas pret Tevi', 'Džons Raskins', '2012-07-25 21:29:22'),
(10, 'Laimi kā zilu pasaku,<br />\r\nSmieklus kā pavasara lietutiņu,<br />\r\nSpītību kā ūdens burbuli,<br />\r\nSavaldību kā akmens krūzi,<br />\r\nBurvību kā pasakās,<br />\r\nBet pāri visam mīlestību kā varavīksni!', '', '2012-07-25 21:29:42'),
(11, 'Šī diena lai Tev skaistāka par citām,<br />\r\nŠī diena tikai reizi gadā aust,<br />\r\nLai pietiek spēka katram dzīves rītam,<br />\r\nLai katrs rīts kā skaisti ziedi plaukst.', '', '2012-07-25 21:29:53'),
(12, 'Ar sniega pieneņziedu krēma un saldo skūpstu šokolādes kūku,<br />\r\nAr nejauši vakarnakt zvaigznēs atrasto dzirksti acīs<br />\r\nUn mutuļojošo jūtu šampanieša sirdi<br />\r\nEs klusiņām, uz pašiem pirkstu galiem Tev pieslīdēšu klāt,<br />\r\nLai, liegi lūpām pieskardamās Tavai ausij, kā sapnī čukstētu,<br />\r\nKa dāvāju Tev šokolādes skūpstu, līdz mana dzirksts ir arī Tavās acīs<br />\r\nUn novēlu, lai kaislē mutuļo Tev jūtas, līdz noreibst Tava šampanieša sirds.', '', '2012-07-25 21:30:05'),
(13, 'Sen jau es šo dienu gaidu,<br />\r\nLai ar lielu, platu smaidu<br />\r\nTeiktu vienā paņēmienā:<br />\r\nMīļi sveicu dzimšan`s dienā!', '', '2012-07-25 21:30:16'),
(14, 'Maz enģelīts man priekšā teic,<br />\r\nKa Tevi šodien jāapsveic!<br />\r\nLai mīlestība apkart valda,<br />\r\nUn nedienas ar būltām skalda!', '', '2012-07-25 21:30:26'),
(15, 'Mīlestību vairāk kā vakar,<br />\r\nSapņus, kas piepildās,<br />\r\nTorti, kas nesakalst<br />\r\nun draugus, kuri vienkārši ir! <br />\r\nDaudz Laimes!', '', '2012-07-25 21:30:46'),
(16, 'Skaiti savas naktis pēc zvaigznēm,<br />\r\nbet ne pēc ēnām.<br />\r\nSkaiti savu dzīvi pēc smaidiem,<br />\r\nbet ne pēc asarām.<br />\r\nUn ar prieku katrā dzimšanas dienā,<br />\r\nSkaiti savu vecumu pēc draugiem,<br />\r\nbet ne pēc gadiem.', '', '2012-07-25 21:30:56'),
(17, 'Dzīvē - naudu, seksā - baudu, <br />\r\ndzimšanas dienā - jaunu jaudu!', '', '2012-07-25 21:31:06'),
(18, 'Atļauj šodien saviem gadiem ziedēt<br />\r\nkā ziedlapiņām, kuras saulē plaukst,<br />\r\nAtļauj šodien saviem gadiem skanēt<br />\r\nkā melodijai, kura sirdī šalc.<br />\r\nĻauj kādam sapnim šodien zvaigznēs mirdzēt,<br />\r\nLai dienu skrejā tie pie tevis trauc!', 'A.Āre', '2012-07-25 21:31:25'),
(19, 'Aiz muguras jaunības trakums,<br />\r\nVēl tālu vecuma miers<br />\r\nBet vidū tie trakākie gadi,<br />\r\nKā kaklā iesprūdis siers...<br />\r\nUn vidū tie aplamie gadi,<br />\r\nKad vieglums sāk sirdi spiest,<br />\r\nKad gribas par kādu rūpēties,<br />\r\nKad gribas par kādu ciest..', 'A.Skalbe', '2012-07-25 21:31:43'),
(20, 'Lai uzzinātu, cik iela nozīme ir vienam gadam,pajautā to studentam, kurš izkritis gala eksāmenā.<br />\r\nJa gribi uzzināt cik vērtīgs ir viens mēnesis,pajautā māmiņai, kurai priekšlaikus piedzimis mazulis. <br />\r\nLai zinātu cik vērtīga ir viena nedēļa, pajautā to nedēļas laikraksta izdevējam.<br />\r\nLai uzzinātu cik vērtīga ir viena stunda, pajautā tiem kas iemīlējušies un gaida, lai viens otru ieraudzītu.<br />\r\nLai uzzinātu cik vērta ir viena minūte,pajautā to kādam, kurš nokavējis savu vilcienu, autobusu vai lidmašīnu.<br />\r\nLai zinātu vienas sekundes vērtību, pajautā to kādam kurš izdzīvojis nelaimes gadījumā.<br />\r\nLai uzzinātu milisekundes vērtību, pajautā to kādam, kurš Olimiādē ieguvis sudraba medaļu.<br />\r\nLaiks nevienu negaida. Būs vērtīgi, ja krāsi katru momentu, kas Tev dots.Dalies tajā ar ipašiem cilvēkiem savā dzīvē, tad tas būs vērtīgāks!!!!<br />\r\nDaudz laimes svētkos:)', '', '2012-07-25 21:31:53'),
(21, 'Tavos svētkos vēlu tev<br />\r\nTo, ko vēlējos es sev:<br />\r\nPrieku, naudu, puķu vāzi,<br />\r\nPudeli un lielu glāzi.', '', '2012-07-25 21:32:02'),
(22, 'Palūdz pavasari, <br />\r\nLai tas spēku aizdod. <br />\r\nPalūdz puteņiem, <br />\r\nLai gadiem pēdas jauc. <br />\r\nPalūdz pašai sirdij- tā vislabāk zinās, <br />\r\nkādus gadus atpakaļ lai sauc. <br />\r\nGadu straumē nestas aiziet dienas, <br />\r\nDzīves jūrā saplūst vienuviet. <br />\r\nLai vēl ilgi mirdz Tev ceļa zvaigzne, darbi veicas, gaišas dienas rit. <br />\r\nLai gadi rit, tā tam ir jābūt <br />\r\nUn lai par tiem nekad nav žēl. <br />\r\nTik jautru prātu, sauli sirdī <br />\r\nUn daudzus skaistus gadus vēl.', '', '2012-07-25 21:32:30'),
(23, 'Dzīve ir dīvaina, lai kā uz to raudzītos. Mēdz teikt, ka dzīve ir ceļojums. Bet uz kurieni? Atrodi savu īsto ceļu dzīvē un dodies galvu reibinošā ceļojumā!<br />\r\n<br />\r\nDaudz buču dzimenītē!', '', '2012-07-25 21:32:41'),
(24, 'Piecdesmit gadi - tas ir tāds slieksnis,<br />\r\nPie kura tiek izvērtēts cilvēka mūžs,<br />\r\nAr citām acīm skatot,kas bijis,<br />\r\nAr prātu uzlūkot to,kas vēl būs.<br />\r\nUn laimīgs ir tas,kas vienmēr pa dzīvi <br />\r\nAr mīlu sirdī pratis ir iet,<br />\r\nKas radis teikt gaišos un mīļos vārdus,<br />\r\nKam prieku dāvājis citu prieks.<br />\r\nIzvērtēts tiek viss-gan prieks,gan bēdas,<br />\r\nUn darba solis,reizēm tik smags,<br />\r\nUn arī raudātās asaru pēdas,<br />\r\nNo citiem ko slēpusi tumšākā nakts.<br />\r\nPiecdesmit gados tik daudz jau ir paveikts<br />\r\nUn reizēm šķiet,spēka kauss izsmelts jau sauss,<br />\r\nBet visu to atsver vislielākais guvums,<br />\r\nTev dārgu un mīļu cilvēku tuvums.', 'Lauma Daugiša', '2012-07-25 21:32:51'),
(25, 'Lai silta vasara,<br />\r\nLai ziemā sniegs;<br />\r\nLai darbs, lai draugi,<br />\r\nUn pa reizei prieks;<br />\r\nLai dvēsele var baltu domu dziju vīt,<br />\r\nLai atnāk viss, ko gribas sagaidīt!!!', '', '2012-07-25 21:33:50'),
(26, 'Šai dienai jābūt neaizmirstamai bezgala daudzo dienu virknē. Lai tajā ir smiekli un dziesmas, un mīļi, tuvi cilvēki, un jaunas cerības, jauni piedzīvojumi un jaunas iespējas. Apsveikumu, tostu un līksmības vidū atrodi brīdi, kad pamest skatu uz skaistākajiem mirkļiem pagātnē un apjaust to, cik daudz laba un gaiša var piedāvāt nākotne.<br />\r\n<br />\r\nLai tev nekad nepietrūkst darāmā darba, lai nepietrūkst iespēju, draugu tuvuma, mīlestības un sirdsmiera. Skaties nākotnē ar cerību, drosmi un apņēmību, ar sparu un līksmu prātu. Jo šīs ir tās lietas, no kurām var veidot dzīvi, kādu patiešām ir vērts dzīvot.<br />\r\n<br />\r\nŠodien un vienmēr vēlu tev mīlēt to, ko ir vērts mīlēt, aizmirst to, ko nav vērts atcerēties, lepoties ar to, kas ir lepnuma vērts, gūt atelpu arī steigas brīdī un uzcelt žogu starp sevi un raizēm.<br />\r\nUn vēl- lai nesteidzīgs ir laiks tavos laimes brīžos un lai ātri aizzib tās dienas, kas nav laipnas pret tevi!<br />\r\nDaudz laimes! :)', '', '2012-07-25 21:34:01'),
(27, 'Lai tev vienmēr jautra oma,<br />\r\nPriecīgs prāts un gudra doma,<br />\r\nLai tev vienmēr pietiek spara<br />\r\nDarīt to, ko jāpadara!', '', '2012-07-25 21:34:18'),
(28, 'Lai Tev laime brauc ar dampi,<br />\r\nVeselība pakaļ skrien,<br />\r\nNaudas maks lai dabū krampi<br />\r\nNo tiem spožiem latiņiem.', '', '2012-07-25 21:34:29'),
(29, 'Mirdzumu acīs un mirdzumu dvēselē,<br />\r\nVeselību pašam un veselus mīļos,<br />\r\nLaiku zvaigžņu skaitīšanai un laiku rīta bučai,<br />\r\nMazus brīnumus ikdienā un lielus brīnumus sirdī,<br />\r\nEņģeli uz pleca un mīļu cilvēku blakus,<br />\r\nMīlestību pret sevi pašu un mīlestību<br />\r\npret pasauli aiz loga...', '', '2012-07-25 21:34:38'),
(30, 'Lai vienmēr<br />\r\ndvēsele Tava<br />\r\nkā brīnumsvecīte zied, <br />\r\nlai ziedi, zvaigznes un sapņi<br />\r\ntur savākti vienuviet!<br />\r\nLai vienmēr ir<br />\r\nradoša doma, <br />\r\nkas apvāršņa<br />\r\ntālumos skrien, <br />\r\nlai esi kā jūra, <br />\r\nkā joma, <br />\r\nkas dzintara<br />\r\npilna arvien!', 'Kornēlija Apškrūma', '2012-07-25 21:34:55'),
(31, 'Ar elegantu pietupienu<br />\r\nAtraktīvu palēcienu,<br />\r\nFantastisku pagriezienu-<br />\r\nSvini savu DZIMŠANAS dienu', '', '2012-07-25 21:35:04'),
(32, 'Dejo un dziedi, skūpsti un mīli...<br />\r\nSkaistākos sapņus pārvērt par dzīvi!', '', '2012-07-25 21:35:12'),
(33, 'Smaidi šodien!<br />\r\nŠī diena ir tava!<br />\r\nSkumjas un ilgas lai gaist,<br />\r\nSmejies šodien, šī diena ir tava,<br />\r\nViss šai dienā, lai veltīts ir tev!', '', '2012-07-25 21:35:21'),
(34, 'Vecums trakumam nebūs par šķērsli,<br />\r\nvecums jau arī aiz kalniem vēl vīd,<br />\r\nTagad tev labākie gadi tik sākas,<br />\r\nacīs nemiera dzirksteles spīd.<br />\r\nPar taviem gadiem pacelsim kausu,<br />\r\nnovēlot laimi, izdzersim sausu,<br />\r\nlai visu, ko vēlies, spētu tu gūt<br />\r\nun vienmēr ar mīļiem cilvēkiem būt.', 'Lauma Daugiša', '2012-07-25 21:35:35'),
(35, 'Kaut laikmets spītīgs, bargs un viltīgs,<br />\r\nKaut gadsimtenis šķībs un greizs,<br />\r\nNo dzīves nebēdz, dzīvi svētī,<br />\r\nKā svētkus, kuri beigsies reiz.<br />\r\n<br />\r\nUn naudu neskaiti, nedz gadus,<br />\r\nBet mirkļus, kuros proti degt.<br />\r\nJo vieglprātībai ir tāds mirdzums,<br />\r\nKo smilts, ne zeme nespēj segt. <br />\r\n<br />\r\nŅem spārnus, rūpēm pāri laidies,<br />\r\nKā taurenis zem saules spožs,<br />\r\nJa nevari ar naudu šķiesties, <br />\r\nAr prieku šķiesties vari drošs.', '', '2012-07-25 21:35:48'),
(36, 'Šodien......<br />\r\nVisi vēl Tev Laimi.<br />\r\nZiedus .....<br />\r\nKlēpiem dāvāt steidz.<br />\r\nArī es......<br />\r\nTev gribu vēlēt,<br />\r\nDzīvi mīlēt neizbeidz.<br />\r\nDroši ej....<br />\r\nPa savu ceļu,<br />\r\nPretī sapnhiem steidz<br />\r\nUn lai nepietrūkst Tev spēka,<br />\r\nDzīvē....<br />\r\nSapņus īstenot.', 'kurshininks', '2012-07-25 21:36:03'),
(38, 'Mēdz teikt - ir jānotiek brīnumam. Par lietām par kurām Tu sapņo un ilgojies, bet īsti netici, jo ... pārāk skaisti, lai būtu patiesi. Tomēr brīnumi notiek ik uz soļa, ja proti tos pamanīt un noticēt.', '', '2012-07-25 21:36:58'),
(39, 'Ir tads teiciens, ka tad, kad berns piedzimst, pie vinja no debesim nokapj Dievs un nobucho to. Ja Dievs nobuccho pieri, tad berns bus gudrs, ja rokas- vinsh bus stradigs, centigs, ja sirdi- berns bus sirsnigs, miljsh, lidzjutigs. Kad Dievs nokapa pie Tevis, vinsh nobuchoja gan galvu, gan rokas, gan sirdi.<br />\r\nDaudz laimes Dzimshanas diena!!!!!!!!!!', '', '2012-07-25 21:37:06'),
(40, 'Lai ar gadiņš uzkrīt plecam,<br />\r\nBet vai tāpēc jākļūst vecam?<br />\r\nJa ir sirdī dzīves prieks,<br />\r\nGadu skaits ir tīrais nieks!', '', '2012-07-25 21:37:15'),
(41, 'Daudz, daudz laim un lustīg prāt,<br />\r\nLai Tev nauda čupēm nāk,<br />\r\nLai Tu vinnē Volg vai Čaik,<br />\r\nLai Tev netrūkst šnabs un paik!', '', '2012-07-25 21:39:12'),
(42, 'Esi sieviete grēcīga,esi lepna un spēcīga,<br />\r\nEsi putna spārni, kas vēzienā atvilkti.<br />\r\nDrīksti visu, ko sieviete drīkst,<br />\r\nCelies augstu, lido tālu, esi laimīga!', '', '2012-07-26 09:11:17'),
(43, 'Vai tiešām labākais jau pagājis!?<br />\r\nNĒ...<br />\r\nLabākais vēl priekšā...<br />\r\nDrīz logā smiesies pavasara rīti,<br />\r\nkas uzdāvinās<br />\r\nprieka vizbulīti..<br />\r\nVēl laiks tev atvēlējis daudz-<br />\r\nteikt labus vārdus,<br />\r\nsmaidus dāvināt,<br />\r\nun mīlestības varavīksnes krāt....', '', '2012-07-26 09:11:27'),
(44, 'Un kas par to, ka gadu vesels klēpis,<br />\r\nKā pļavu ziedi laika vāzēs mirdz,<br />\r\nGan sūrumu, ganprieku katrs slēpis,<br />\r\nBet viss ir bijis vajadzīgs priekš sirds.<br />\r\nLai sajustu - cik dzīve tomēr skaista,<br />\r\nKur tā kā darba bite vari būt,<br />\r\nTo sudrabu no mirkļa kārēm vācot,<br />\r\nKas neapsūb un vējos nepazūd!', 'K.Apšukrūma', '2012-07-26 09:11:43'),
(45, 'Lai vienmēr ir ,ko citiem dot,<br />\r\nLai vienmēr ir,ko samīļot,<br />\r\nLai vienmēr ir kāds,kura dēļ,<br />\r\nNav it nekā no sevis žēl.', '', '2012-07-26 09:11:52'),
(46, 'Lai vienmēr dvēsele tava<br />\r\nKā brīnumsvecīte zied,<br />\r\nLai ziedi, zvaigznes un sapņi<br />\r\nTur savākti vienuviet.<br />\r\nLai vienmēr ir radoša doma,<br />\r\nKas apvāršņa tālumos skrien,<br />\r\nLai esi kā jūra, kā doma,<br />\r\nKas dzintara pilna arvien!<br />\r\nLai pietiek spēka katram dzīves rītam,<br />\r\nLai katrs rīts kā skaisti ziedi plaukst.', '', '2012-07-26 09:12:00'),
(47, 'Vienmēr var vēlēties labāk.<br />\r\nVienmēr var izdoties sliktāk.<br />\r\nTas jokdaris, kas to kārto,<br />\r\nNekad neko nesaskaņo.<br />\r\nPie viņa ar kukuli nevar.<br />\r\nViņš nešķiro asaras, smaidus<br />\r\nViņš vienīgi atzīst un ciena tos -<br />\r\nKuri nenobaidās.<br />\r\n(O. Skuja)<br />\r\nVISLABĀKIE VĒLĒJUMI DZIMŠANAS DIENĀ!', '', '2012-07-26 09:12:09'),
(48, 'Lai vienmēr būtu sapnis, ko piepildīt,<br />\r\nKādas alkas un ilgas, ko remdēt.<br />\r\nKāda laba un jūtīga cilvēka sirds<br />\r\nDaži draugi un darbs, kuru darīt ļauts', '', '2012-07-26 09:12:17'),
(49, 'Šī diena lai tev<br />\r\nskaistāka par citām<br />\r\nŠī diena tikai reizi gadā aust.<br />\r\nLai paliek spēka katram<br />\r\ndzīves rītam,<br />\r\nlai katrs rīts kā skaisti<br />\r\nziedi plaukst.', '', '2012-07-26 09:12:26'),
(50, 'Nav nozīmes kāds galds ir klāts<br />\r\nTu vienmēr tiksi sumināts!<br />\r\nJo jauku draugu tā kā tu<br />\r\nIk vienam dzīvē novēlu.', '', '2012-07-26 09:12:39'),
(51, 'Dalies, un Tevi neapdalīs,<br />\r\nMīli, un Tevi mīlēs,<br />\r\nVēlies, <br />\r\nun viss buus tiehsi taa kaa Tu veelies.....:)', '', '2012-07-26 09:12:47'),
(52, 'Dzīve ir kā svārki šauri.<br />\r\nGrūti ir, bet jātiek cauri.<br />\r\nAlus, vīns, lai galdā plūst,<br />\r\nJubilārs, lai pirmais lūst.', '', '2012-07-26 09:12:55'),
(53, 'Vēl jau septembris smaržīgas rozes<br />\r\nTavās rokās reibinot liek,<br />\r\nVēl jau svešas<br />\r\nTev asteru skumjas, liesmo dvēselē mežvīna prieks.<br />\r\nVēl to dienu vesela jūra, Kas kā pīlādži debesīs zied,<br />\r\nPie tā zeltainā mākoņa turies,<br />\r\nKurā vasara nenoriet!', '', '2012-07-26 09:13:02'),
(54, 'Vēlu mazas laimītes,<br />\r\nLielu nav ko vēlēt....<br />\r\nLiela laime lieliski<br />\r\nPaslēpes prot spēlēt!', '', '2012-07-26 09:13:10'),
(55, 'Tu dzimusi starp rudens bērniem,<br />\r\nTo dzīvei rudens krāsas klāt,<br />\r\nUn rudens vēju dotās dziesmas,<br />\r\nTev savā mūžā izdziedāt.<br />\r\nIk grūtībās tev lepnai stāvēt,<br />\r\nkā martiņrozes salnās stāv,<br />\r\nUn atvasaras silto gaismu<br />\r\nLīdz pavasarim saglabāt.<br />\r\nNāk rudens atkal tavā sētā,<br />\r\nUn vēl viens gads ir mūžam klāt.<br />\r\nTas pildīsies ar priekiem,bēdām,<br />\r\nVarbūt būs mazliet bagātāks.', 'Lauma Daugiša', '2012-07-26 09:13:25'),
(56, 'Prieku, laimi spaiņiem smelt.<br />\r\nNaudu lieliem maisiem velt<br />\r\nVeselību saglabāt,<br />\r\nVisas bēdas sabradāt.', '', '2012-07-26 09:13:34'),
(57, 'Lai pagātne ar vēja spārniem aizplūst<br />\r\nUn tagadne kā zelta stariņš mirdz,<br />\r\nBet nākotne, lai atnes visu,<br />\r\nIt visu to, ko ilgojas Tev sirds.', '', '2012-07-26 09:13:44'),
(58, 'Vēlu Tavai dzīvei<br />\r\ngaišus saules starus,<br />\r\nkvēlu mīlas liesmu,<br />\r\ntikai prieka asaras<br />\r\nun stipru draudzības tiltu<br />\r\npār dzīves dienas un nakts stundām.', '', '2012-07-26 09:13:52'),
(59, 'Dzīvi nedrīkst vien dzīvot<br />\r\nUn vīnu ar steigu dzert;<br />\r\nVaj`g iet caur pasauli mīlot<br />\r\nUn baudu no mirkļa tvert!<br />\r\n<br />\r\nPar grūtībām nedrīkst domāt, <br />\r\nTās vajag darīt un viss; <br />\r\nVar akmeņi būt tev somā<br />\r\nUn tās var būt debesis!<br />\r\n<br />\r\nUn vecumu nedrīkst gaidīt;<br />\r\nTas jau tāpat reiz nāks.<br />\r\nJa vari par likstām smaidīt, <br />\r\nTad laiks tevi nepanāks!', '', '2012-07-26 09:14:08'),
(60, 'Mums dzīves ceļos akmeņi un māli, <br />\r\nIr šaubas, vilšanās un prieks.<br />\r\nTam visam pāri varavīksnes vāli<br />\r\nKā cerība, kas laimi sniegs<br />\r\nTūkstoš dzidru saules staru<br />\r\nLai vēl ilgi dzīvē mirdz. <br />\r\nPrieku, laimi, veselību<br />\r\nVēlējām no visas sirds!', '', '2012-07-26 09:14:16'),
(61, 'Pēc veciem seņču tikumiem,<br />\r\nšņabi dzer bez likumiem,<br />\r\nlai galva zin cik dibens sver,<br />\r\nuz to mums atkal, jāiedzer!<br />\r\nApsveicu dzimšanas dienā!', '', '2012-07-26 13:07:14'),
(62, 'Tie gadiņi,tie gadiņi.<br />\r\nNāk nelūgti kā radiņi.<br />\r\nViens projām,otrs klātu jau,<br />\r\nPat atvadīties neatļauj.<br />\r\nBet tas nekas,lai gadi nāk,<br />\r\nTie nekait tam,kas dzīvot māk!', '', '2012-07-26 13:07:26'),
(63, 'Ar Tevi vienmeer ir pavasaris!<br />\r\nKaa Tu no ziemas to nosargaaji?<br />\r\nIt kaa Tevii kaads saules stars<br />\r\nBuutu atradis maajas...', '', '2012-07-26 13:07:33'),
(64, 'Ja manos spēkos būtu īstenot kādu vēlējumu, man būtu grūti izvēlēties kādu dāvanu Tev sniegt, lai daritu Tevi laimīgu.<br />\r\nSkaistums ir bīstams, gudriba jānopelna, mīlestība paša izvēle, bet galu galā es tomer saprotu, ka vislabākā dāvana ir drosme, lai Tev tās dzīvē nekad nepietrūkst!', '', '2012-07-26 13:07:41'),
(65, 'Ne jau vienmēr jābūt klāt,<br />\r\nJubilāru sumināt.<br />\r\nVar jau arī klusi sveikt-<br />\r\nNosūtīt un vārdus teikt;<br />\r\nVēlēt daudz? Nē, tomēr vienu-<br />\r\nSagaidīt tev Pastardienu!', '', '2012-07-26 13:07:49'),
(66, 'Tā gaisma, ko izstaro svece,<br />\r\nTas gaišums, kas liesmiņā plīvo,<br />\r\nIzplēn, pazūd un gaist.<br />\r\nBet gaišums, kas cilvēkā dzīvo,<br />\r\nUn siltums, ko izstaro sirds,<br />\r\nTas nezūd, tas palīdz un mirdz.', '', '2012-07-26 13:08:33'),
(67, 'Šhodien Tava diena,<br />\r\nVisam pāri mirdz,<br />\r\nŠodien Tevi vienu,<br />\r\nApsveicu no sirds!=', '', '2012-07-26 13:08:45'),
(68, 'Asaras, smiekli un ziedi,<br />\r\nSpoža saule un zvaigžņu spiets,<br />\r\nBalts sniegs un sapņi tik liegi,<br />\r\nUn augļu smagums, kas zarus liec.<br />\r\n<br />\r\nAsaras, smiekli un ziedi,<br />\r\nTiem pa vidu vijas tavs mūžs.<br />\r\nTe tu raudi, te smejies, te dziedi,<br />\r\nVējš dienas kā ziedlapas pūš.<br />\r\n<br />\r\nAsaras, smiekli un ziedi,<br />\r\nTas ir ceļš, pretī laimei kas nes,<br />\r\nRīta rasā tu iemini sliedi,<br />\r\nUz to pļavu, kur zemenes.<br />\r\n<br />\r\nAsaras, smiekli un ziedi,<br />\r\nDarbs, raizes un bērnu smaids,<br />\r\nIr grūti pa reizei un viegli,<br />\r\nUz vēja spārniem kad traucas laiks.<br />\r\n<br />\r\nAsaras, smiekli un ziedi,<br />\r\nSalds un rūgts ir cilvēka kauss.<br />\r\nVien visu izbaudot topam mēs viedi<br />\r\nKā ceļš saules gaismā top sauss.<br />\r\n<br />\r\nAsaras, smiekli un ziedi,<br />\r\nKo man no sevis vēl dot?<br />\r\nJa tu ik dienu vadīsi dziedot,<br />\r\nTev naktī nebūs ko nožēlot.<br />\r\n<br />\r\nAsaras, smiekli un ziedi,<br />\r\nBūs rīt, parīt un aizparīt,<br />\r\nBet mums dzīvi dzīvot ir vieglāk;<br />\r\nAr tavas sirds gaismu ceļš kad vīts.', 'Gunta Kraulere', '2012-07-26 13:09:09'),
(69, 'Vai Tu atceries to dienu,<br />\r\nKad pirmo reizi dzēri pienu,<br />\r\nRatos gulēji un bļāvi<br />\r\nVisiem sevi bučot ļāvi!', '', '2012-07-26 13:09:17'),
(70, 'Šī diena, lai Tev skaistāka par citām,<br />\r\nŠī diena tikai reizi gadā aust,<br />\r\nLai paliek spēka katram dzīves rītam,<br />\r\nLai katrs rīts kā skaisti ziedi plaukst.', '', '2012-07-26 13:09:24'),
(71, 'Es gribu Tev dāvināt<br />\r\nsaulītes maliņu –<br />\r\nar mākoņiem kabatas pilnas.<br />\r\nEs gribu Tev novēlēt<br />\r\nsprīdi bezsvara telpas...<br />\r\nEs gribu Tev dāvināt<br />\r\nkafijas smaržu,<br />\r\nlai piejaucētu dzīvi- grēcīgi rūgtu.<br />\r\nEs gribu Tev novēlēt<br />\r\ntvanošu brendija glāzi,<br />\r\nkad dvēsele tāda caurspīdīga šķiet<br />\r\nun pasaule citiem aizgājusi ciet.<br />\r\nEs gribu Tev dāvināt<br />\r\nmedainu brītiņu laimes...<br />\r\nEs gribu Tev novēlēt mirkļus –<br />\r\ntos nenotveramos, tos izdzīvojamos,<br />\r\ntos nepierakstāmos un neizstāstāmos,<br />\r\ntos, kurus neplānojam, bet gaidām,<br />\r\ntos, kuri neuzprasās –<br />\r\nvien atnāk un atnes sajūtu – dzīvoju!', '', '2012-07-26 13:09:33'),
(72, 'Dzīvot var divejādi: pieņemot, ka brīnumi nenotiek, vai pieņemot, ka katrs notikums ir brīnums!<br />\r\n<br />\r\nDaudz laimes Dzimšanas dienā! :)*', '', '2012-07-26 13:09:43'),
(73, 'No saules, no laimes, no prieka,<br />\r\nKaut mazu daļiņu, bet katru dieniņu!!!', '', '2012-07-26 13:09:51'),
(74, 'Nevajag skaitīt gadus,<br />\r\nKuri aulēkšiem skrien -<br />\r\nGadus tāpat kā naudu,<br />\r\nNoturēt nespēj neviens.<br />\r\nSkaitīsim laimīgos mirkļus,<br />\r\nKuru mūžā bijis tik daudz -<br />\r\nAr tiem mēs bagāti esam<br />\r\nUn novecot nebūs ļauts!', '', '2012-07-26 13:09:59'),
(75, 'Aizver acis...redzi maza puķīte zied.<br />\r\nNezina mērķi tā savu.<br />\r\nVienkārši uzzied un zied.<br />\r\nAizver acis...jūti-laižas taurenis.<br />\r\nNetic tas smaguma spēkam,<br />\r\nVienkārši lido un dzied.<br />\r\nAizver acis...dzirdi-apkārt tik papeles.<br />\r\nNedomā koki par malku,<br />\r\nVienkārši izaug un mieg.<br />\r\nAizver acis...smaržo-apkārt ir bitītes trīs.<br />\r\nNejūt tās medus garšu,<br />\r\nVienkārši dīcina, sīc.<br />\r\nAizver acis...garšo uz mēles Tev zemenes.<br />\r\nNezin tās krāsu gammu,<br />\r\nVienkārši priecē un mirdz.<br />\r\nAizver acis...un vēlies,kas gan Tu gribi būt?<br />\r\nNezinot iespēju robežu,<br />\r\nVienkārši sajūtas rod!', '', '2012-07-26 13:10:07'),
(76, 'Lai pagātne ar vēja spārniem aizplūst,<br />\r\nUn tagadne kā zelta stariņš mirdz,<br />\r\nBet nākotne, lai atnes visu,<br />\r\nIt visu to, ko ilgojas tev sirds.', '', '2012-07-26 13:10:16'),
(77, 'Ko šodien mēs tev sacīt varam,<br />\r\nkad balti sniegi zemi klāj,<br />\r\nkad tava mūža gadu kokam<br />\r\nir zelta krāsas lapa klāt?<br />\r\nTik klusa, rimta katru dienu,<br />\r\ntik nemanāma daudziem mums,<br />\r\nbet tevi satiekot, mēs jūtam-<br />\r\nnāk labestības strāvojums.<br />\r\nUn tādēļ mēs no sirds tev vēlam,<br />\r\nlai balta katra diena aust,<br />\r\nlai tavā dzīves zelta kausā <br />\r\nir laimes, prieka, saules daudz.', 'Lauma Daugiša', '2012-07-26 13:10:29'),
(78, 'Galvenais neuzkāp uz viskija diētas!<br />\r\nātri vien var pazaudēt 3 dienas!<br />\r\nsveiciens dzimšanas dienā!', '', '2012-07-26 13:10:37'),
(79, 'Ir katram ziedam sava rasas pērle,<br />\r\nKas visās varavīksnes krāsās mirdz.<br />\r\nKā gribētos to satvert saules starā<br />\r\nUn dāvināt to tev no visas sirds!', '', '2012-07-26 13:10:44'),
(80, 'Apsveikt nevaram ka nakas,<br />\r\nJo ta liela krize sakas:<br />\r\nVeikalos viss traki dargs-<br />\r\nCilveks noguris un vargs.<br />\r\nLatini mums slid no rokam,<br />\r\nDzive turamies ar mokam.<br />\r\nTomer vajag ticet,ceret-<br />\r\nLai nakotnei vel varam deret!', '', '2012-07-26 13:10:53'),
(81, 'Maigi noglāstīt vakardienu,<br />\r\niecelt savu dzīvi<br />\r\nšodienas šūpolēs, un<br />\r\nuzšūpot līdz galotnēm, līdz<br />\r\nnepazaudēšanas augstumiem, līdz<br />\r\nneapmaldīšanās skaistumam, līdz<br />\r\nneaizmiršanas zelta stariem -<br />\r\nkatrā ziņā līdz vietai, kur<br />\r\n<br />\r\nviss sliktais aizkūp kā dūmi, bet<br />\r\nviss labais- joprojām<br />\r\nnāk līdzi un<br />\r\nnepāriet...', '', '2012-07-26 13:11:06'),
(82, 'Atvēli laiku darudzībai,<br />\r\njo tā ir ceļš uz laimi.<br />\r\nAtvēli laiku sapņiem,<br />\r\njo tie tevi aiznesīs līdz pat zvaigznēm.<br />\r\nAtvēli laiku, kad mīlēt un būt mīlētai,<br />\r\njo tā patiesi ir debesu dāvana.<br />\r\nAtvēli laiku, lai redzētu, kas notiek ap tevi,<br />\r\njo diena ir par īsu, lai varētu ļauties savtībai.<br />\r\nAtvēli laiku smiekliem,<br />\r\njo smiekli ir dvēseles mūzika.', '', '2012-07-26 13:11:13'),
(83, '...un šo dienu, Dieviņ, manam mūžam<br />\r\npieraksti klāt.<br />\r\nNākamo dienu- rītdienu-<br />\r\natļauj man piedziedāt,<br />\r\npiesāpēt, piegavilēt, just līdzi un<br />\r\ndot un dot...<br />\r\nNākamo dienu- rītdienu- dod,<br />\r\nDieviņ, man<br />\r\nsidrabotu-<br />\r\nMāci man iesēt cerību<br />\r\nvietā, kur prāts negrib ticēt.<br />\r\nĻauj kā mirdzošām pogām <br />\r\naiz prieka tik<br />\r\nlaimes asarām ritēt.<br />\r\nMāci man viegli ritināt<br />\r\nkamolu, Tevis doto,-<br />\r\nšo rītu, šo mirkli,<br />\r\nšo dieniņu, šo mūžu<br />\r\nsidraboto...', '', '2012-07-26 13:11:21'),
(84, 'Visi vēl tev šo un to,<br />\r\nEs ar vēlēšu kaut ko:<br />\r\nKūku kastē ierāpties,<br />\r\nPilnu punci saēsties.', '', '2012-07-26 13:11:29'),
(85, 'Dzīve gluži kā milzīga jūra,<br />\r\nlejup un augšup pa viļņiem mūs sviež.<br />\r\nEsi kā laiva, kam ir sava bura,<br />\r\nKura zin straumi, pret mērķi kas griež.<br />\r\nEsi kā laiva, kas nebaidās vētras,<br />\r\nTālumā prieku sevī, kas jūt,<br />\r\nnevar bez bangu un negaisu ceļiem,<br />\r\nBraucējs neviens, laimes dzintaru gūt!', '', '2012-07-26 13:11:39'),
(86, 'Kāds man šodien milzīgs prieks<br />\r\nApsveikt to, kas gaviļnieks, <br />\r\nBučas, ziedi, smaidi straumēm plūst, <br />\r\nUn pats vaininieks tik kūst...', '', '2012-07-26 13:11:47'),
(87, 'Novēlu Tev pārsteigumu, kas rada prieku,<br />\r\nPrieku, kas rada iedvesmu;<br />\r\nIedvesmu, kas iepriecina draugus,<br />\r\nDraugus, kas sagādā pārsteigumus!<br />\r\nDaudz laimes dzimšanas dienā!', '', '2012-07-26 13:11:55'),
(88, 'Tas jau nevienam nenāca prātā,<br />\r\nJautāt - Tu vari? Tu gribi?<br />\r\nUzvēla milzis kalnu Tev plecos <br />\r\nUn teica : " Še, paturi ... dzīvi!".', '', '2012-07-26 13:12:03'),
(89, '... Lai prieka Tev būtu tik daudz, ka to varētu savest siena šķūnī kā smaržīgu sienu un visu ziemu just vasaras aromātu, bet skumjas pakar virtuvē pie sienas kā zeltainu sīpolu virteni, kur slēpjas miljons neizraudātu asaru....', '', '2012-07-26 13:12:10'),
(90, 'Tavi gadi kā ābeles baltas,<br />\r\nTavi gadi kā vīgriezes zied.<br />\r\nAr vēja spārniem aizsteidzas dienas,<br />\r\nKā gājputni projām tās iet.<br />\r\nJel apstājies brīdi piekalnē zaļā,<br />\r\nKur vasarās baltas madaras zied,<br />\r\nSaber savus gadus pīpeņu kausā,<br />\r\nLai no Tevis tie neaiziet..', '', '2012-07-26 13:13:18'),
(91, 'Uz dienvidiem steidzas gājputnu bari<br />\r\nun gadu no mūza aiznes sev līdz,<br />\r\nBet sirdī kā liesmaina uguns kvēlo<br />\r\nPīlādžu ogu sarkanais spīts.<br />\r\nTas palīdz saglabāt saules gaismu,<br />\r\nUn mīļumu sevī ik dienu nest līdz.<br />\r\nLai gaišas domas un vieglu soli<br />\r\nTev dāvā ikkatrs austošais rīts.', '', '2012-07-26 13:13:26'),
(92, 'Mirdzumu acīs un mirdzumu dvēselē<br />\r\nVeselību pašai un veselus mīļos<br />\r\nLaiku zvaigžņu skaitīšanai un laiku rīta bučai<br />\r\nMazus brīnumus ikdienā un lielu brīnumu sirdī<br />\r\nEņģeli uz pleca un mīļu cilvēku blakus<br />\r\nMīlestību pret sevi pašu un mīlestību<br />\r\npret pasauli aiz loga!!', '', '2012-07-26 13:13:42'),
(93, 'Mums katram savi dzīves kalni zilie,<br />\r\nKas visu mūžu tikai tālāk sauc,<br />\r\nJa tik mēs paši apkārt nebūvētu valni,<br />\r\nKas sapņiem aizsedz plašuma tik daudz.<br />\r\nTad rudens lietavas un bērzu zaru trīsas<br />\r\nVien klusu vēsmu karstai sirdij dzied.<br />\r\nTik nepagurstot iet, jo zvaigznes itin visas<br />\r\nMums katram savos zilos kalnos zied...', 'T.Lāse', '2012-07-26 13:14:01'),
(94, 'Ja dzimšanas dienas nebūtu tev,<br />\r\nKā gan tu zinātu cik gadiņu sev. <br />\r\nBet tā kā tā ir un līksmību nes<br />\r\nTad priecīgus svētkus Tev novēlu es!', '', '2012-07-26 13:14:13'),
(95, 'Un viss tāpat kā bijis -<br />\r\nTā pati sirds, tas pats vēl liesmojums,<br />\r\nTā pati saule, zilais debesjums,<br />\r\nTik laiks ir gadu skaitu pārmainījis', 'N.Kalna', '2012-07-26 13:14:29'),
(96, 'Lai laime visur smaida<br />\r\nUn mīlestības zvaigzne mirdz,<br />\r\nUn prieks lai allaž gaida - <br />\r\nTo novēlu no visas sirds!', '', '2012-07-26 13:14:43'),
(97, 'Atvēli laiku sapņiem,<br />\r\nJo tie Tevi aiznesīs līdz pat zvaigznēm.<br />\r\nAtvēli laiku, kad mīlēt un būt mīlētam,<br />\r\nJo tā patiesi ir debesu dāvana.<br />\r\nAtvēli laiku, lai redzētu, kas notiek ap tevi,<br />\r\nJo diena ir par īsu, lai ļautos savtībai.<br />\r\nAtvēli laiku smiekliem,<br />\r\nJo smiekli ir dvēseles mūzika!', '', '2012-07-26 13:14:48'),
(98, 'Bagāts tu esi - tev pieder dzīve, kuru jūti plaukstam<br />\r\ntev pieder laime, kuru līdz sev nesi<br />\r\npati savā spēka pārpilnajā plaukstā. <br />\r\n<br />\r\nĻauj novēlēt tev dzimšanas dienā laimi,<br />\r\nlai tavās dienās gaismas staru daudz!<br />\r\nJa ceļš ir grūts-<br />\r\nlai apkārt draugu saime,<br />\r\nKas roku sniedz un tālāk sauc.<br />\r\nNovēlu tev visu to labāko <br />\r\nun ar smaidu uz lūpam sagaidīt<br />\r\nnākamo dzimšanas dieniņu!', '', '2012-07-26 13:14:59'),
(99, 'Mazs enģelīts man priekšā teic,<br />\r\nKa tevi šodien jāapsveic.<br />\r\nLai amoriņš Tev sirdī valda,<br />\r\nUn visas bēdas bultām skalda', '', '2012-07-26 13:15:08'),
(100, 'Es nevēlēšu Tev zvaigžņu lietu, kad vēlēšanos ir daudz, tām nav jēgas. <br />\r\nEs novēlu Tev vienu vienīgo zvaigzni, bet tādu, kura izpildīs Tavu viskvēlāko vēlēšanos. <br />\r\nKad dzīvē daudz saldumu, tad zūd dzīves garša, tāpēc es novēlu tev vienu vienīgo torti, bet ar svecēm, kuras nopūšot, Tev piepildīsies iecerētais. <br />\r\nKad apkārt daudz cilvēku, tas rada drūzmu, tāpēc nevēlēšu Tev daudz draugu. <br />\r\nEs novēlēšu Tev, lai tie draugi, kas tev ir pašlaik, ir tādi, kuri nāk kad tie vajadzīgi, iet, kad tie traucē un nelien ar padomiem, kad Tev pašam jāgūst pieredze.<br />\r\nTāpēc tā: DAUDZ LAIMES DZIMŠANAS DIENĀ !!!!!!', '', '2012-07-26 13:15:20'),
(101, 'Ņem par labu nedaudz rindu,<br />\r\nKuras ļoti maz var teikt.<br />\r\nTomēr tavā dzimšanas dienā<br />\r\nAtļauj visiem tevi sveikt.', '', '2012-07-26 13:15:28'),
(102, 'Lai Tev vienmēr mēle asa,<br />\r\nKad kāds gadu skaitu prasa.<br />\r\nLai jau prasa, ja nav kauns(-a)<br />\r\nMums tu vienmēr būsi jauns(-a).', '', '2012-07-26 13:15:38'),
(103, 'Es ņemu debess gabaliņu zilu<br />\r\nUn pielieku tam sniegpulksteni klāt,<br />\r\nVēl pūpolus un smaržu pilno silu,<br />\r\nTo visu vēlos tev es uzdāvināt.<br />\r\n<br />\r\nŠai apsveikumā sūtu bērzu prieku,<br />\r\nKo apzeltījis plaukstošs lazdas zars,<br />\r\nUn vēl simtstūktošs dzimtās puses nieku,<br />\r\nKas tavai sirdij prieku dāvāt var.', 'Kornēlija Apšukrūma', '2012-07-26 13:15:52'),
(104, 'Trīs ziedus es Tev šodien sūtu, jo trijos ziedos izteikts viss, viens - laimei, otrs - mīlai, bet teršais tiekot vāzē likts!', '', '2012-07-26 13:16:01'),
(105, 'Čaklas rokas, vieglas kājas,<br />\r\nPatīkamas, siltas mājas.<br />\r\nVeselību, dzīvesprieku,<br />\r\nVienmēr santīmiņu lieku.', '', '2012-07-26 13:16:09'),
(106, 'Ir trīs lietas kas dzīvē mirdz<br />\r\nSaule,<br />\r\nZiedi un<br />\r\nLaba sirds!', '', '2012-07-26 13:16:18'),
(107, 'Esi mūžam smuka, slaida,<br />\r\nLai tie foršie čaļi smaida.<br />\r\nBet, ja nedarīs kā liek,<br />\r\nTad Tev mūžam stulbie tiek.', '', '2012-07-26 13:16:26'),
(108, 'Lai Tavs dzimsanas dienas rits,<br />\r\ndaudziem skaistiem sapniem vits.<br />\r\nlai sie sapni piepildas,<br />\r\nlai dzive Tev viss izdodas!!!!', '', '2012-07-26 13:16:34'),
(109, 'Ieskatījos kalendārā zibens iespēra man prātā jopcik copcik spainis piena, tev tak šodien dzimšansdiena!!', '', '2012-07-26 13:16:42'),
(110, 'Būt savā patiesībā drošai,<br />\r\nBūt skaistai tā kā rozes zieds,<br />\r\nBūt tādai, kuru nevar nodot,<br />\r\nBūt tādai, kura pieburt prot.', '', '2012-07-26 13:16:50'),
(111, 'Novēlu Tev spēku tikt galā ar lietām, kuras ir iespējams izmainīt.<br />\r\nNovēlu tev izturību samierināties ar apstākļiem, kurus Tu nevari ietekmēt.<br />\r\nUn gudrību-atšķirt vienu no otras.', '', '2012-07-26 13:16:59'),
(112, 'Lai vienmēr ir ,ko citiem dot,<br />\r\nLai vienmēr ir,ko samīļot,<br />\r\nLai vienmēr ir kāds,kura dēļ,<br />\r\nNav it nekā no sevis žēl.', '', '2012-07-26 13:17:08'),
(113, 'Katru dienu pa sapnim, pa cerībai,<br />\r\nSmaidam siltam un vārdam sirsnīgam;<br />\r\nKatru dienu pa prieka brītiņam,<br />\r\nLai ievijas dzīves apcirkņos!', '', '2012-07-26 13:17:18'),
(114, 'Es ņemu debess gabaliņu zilu<br />\r\nUn pielieku tam sniegpulksteni klāt,<br />\r\nVēl pūpolus un smaržu pilno silu,<br />\r\nTo visu vēlos Tev es dāvināt.<br />\r\nŠai apsveikumā sūtu bērzu prieku,<br />\r\nKo apzeltījis plaukstošs lazdas zars,<br />\r\nUn vēl simttūkstošs dzimtās puses nieku,<br />\r\nKas Tavai sirdij prieku dāvāt var!', '', '2012-07-26 13:17:30'),
(115, 'Viss ir joks, par visu smejies,<br />\r\nLaid, lai citi raud un vaid.<br />\r\nJa ir labi ? pakavējies,<br />\r\nUn ja netīk ? projām laid!', 'Ed. Veidenbaums/', '2012-07-26 13:17:46'),
(116, 'Ir skaistākas dienas par citām..<br />\r\nDienas, kurās asaras pāraug<br />\r\nprieka asarās, sāpes pāraug<br />\r\nvieglos smieklos.<br />\r\nDienas, kurās<br />\r\npat rudens saltais vējš silda..<br />\r\nUn šī ir viena no tām<br />\r\nīpašajām dienām - tā ir un būs<br />\r\nTava diena, kad saule spīd<br />\r\nspožāk par visām zvaigznēm<br />\r\ndebesīs...<br />\r\nDiena, kad Tavs vārds<br />\r\nun Tu pati atspīd plašajā<br />\r\nzvaigznājā...', '', '2012-07-26 13:17:58'),
(117, 'Gadi ir dīvaini dārgumi, draugs,<br />\r\nBirst tā kā ziedi no ābeles salnā,<br />\r\nNobirst kā ziedi un aizspurdz kā putni<br />\r\nPagaistot zilajā kalnā.<br />\r\nGadi ir dīvaini, dīvaini, draugs,<br />\r\nBrīžiem tie brāzmaini, žilbi un strauji<br />\r\nAizdip kā kumeļi jaunības rītā,<br />\r\nIekrīt kā zvaigznes mūžības klēpī.<br />\r\nUn šķiet - tev vēl sētuvē<br />\r\nViņu tik daudz, ka vari tos kaisīt bez skaita.<br />\r\nTaču tie visi tev vienlīdz smeldz,<br />\r\nKad steidzies tiem cauri atmiņu dzelts,<br />\r\nUn redzi, cik virtene īsa...', '', '2012-07-26 13:18:06'),
(118, 'Laimi, lai tā vienmēr soļo blakus,<br />\r\nSauli, lai tā vienmēr sirdī spīd,<br />\r\nVeselību, kas ir visa mīļa, <br />\r\nDzīvesprieks lai garām neaizskrien!!!', '', '2012-07-26 13:18:15'),
(119, 'Prieku, laimi veselību,<br />\r\nVisu draugu uzmanību,<br />\r\nLai tu tortē ieveltos<br />\r\nUn ar krēmu aizrītos!', '', '2012-07-26 13:18:24'),
(120, 'Ļauj novēlēt tev dzimšanas dienā laimi,<br />\r\nlai tavās dienās gaismas staru daudz!<br />\r\nJa ceļš ir grūts-<br />\r\nlai apkārt draugu saime,<br />\r\nKas roku sniedz un tālāk sauc.<br />\r\nNovēlu tev visu to labāko <br />\r\nun ar smaidu uz lūpam sagaidīt<br />\r\nnākamo dzimšanas dieniņu!', '', '2012-07-26 13:18:36'),
(121, 'Vienmēr labu omu,<br />\r\nNaudas pilnu somu,<br />\r\nVeselību labu, <br />\r\nVisu cauru gadu.', '', '2012-07-26 13:18:46'),
(122, 'Man bija reiz pīpenes seja<br />\r\nAr smaida saulīti vidū,<br />\r\nun tā es pa dūksnājiem bridu<br />\r\nAr smaida saulīti vidū.<br />\r\nman bij reis rozes seja,<br />\r\nKvēls, valdzinošs ziedlapu vainags,<br />\r\nUn nebij man citas vainas,<br />\r\nVien valdzinošs ziedlapu vainags.<br />\r\nMan bij reiz dālijas seja,<br />\r\nGan labestīga, gan rāma,<br />\r\nBet arī dālijas seja<br />\r\nman nebija paturama...<br />\r\nNu kā lai es uzziedu tagad,<br />\r\nKad salna pa tīrumiem staigā,<br />\r\nKad plecus liec auglības smagums,<br />\r\nbet plaukšanas alkas vēl vaigā?', '', '2012-07-26 13:18:58'),
(123, 'Gadu skaitlim šai brīdī nav nozīmes,<br />\r\nVisa pamatā cilvēka sirds.<br />\r\nViss tas maigums, ko mīlot Tu sniedzi mums,<br />\r\nTavu tuvāko acīs mirdz.<br />\r\n<br />\r\nDāsnums šis mūžam lai neizzūd,<br />\r\nČaklās rokas lai nepiekūst!<br />\r\nUn tā - pat līdz simtam aizdzīvot<br />\r\nSkaistā , svētīgā mūžā Tev būs!', 'M. Dūka', '2012-07-26 13:19:12'),
(124, 'Darbā krokodila spēku<br />\r\nMīlestībā mazāk grēku<br />\r\nMājās mieru turi tīru<br />\r\nUn par niekiem necel brēku', '', '2012-07-26 13:19:21'),
(125, 'Ilgus, skaistus dzīves gadus<br />\r\nTev vēl priecāties un smiet,<br />\r\nDaudzreiz sasaukt draugus, radus,<br />\r\nSaldu vīnu glāzēs liet!', '', '2012-07-26 13:19:29'),
(126, 'Veiksmi darbiņā,<br />\r\nNaudu kārbiņā,<br />\r\nLaimi ik dienas,<br />\r\nBēdas nevienas!', '', '2012-07-26 13:19:37'),
(127, 'Uzmini nu, kāda būs tava dzimšanas dienas dāvana? <br />\r\nMašīna? Celjojums uz Pariizi? Nee! <br />\r\nTu kļūsi par gadu vecaks! <br />\r\nDaudz laimes!', '', '2012-07-26 13:19:48'),
(128, 'Pacel, dzīve, vēl uz spārniem,<br />\r\nNe jau lidot,- vēju just,<br />\r\nVēju vien, kas dzīves burās<br />\r\nVēl aiz burātāja turas.<br />\r\nVēju, kas vēl tiekties liek<br />\r\nUn ar siltu elpu snieg<br />\r\nPāri nodzīvotām dienām.<br />\r\nPāri nodzīvotiem gadiem<br />\r\nPacel, dzīve, vēl uz spārniem.', 'M.Bārbale ', '2012-07-26 13:19:59'),
(129, 'Dzimšandiena vienreiz gadā, <br />\r\nneļauj draugiem nomirt badā, <br />\r\nabas acis kopā miedz, <br />\r\npudelīti galdā liec!', '', '2012-07-26 13:20:28'),
(130, 'Lai tavā mužā saules daudz,<br />\r\nLai tavā sirdī prieks.<br />\r\nLai nākotne vēl ilgi var<br />\r\nTev daudz ko skaistu sniegt.', '', '2012-07-26 13:20:37'),
(131, 'Es palūdzu saulīti<br />\r\npavest mazu gabaliņu.<br />\r\niegriezties un sveicināt<br />\r\nŠīs dienas gaviļnieku.', '', '2012-07-26 13:20:45'),
(132, 'Šajā dienā līksmu prātu,<br />\r\nKonjaciņiem galdu klātu.<br />\r\nSaulainas lai visas dienas,<br />\r\nMākoņainas nav nevienas.', '', '2012-07-26 13:20:56'),
(133, 'Lai veselība, priecīgs prāts<br />\r\nGan tev, gan visai dzimtai!<br />\r\nLai glāzes šķind un galds ir klāts<br />\r\nLīdz jubilejai simtai!', '', '2012-07-26 13:21:06'),
(134, 'Dzīve ir kā svārki šauri,<br />\r\nGrūti ir...bet jātiek cauri.<br />\r\nAlus, vīns lai galdā plūst,<br />\r\nJubilārs lai pirmais lūst!', '', '2012-07-26 13:21:15'),
(135, 'Kad tu piedzimi - lija, bet tas nebija lietus. Tās bija debesis, kas raudāja, zaudējot savu visskaistāko zvaigzni...', '', '2012-07-26 13:21:25'),
(136, 'Tu dzimusi starp rudens bērniem,<br />\r\nTo dzīvei rudens krāsas klāt,<br />\r\nUn rudens vēju dotās dziesmas,<br />\r\nTev savā mūžā izdziedāt.<br />\r\nIk grūtībās tev lepnai stāvēt,<br />\r\nkā martiņrozes salnās stāv,<br />\r\nUn atvasaras silto gaismu<br />\r\nLīdz pavasarim saglabāt.<br />\r\nNāk rudens atkal tavā sētā,<br />\r\nUn vēl viens gads ir mūžam klāt.<br />\r\nTas pildīsies ar priekiem,bēdām,<br />\r\nVarbūt būs mazliet bagātāks.', 'Lauma Daugiša', '2012-07-26 13:21:46'),
(137, 'Laime tāpat kā māksla, nāk pie mums miljonos veidu.<br />\r\nMums nepieciešama dižā un neaizmirstamā laime - mīlestība,draudzība...<br />\r\nTaču mums vajadzīgas arī sīkas un pat muļķīgas laimītes - lietus logā, kaķis gultā, dubultvaravīksne, pašizcepta maize...<br />\r\nViss,kas pacilā un drūmāko dienu padara dzīvošanas vērtu.', '', '2012-07-26 13:21:55'),
(138, 'Tu man sagrozīji galvu<br />\r\nDzimšanas dienā savā, <br />\r\nKad Tu piedzimi, <br />\r\nPasaulē ienāca pusīte mana.', '', '2012-07-26 13:22:06'),
(139, 'Novēlu tev medu ēst,<br />\r\nĀbolu uz pusēm plēst,<br />\r\nIenaidnieku gaisā sviest,<br />\r\nMīļoto pie krūtīm spiest!!', '', '2012-07-26 13:22:14'),
(140, 'Lai Tava sirsniņa būtu kā atvērta plauksta, kurā daudz mīlestības tauriņu nosēsties var!<br />\r\nLai vienmēr šie tauriņi mīlu un laimi nestu un nekad nepamestu!', '', '2012-07-26 13:22:25'),
(141, 'Skrien gadi prom,Tev dodot gadu skaitu.<br />\r\nIr tajos dzīvots,dejots,trakots,smiets.<br />\r\nŠiem gadiem netici un sevi stipru jūti<br />\r\nVēl jāveic viss,kas dzīves kausā liets.', '', '2012-07-26 13:22:33'),
(142, 'lai Tev būtu kas balts<br />\r\nAr ko nosegt<br />\r\nDzīves koduma pēdas!<br />\r\nSpirgtu avotu šalts<br />\r\nZem ikdienas rūpju grēdas.<br />\r\nLai Tev būtu kas salds,<br />\r\nRūgtas domas<br />\r\nKad dvēselē ēdas,<br />\r\nUn pie rokas kas salts<br />\r\nAr ko apdzēst sāpi,<br />\r\nKas mēdās!<br />\r\nLai Tev būtu kāds malds,<br />\r\nKas dod dzīvei neprāta vēdas!', 'K.Apškrūma', '2012-07-26 13:22:44'),
(143, 'Palūdz pavasari,<br />\r\nLai tas spēku aizdod.<br />\r\nPalūdz puteņiem,<br />\r\nLai gadiem pēdas jauc.<br />\r\nPalūdz pašai sirdij - tā vislabāk zinās,<br />\r\nKādus gadus atpakaļ lai sauc.<br />\r\nGadu straumē nestas aiziet dienas,<br />\r\nDzīves jūrā saplūst vienuviet.<br />\r\nLai vēl ilgi mirdz Tev ceļa zvaigzne, <br />\r\nDarbi veicas, gaišas dienas rit.', '', '2012-07-26 13:23:15'),
(144, 'Dzimšanas dienā esi mierīgs,<br />\r\nVisu labi pārdomā.<br />\r\nNeskumsti par gadiem,<br />\r\nPriecājies par pieredzi.', '', '2012-07-26 13:23:24'),
(145, 'Šajā dienā līksmu prātu,<br />\r\nKonjaciņiem galdu klātu.<br />\r\nSaulainas lai visas dienas,<br />\r\nMākoņainas nav nevienas.', '', '2012-07-26 13:23:33'),
(146, 'Lai maigi skar tavu pieri lietus lāses,<br />\r\nLai liega vēsme atspirdzina tavu garu,<br />\r\nLai saulstari padara gaišāku tavu sirdi,<br />\r\nLai dienas nastas nogulstas uz tevi viegli<br />\r\nUn, lai Dievs tevi ietin savas mīlestības apmetnī...', '', '2012-07-26 13:24:20'),
(147, 'Pie mūsu jaunības slitas<br />\r\nSen citi kumeļi zviedz,<br />\r\nUn arī dzērves ir citas,<br />\r\nkas, vasarai aizejot, kliedz.<br />\r\nUn citi ūdeņi Gaujā<br />\r\ngan strauji, gan lēni plūst,<br />\r\nun Daugavas stāvajā kraujā<br />\r\ncitas priedes lūst.<br />\r\nUn citi mēs arī esam<br />\r\ntik sapņi - tie paši vēl,-<br />\r\nko laikam gan veltīgi nesam,<br />\r\nbet aizmirst - žēl.', 'D.Avotiņa', '2012-07-26 13:24:37'),
(148, 'Tev gadu mija? Ko lai vēl? <br />\r\nTik daudz vēl skaista notiks,<br />\r\nBet lai nekad nav gadu žēl, kas mūžā nodzīvoti,<br />\r\nlai sirds un prāts bez šaubām zin,<br />\r\nka sevi nepiekrāpsi.<br />\r\nUn dienas lai nav jāpiemin<br />\r\nar zaudējuma sāpi.', '', '2012-07-26 13:24:47'),
(149, 'Nekas šājā pasaulē nav svarīgāks par cilvēkiem.<br />\r\nĪpaši tiem, kuri mums ir blakus, kuri ir mūsu domās un sirdīs.', '', '2012-07-26 13:24:55'),
(150, 'Ko lai veelam <br />\r\nstipram musu zemes delam?-<br />\r\nDaiktu gaisa vienmer staltu,<br />\r\nAutinju ar shipiem kaltu,<br />\r\nUn vel tautumeitu baltu,<br />\r\nKuru citi nesauktu par mau...', '', '2012-07-26 13:25:05'),
(151, 'Laimi- kaa zilu pasaku;<br />\r\nSmieklus- kaa pavasara lietutinju;<br />\r\nSpiitiibu- kaa uudens burbuli; <br />\r\nSavaldiibu- kaa akmens kruuzi<br />\r\nUn paari visam- MIILESTIIBU KAA VARAVIIKSNI!!!', '', '2012-07-26 13:25:13'),
(152, 'Mīlestību vairāk kā vakar,<br />\r\nSapņus, kas piepildās,<br />\r\nTorti, kas nesakalst<br />\r\nun draugus, kuri vienkārši ir! <br />\r\nDaudz Laimes!', '', '2012-07-26 13:25:22'),
(153, 'Skatos kalendārā,<br />\r\nMan cipars vēsta -<br />\r\nDzimšanas diena Tev svinama godam!<br />\r\nDraugi tuvākie un radi<br />\r\nDāvanu kalnus lai stiepj,<br />\r\nGaldi no virtuves veltēm lūzt,<br />\r\nSaldi dzērieni straumēm plūst!<br />\r\n<br />\r\nVēlu es no visas sirds<br />\r\nVēl daudzas desmitgades godam nodzīvot!<br />\r\nDaili starojošo saglabāt<br />\r\nLīdz pašai kapa malai!', '', '2012-07-26 13:25:30'),
(154, 'Visi mazi kukainīši<br />\r\nTevim laimes vēlē,<br />\r\nVisi ciema meitenīši<br />\r\nTevi dikti mīlē!', '', '2012-07-26 13:25:38'),
(155, 'Lai laimei tici, ko Tev draugi vēl,<br />\r\nLai dzīves dārzā Tu vēl ilgi ziedi,<br />\r\nLai gadu nodzīvoto Tev nav žēl,<br />\r\nBet nākamie, lai deg kā krāšņi rieti!', '', '2012-07-26 13:25:47'),
(156, 'Gadu kāpnēm pāri ejot, lai sirds un domas nepagurst!', '', '2012-07-26 13:25:56'),
(157, 'Ir Tavam mūža lokam atkal<br />\r\nviens zelta dzīpariņš nu klāt,<br />\r\nar Ziemassvētku gaismu baltu,<br />\r\nar cerību, kas klusa nāk,<br />\r\nar mīlestību, vārdu labu,<br />\r\nkad laime acīs starot sāk.<br />\r\nLai prieks, kas šodien acīs uzzied,<br />\r\nTev palīdz dienas tālāk vest,<br />\r\nkad plecos, sievišķīgos, trauslos,<br />\r\nir jāprot darba smagums nest.', 'Lauma Daugiša', '2012-07-26 13:26:06'),
(158, 'Rūķu cilts,<br />\r\nNo laika gala,<br />\r\nKatru dienu<br />\r\nLaimi dala.<br />\r\nEs ar būtu,<br />\r\nRūķis viens,<br />\r\nKas tev laimi,<br />\r\nNovēl daudz.', '', '2012-07-26 13:26:15'),
(159, 'Lai Tavā dzīvē allaž jauns ceļš.<br />\r\nLai Tavās burās pūš ceļa vējšs.<br />\r\nLai saule silti apspīd Tevi <br />\r\nUn lai lietus, kas līst Tev virsū,<br />\r\nIr veldzējošs un maigs!', '', '2012-07-26 13:26:24'),
(160, 'Tā puķe uzzied, ko tu paņem rokā,<br />\r\nTā sirds sāk mīlēt, ko tu uzlūko.<br />\r\nTev ir jau viss, tu staigā zvaigžņu lokā ?<br />\r\nEs tev vairs dāvāt nevaru neko.', 'J. Ziemeļnieks', '2012-07-26 13:26:37'),
(161, 'Līksmai dienai-putna dziesma,<br />\r\nAukstai-ugunskura liesma.<br />\r\nLabam miegam-jūras šalku,<br />\r\nLielām slāpēm-rasas malku,<br />\r\nKarstai dienai-vēju lēnu,<br />\r\nNogurumam-vēsu ēnu.<br />\r\nSkumjām-pūpolmīkstu glāstu,<br />\r\nTukšiem brīžiem-brīnumstāstu.<br />\r\nTumšai naktij -zvaigžņu lietu,<br />\r\nSirdī mīlai-siltu vietu!', '', '2012-07-26 13:27:04'),
(162, 'Tu esi brīnumzvaigznīte un tev atliek tikai spīdēt un spīdēt vēl spožāk un citos uzturēt gaismu! :)', '', '2012-07-26 13:27:15'),
(163, 'Ļauj novēlēt tev dzimšanas dienā laimi,<br />\r\nLai tavās dienās gaismas staru daudz!<br />\r\nJa ceļš ir grūts - lai apkārt draugu saime,<br />\r\nKas roku sniedz un tālāk sauc.', '', '2012-07-26 13:27:25'),
(164, 'Šodien tortē sveces spraustas,<br />\r\nJubilāram seja raustas,<br />\r\nJo ir zināms,ak tu mūžs,<br />\r\nVisas tās būs jānopūš!', '', '2012-07-26 13:27:35');
INSERT INTO `regards` (`regard_ID`, `poem`, `author`, `created`) VALUES
(165, 'Vecums trakumam nebūs par šķērsli, <br />\r\nvecums jau arī aiz kalniem vēl vīd, <br />\r\nTagad tev labākie gadi tik sākas, <br />\r\nacīs nemiera dzirksteles spīd. <br />\r\nPar taviem gadiem pacelsim kausu, <br />\r\nnovēlot laimi, izdzersim sausu, <br />\r\nlai visu, ko vēlies, spētu tu gūt <br />\r\nun vienmēr ar mīļiem cilvēkiem būt.', '', '2012-07-26 13:27:45'),
(166, 'Balta diena lai Tev šī!!! Ar to noskaņu, kāda ir divas minūtes pirms pusnakts vecgada naktī, ar to skaņu, kāda ir klausoties mūziku, ar to smaržu, kura piepilda istabu, kad kāds smēķē ķiršu tabaku, ar to garšu kāda ir karstai šokolādei... un visam pāri - balta diena, kurā Tu izvēlies visu, kas notiek!', '', '2012-07-26 13:27:53'),
(167, 'No pavasara - vasarā,<br />\r\nno rudens zelta - ziemā-<br />\r\nrit laiks un tas tev pasaka,<br />\r\nka esi savā vietā<br />\r\nun ka tos laimes pakavus<br />\r\nvar rast pat ikdienībā.', 'M.Svīķe', '2012-07-26 13:28:09'),
(168, 'Lai vienmēr ir ,ko citiem dot,<br />\r\nLai vienmēr ir,ko samīļot,<br />\r\nLai vienmēr ir kāds,kura dēļ,<br />\r\nNav it nekā no sevis žēl.', '', '2012-07-26 13:28:20'),
(169, 'Dzīves melnās dienas<br />\r\nliek dvēselei<br />\r\ncietā gliemeža <br />\r\nčaulā tīties,<br />\r\nnejūtīgie un pārlieku <br />\r\nziņkārīgie,<br />\r\nlai tur nespētu<br />\r\nieskatīties.<br />\r\nDzīves baltās dienas<br />\r\nsavukārt<br />\r\natver aizslēgtos<br />\r\nlogus un durvis,<br />\r\nvienā mirklī ļauj<br />\r\npiedzimt brīnumam, <br />\r\nkurš tev spārnus<br />\r\niedod kā burvis.<br />\r\nLai Tev vairāk<br />\r\nbalto kā nebalto,<br />\r\ndienu turpmāk<br />\r\ndzīvojot būtu, lai tās zelta<br />\r\nguldeņos pārtaptu,<br />\r\nun no draugiem<br />\r\ncīruļu vieglumu<br />\r\nsaviem nākošiem<br />\r\ngadiem gūtu!', '', '2012-07-26 13:28:30'),
(170, 'Lai viss, ko vēlies, piepildās,<br />\r\nLai viss, ko dari, izdodās<br />\r\nUn lai nekad nav dzīvē tā -<br />\r\nIr itkā viss, bet nav nekā!', '', '2012-07-26 13:28:39'),
(171, 'Lai pagātne ar vēju aizšalc tālu<br />\r\nUn tagadne, kā saules stars lai mirdz.<br />\r\nUn dzīve lai Tev atnes visu, visu...<br />\r\nPēc kā tik ļoti ilgojās Tev sirds!', '', '2012-07-26 13:28:49'),
(172, 'Pasaules dvēseli baro cilvēku laime. Un arī to nelaime, ļaunums un skaudība. Īstenot savu Likteni ir cilvēka vienīgais, patiesais uzdevums. Viss kalpo vienam. Ja tu kaut ko vēlies, visa pasaule slepus sadodas rokās, lai tev palīdzētu.', 'P.Koelju', '2012-07-26 13:29:05'),
(173, 'Lai visi mazie putniņi<br />\r\nKas koka zaros čivina<br />\r\nNo rīta tevi modina<br />\r\nUn mīļi, mīļi sveicina!', '', '2012-07-26 13:29:15'),
(174, 'Vienmēr var vēlēties labāk.<br />\r\nVienmēr var izdoties sliktāk.<br />\r\nTas jokdaris, kas to kārto,<br />\r\nNekad neko nesaskaņo.<br />\r\nPie viņa ar kukuli nevar.<br />\r\nViņš nešķiro asaras, smaidus<br />\r\nViņš vienīgi atzīst un ciena tos -<br />\r\nKuri nenobaidās.', '. Skuja', '2012-07-26 13:29:41'),
(175, 'Ak, jaunība, mēs aizejam pa vienam<br />\r\nNo Taviem dārziem pusnakts klajumos.<br />\r\nBet simtiem citu atnāk mūsu vietā<br />\r\nNo jauna mīlēt, ilgoties un tvīkt.', '', '2012-07-26 13:29:49'),
(176, 'Kad uz visas zemeslodes<br />\r\nTavu dzimtenīti svin,<br />\r\nTad lai slimo tas ar gripu,<br />\r\nKas šo dienu nepiemin!', '', '2012-07-26 13:29:59'),
(177, 'Lai laime Tev visur smaida,<br />\r\nUn mīlestības zvaigzne mirdz,<br />\r\nUn vēl - prieks allaž lai Tevi gaida<br />\r\nTo novēlam no visas sirds.', '', '2012-07-26 13:30:08'),
(178, 'Kad klinģeris smaržo<br />\r\nUn svecītes mirdz, <br />\r\nDaudz laimes tad vēlu<br />\r\nNo visas sirds.', '', '2012-07-26 13:30:16'),
(179, 'Būt vijīgai un valdzinošai,<br />\r\nBūt savā patiesībā drošai.<br />\r\nBūt durstīgai, kā rozes zars.<br />\r\nBūt tādai - kuru nevar nodot,<br />\r\nBūt tādai - kura pieburt prot!', '', '2012-07-26 13:30:25'),
(180, 'Kaut kur ir pļava -<br />\r\nViena vienīgā pasaulē - Tava!<br />\r\nNaktīs viņa Tev sapņos smaržo<br />\r\nUn rītos vēl ilgi uz lūpām<br />\r\nJūti medus garšu;<br />\r\nLai arī Tev pieder pļava,<br />\r\nKurā tikai atliek ieiet<br />\r\nUn kur Tu būtu laimīga.<br />\r\nMīļi sveicu!', '', '2012-07-26 13:33:10'),
(181, 'Ja būtu Dievs, es viņu lūgtu, lai sargā Tevi kurp vien ej,<br />\r\nLai mīlestība nepietrūktu un laimes Tavai dvēselei.<br />\r\nJa būtu Dievs, es ceļos kristu un lūdzot aizplūstu kā gaiss,<br />\r\nLai Tavā sejā neizdzistu, Tavs daiļums, viņam līdzīgais.', '', '2012-07-26 13:33:18'),
(182, 'Lai tas, ko gaidi, <br />\r\nŠodien Tevi apņem<br />\r\nNoskūpsta un saka:<br />\r\nEs mīlu, sveicu tevi!', '', '2012-07-26 13:33:28'),
(183, 'Gadiem līdzi arī ērkšķi<br />\r\nNav jau tikai rozes vien<br />\r\nNu jau jāpiedomā bieži<br />\r\nKāpēc tie tik ātri skrien.<br />\r\nBet, lai netrūkst dzīves prieka<br />\r\nSpīta ēnas projām dzīt,<br />\r\nGadus vainagā kā ziedus<br />\r\nVarēs ilgi, ilgi vīt.', '', '2012-07-26 13:33:36'),
(184, 'Ja nav ne zvaigznītes, kas gaismu dod,<br />\r\nVar pasaule bez ziediem un bez skaņām būt ?<br />\r\nLikt visam gavilēt un vizuļot<br />\r\nVar maza, sīka cilvēkbērna sirds,<br />\r\nJa viņa prieku jūt.', 'E.Zālīte', '2012-07-26 13:33:54'),
(185, 'Šī diena lai skaista kā sapnis,<br />\r\nTāda ir tikai vienreiz gadā.<br />\r\nŠī diena lai uzbur to.<br />\r\nKo visu gadu gaidi.', '', '2012-07-26 13:34:03'),
(186, 'Nav vēl pagājis jaunības trakums, kā vecuma dullums jau klāt.', '', '2012-07-26 13:34:13'),
(187, 'Daudz laimes un prieka<br />\r\ntev ziedu vietā sūtu<br />\r\nun vēlos kaut tiešām<br />\r\ntu laimīgs būtu!', '', '2012-07-26 13:34:21'),
(188, 'Novēlu mirklīti saldu skumju, pilnas riekšavas gaiša prieka un mīlestības- pārpārēm!', '', '2012-07-26 13:34:29'),
(189, 'Es vīna glāzi pilnu pieleju,<br />\r\nJo šodien mūža gadi mijas man-<br />\r\nCeļš negāja pa rāmu ieleju,<br />\r\nEs dzirdēju,kā vētrā jūra skan.<br />\r\nEs rūgtumu un sāpes pazinu,<br />\r\nKas cilvēku no putna gaisos šķir,<br />\r\nUn tomēr tūkstoš reizes atzīstu,<br />\r\nCik bagāta un skaista dzīve ir.', 'I.Kalnāne', '2012-07-26 13:34:47'),
(190, 'Smaidi, šodien šī diena ir tava!<br />\r\nMigla izgaist, kā mākonis balts!<br />\r\nSmaidi, šodien šī diena ir tava!<br />\r\nVisskaistākā diena ir veltīta Tev!', '', '2012-07-26 13:34:55'),
(191, 'Dzīves straumēm krāsu ir daudz,<br />\r\nDzīves dienām ceļu bez gala,<br />\r\nVajag zināt, kur Tavējais sauc,<br />\r\nKur Tavs sapnis un debesu mala.<br />\r\nDzīves krācēm grūtuma daudz,<br />\r\nSavi spēki ir jāsadala,<br />\r\nLai Tev nepieder pelēka sala,<br />\r\nBet lai vienmēr kāds ceļš Tevi tālē<br />\r\nPretī apvāršņu brīnumam sauc!', '', '2012-07-26 13:35:04'),
(192, '"Esi sieviete grēcīga,esi lepna un spēcīga!<br />\r\nEsi putna spārni, kas vēzienā atvilkti!<br />\r\nDrīksti visu, ko sieviete drīkst,<br />\r\nCelies augstu, lido tālu, esi laimīga!"', '', '2012-07-26 13:35:13'),
(193, 'Katra diena slēpj sevī iespējas un pārsteigumus...<br />\r\nLai izdodas atklāt un izmantot tos!<br />\r\nLai domu spārni, smalki kā zīds,<br />\r\nNes vienmēr uz varavīksni!<br />\r\nLai allaž uz pleca saules pilns rīts<br />\r\nVērš nedienu zeltainā drīksnā.', '', '2012-07-26 13:35:20'),
(194, 'Nekas uz šīs zemes nav vienāds,<br />\r\nne upe, ne jūra, ne krasts.<br />\r\nKatram mirklim, stundai<br />\r\nun dienai piemīt kas savs un neparasts.<br />\r\nKatrs smilšu graudiņš, katrs gliemežvāks,<br />\r\nvētru un viļņu glaudīts, mirdz savādāks.<br />\r\nKatrs cilvēks pasaulē plašā<br />\r\nir tik dažāds, kā cits neviens.', '', '2012-07-26 13:35:28'),
(195, 'Pie viršu druvas apstājos<br />\r\nUn skatos ziedos mēļos.<br />\r\nEs dzīves kalnā pakāpjos,<br />\r\nKa bagāts – pateikt vēlos.<br />\r\nKā augšup, lejup šūpolēs<br />\r\nMan dzīves ceļš ir gājis,<br />\r\nBet skatos baltos pūpolos,<br />\r\nUn aizmirstu, kurš rājis.<br />\r\n<br />\r\nKas grumbuļi, kas sprunguļi?<br />\r\nPret to, ko dzīvē guvis.<br />\r\nPēc lietus dienām bumbuļi<br />\r\nVer zaļiem asniem durvis.<br />\r\n<br />\r\nKo palīdz bēdas, ka ir prieks,<br />\r\nKas visur apkārt valda.<br />\r\nViss cits ir dzīvē tīrais nieks,<br />\r\nJa sirds tev ir bez malda.<br />\r\n<br />\r\nEs tādēļ dzīves rudeni<br />\r\nKā lielus svētkus teicu.<br />\r\nUn zilu viršu puteni<br />\r\nAr pateicību sveicu.', '', '2012-07-26 13:35:41'),
(196, 'Dzīvo tā kā Tava zvaigzne liek,<br />\r\nMīli tā, kā Tavi meži saka<br />\r\nJa Tev drauga rokas nepietiek,<br />\r\nMeklēt ej, kur aiztek stiegrā taka.<br />\r\nDzīvo tā kā Tava zvaigzne liek,<br />\r\nTad neviens neko Tev nepārmetīs.<br />\r\nDienas iet. Un baltos mežos snieg.<br />\r\nZveigznes ceļs tev pretī laipu metīs.', '', '2012-07-26 13:35:49'),
(197, 'Atceries ka kādu dienu<br />\r\nneatstāšu tevi vienu,<br />\r\nJo nākšu pie tevis <br />\r\nsvinēt dzimšanas dienu.', '', '2012-07-26 13:35:56'),
(198, 'Veselību stipru,<br />\r\nAllaž gaitu ņipru;<br />\r\nBrīnišķīgu omu,<br />\r\nTūkstoš labu domu!!!', '', '2012-07-26 13:36:04'),
(199, 'Lai Tevi mīlu nevis par to kas Tu esi, bet par to ka TU ESI.', '', '2012-07-26 13:36:46'),
(200, 'Viens nāks tavu dvēseli duļķot,<br />\r\nOtrs seklu un netīru sacīs.<br />\r\nKāds trešais nākš slāpes dzesēt,<br />\r\nTavā dzidrumā acis mazgāt.<br />\r\nCiti teiks:"Kāda apgrēcība!"<br />\r\nKāda vesmaina uguns kvēlo<br />\r\nKāds nosalis sildīsies tevī,<br />\r\nUn mūžs nebūs jānožēlo.<br />\r\nTu dzīvosi tiem,kuri nāks<br />\r\nUguns un ūdens šķīstīti,<br />\r\nTiem,kuriem vajadzēs tevi,<br />\r\nNedaudziem-taču īstiem.', 'V.Urtāne', '2012-07-26 13:55:32'),
(201, 'Ai, cik labi nesastingt, bet dzīvot, <br />\r\nDzīves dzelmē dziļi, dziļi nirt! <br />\r\nAtļaut matiem spējos vējos plīvot, <br />\r\nAtļaut zvaigznēm pašā sirdī birt.', 'A. Pormale', '2012-07-26 13:55:51'),
(202, 'Iemācies smieties ar saules smiekliem,<br />\r\nPelēkai dienai dziesmu liec klāt,<br />\r\nIzej tad dzīvi kā mūžību garu<br />\r\nOzola galotnē izdziedāt.', 'Sk.Kaldupe', '2012-07-26 13:56:08'),
(203, 'Ja nav ne zvaigznītes, kas gaismu dod,<br />\r\n<br />\r\nVar pasaule bez ziediem un bez skaņām būt ?<br />\r\n<br />\r\nLikt visam gavilēt un vizuļot<br />\r\n<br />\r\nVar maza, sīka cilvēkbērna sirds,<br />\r\n<br />\r\nJa viņa prieku jūt.', 'E.Zālīte', '2012-07-26 13:56:23'),
(204, 'Tev astoņpadsmit.<br />\r\nBagāta tu esi - tev pieder dzive, kuru jūti plaukstam<br />\r\ntev pieder laime, kuru līdz sev nesi<br />\r\npati savā spēka pārpilnajā plaukstā.', '', '2012-07-26 13:56:33'),
(205, 'Pacel, dzīve, vēl uz spārniem,<br />\r\nNe jau lidot,- vēju just,<br />\r\nVēju vien, kas dzīves burās<br />\r\nVēl aiz burātāja turas.<br />\r\nVēju, kas vēl tiekties liek<br />\r\nUn ar siltu elpu snieg<br />\r\nPāri nodzīvotām dienām.<br />\r\nPāri nodzīvotiem gadiem<br />\r\nPacel, dzīve, vēl uz spārniem.', 'M.Bārbale', '2012-07-26 13:56:47'),
(206, 'Neskaties spogulī,<br />\r\nbet skaties sevī!<br />\r\nstum ārā<br />\r\ntos vecos kravasratus,<br />\r\nlec pati iekšā -<br />\r\nTavas atmiņas lēks Tev līdz!<br />\r\nDod viņām darbu,<br />\r\nlai Tevi netraucē stūrēt.<br />\r\nNebaidies griezties,<br />\r\nmazie ceļi ir brīvi.<br />\r\nNeceļo viena -<br />\r\nļauj mīlai pieķerties pie<br />\r\nmotora malām,<br />\r\nļauj gribai iesūkties<br />\r\nbenzīna tvertnē!<br />\r\nTā netrūks Tev degvielas<br />\r\ntuksnešus izbraukt.<br />\r\nTā netrūks Tev degvielas<br />\r\npašai sevi atrast.', 'S.Muižniece', '2012-07-26 13:57:08'),
(207, 'Pēc veciem seņču tikumiem,<br />\r\nšņabi dzer bez likumiem,<br />\r\nlai galva zin cik dibens sver,<br />\r\nuz to mums atkal, jāiedzer!<br />\r\nApsveicu dzimšanas dienā!', '', '2012-07-26 13:57:17'),
(208, 'Mēdz teikt - ir jānotiek brīnumam. Par lietām par kurām Tu sapņo un ilgojies, bet īsti netici, jo ... pārāk skaisti, lai būtu patiesi. Tomēr brīnumi notiek ik uz soļa, ja proti tos pamanīt un noticēt.', '', '2012-07-26 13:57:28'),
(209, 'Laimi kā zilu pasaku,<br />\r\nSmieklus kā pavasara lietutiņu,<br />\r\nSpītību kā ūdens burbuli,<br />\r\nSavaldību kā akmens krūzi,<br />\r\nBurvību kā pasakās,<br />\r\nBet pāri visam mīlestību kā varavīksni!', '', '2012-07-26 13:57:40'),
(210, 'Lai dzīve tik skaista un dzirkstoša viz<br />\r\nKā šampanietis kristāla glāzē,<br />\r\nLai piepildās sapņi, kas tālāk trauc<br />\r\nUn laimes, lai bezgala bezgala daudz.', '', '2012-07-26 13:57:49'),
(211, 'Var būt ka jājautā citādāk ? <br />\r\n<br />\r\nbet no kurienes tevī saule nāk<br />\r\nkad citiem ļaudīm ir mēness?<br />\r\nun kas vēl tā prot un kas vēl tā māk<br />\r\nka sirdī nepaliek plēnes?<br />\r\n<br />\r\nvar būt ka vispār nav jājautā<br />\r\nun tu pati zini, ko dari<br />\r\nun dienā tik slapjā kā kājautā<br />\r\ntu iemāni pavasari', 'Knuts Skujenieks', '2012-07-26 13:58:03'),
(212, 'Maliet saules,<br />\r\nmazliet prieka,<br />\r\nbet par visu vairāk milestibas!', '', '2012-07-26 13:58:12'),
(213, 'Šodien Tava diena,<br />\r\nVisām pāri mirdz.<br />\r\nŠodien Tevi vienu,<br />\r\nApsveicam no sirds!', '', '2012-07-26 13:58:20'),
(214, 'Par rozi pārvērst ik bēdu<br />\r\nJa Dievs būtu atļāvis mums, <br />\r\nKā rožu dārzs plauktu tad zeme<br />\r\nUn zvaigžņotais debesjums.<br />\r\nAr smaidu par rozi vērst bēdu <br />\r\nLai reizēm liktenis ļauj<br />\r\nUn lai Tavas ziedlapiņas<br />\r\nKā rozei viņš nenorauj!', '', '2012-07-26 13:58:29'),
(215, 'Ar elegantu pietupienu, Atraktīvu palēcienu, Fantastisku pagriezienu - svini savu DZIMŠANSDIENU!', '', '2012-07-26 13:58:38'),
(216, 'Lai tev vienmēr jautra oma,<br />\r\nPriecīgs prāts un gudra doma,<br />\r\nLai tev vienmēr pietiek spara<br />\r\nDarīt to, ko jāpadara!', '', '2012-07-26 13:58:47'),
(217, 'Prieku, laimi spaiņiem smelt.<br />\r\nNaudu lieliem maisiem velt<br />\r\nVeselību saglabāt,<br />\r\nVisas bēdas sabradāt.', '', '2012-07-26 13:58:55'),
(218, 'Pacelt roku, lai noglāstītu, izstiept plaukstas, lai apmīļotu, piekļaut sev, lai mierinātu. Tikai ar tuviniekiem cilvēks nejūtas vientuļš. Tikai tas, kurš tiek pieņemts, jūtas par sevi drošs. Un viņam ir svarīgi, lai kāds to apliecinātu. Nekādi pasaules labumi nespēs aizstāt ikdienišķu maigumu...', 'Gunta Kraulere', '2012-07-26 13:59:29'),
(219, 'Kas godīgi pa ceļu ies,<br />\r\nTas mūžam neapmaldīsies.<br />\r\nBet kas gan tādam ziņu dos<br />\r\nPar brīnumiem meža biezokņos?', '', '2012-07-26 14:00:00'),
(220, 'Lai domu spārni,<br />\r\nSmalki kā zīds,<br />\r\nNes vienmēr uz varavīksni!<br />\r\nLai allaž uz pleca<br />\r\nSaules pilns rīts<br />\r\nVērš nedienu<br />\r\nZeltainā drīksnā.<br />\r\n<br />\r\nLai sveicina<br />\r\nCīrulis mākoņos<br />\r\nUn purene kumeļa pēdā!<br />\r\nLai Laima nes<br />\r\nKlēpjiem mīļumu<br />\r\nUn rieksta čaulā<br />\r\nSlēdz bēdas!', '', '2012-07-26 14:00:11'),
(221, 'Lai Tev vienmēr un visur veicas<br />\r\nkatru dienu pa domai, pa teikumam,<br />\r\ngaismas staram un atklāsmes brītiņam,<br />\r\nkatru dienu pa atziņai prātīgai<br />\r\nievijas gudrības audumā.<br />\r\nkatru dienu pa solim, pa kāpienam,<br />\r\ntaisnam ceļam un sasniegtai virsotnei,<br />\r\nkatru dienu pa veiksmes graudiņam<br />\r\nsakrājas dzīves apcirkņos.<br />\r\nkatru dienu pa sapnim, pa cerībai,<br />\r\nsmaidam siltam un vārdam sirsnīgam,<br />\r\nkatru dienu pa prieka brītiņam<br />\r\nieceļ dvēseli brīnuma apjausmā...', '', '2012-07-26 14:00:20'),
(222, 'Šodien Tava diena pāri visam mirdz,<br />\r\nŠodien Tevi vienu apsveicu no sirds.<br />\r\nBaudi dzīvi vienos priekos,<br />\r\nTrusikos un gumijniekos!', '', '2012-07-26 14:00:28'),
(223, 'Tici zvaigznēm debess tālē,<br />\r\nKas virs Tavas galvas mirdz,<br />\r\nTici nākotnei un dzīvei,<br />\r\nTici tam, ko saka sirds.', '', '2012-07-26 14:00:36'),
(224, 'Tavs pavasaris tik ziedošs<br />\r\nšai ziemas dienā,<br />\r\nTava jaunība, kā pumpurs,<br />\r\npirms zieds ir briedā.<br />\r\nTavi sapņi, kā bites <br />\r\nielido pašos krāšņākos ziedos.<br />\r\nVisa pasaule šodien<br />\r\nar Tevi ir biedros.', 'M. Dūka', '2012-07-26 14:00:50'),
(225, 'Maz enģelīts man priekšā teic,<br />\r\nKa Tevi šodien jāapsveic!<br />\r\nLai mīlestība apkart valda,<br />\r\nUn nedienas ar bultām skalda', '', '2012-07-26 14:01:02'),
(226, 'Kal laimi klintī cietajā, <br />\r\nKal tā lai stāv tā mūžīgi, <br />\r\nBet bēdas raksti smiltājā, <br />\r\nLai vējš tās aiznes tālumā.', '', '2012-07-26 14:01:35'),
(227, 'Tu šajā dienā dzimi<br />\r\nKā maziņš kamolīts,<br />\r\nBet šodien pāri visiem,<br />\r\nTu starus maigi sneidz!<br />\r\nUn visiem priecīgs prāts,<br />\r\nŠai Tavā dienā arī mums,<br />\r\nBet atceries, ka pāri visam,<br />\r\nIr laiks, ko pavadi ar mums!', '', '2012-07-26 14:01:43'),
(228, 'Lai vienmēr dvēsele Tava... kā brīnumsvecīte zied... lai ziedus, <br />\r\nzvaigznes un sapņus... tu savāktu vienuviet...', '', '2012-07-26 14:01:52'),
(229, 'Daudz laimes Tev Dzimšanas dienā<br />\r\nBet Tu tikai nelec sienā,<br />\r\nLai mēs varam Tevi apsveikt<br />\r\nUn vēl mīļus vārdus pateikt.', '', '2012-07-26 14:02:00'),
(230, 'Visiem zēniem prātu jauc,<br />\r\nVienu tik pie sevis sauc,<br />\r\nTevi savaldīt viņš spēs,<br />\r\nToties apsveikt spēšu es.', '', '2012-07-26 14:02:09'),
(231, 'Kad tu piedzimi, lija lietus!<br />\r\nnē tas nebija lietus, tās bija debesis,<br />\r\nkuras raudaja, ka ir zaudejušas eņģeli!', '', '2012-07-26 14:02:22'),
(232, 'Ja tās domas, ko Tev veltu<br />\r\nVisas puķēs pārvērstos,<br />\r\nBagāts dārzs ap Tevi zeltu<br />\r\nGrimdams smaržu vīrakos!', '', '2012-07-26 14:02:31'),
(233, '"Labām lietām cilvēks nekad nav par vecu..."', 'Z.Mauriņa', '2012-07-26 14:02:50'),
(234, 'Tai laimei tici, ko tev draugi vēl,<br />\r\nLai dzīves dārzā tu vēl ilgi ziedi.<br />\r\nLai gadu nodzīvoto tev nav žēl,<br />\r\nBet nākamie lai deg kā krāšņi rieti!', '', '2012-07-26 14:02:59'),
(235, 'Daudz, daudz laim un lustīg prāt,<br />\r\nLai Tev nauda čupām nāk,<br />\r\nLai Tu vinnē Volg vai Čaik,<br />\r\nLai Tev netrūkst šnabs un paik!', '', '2012-07-26 14:03:09'),
(236, 'Prieku kā saules glāstu <br />\r\nIk rītu no jauna rast. <br />\r\nLaimi ikdienas stāstā <br />\r\nVienmēr nosargāt prast!', '', '2012-07-26 14:03:18'),
(237, 'Tava zvaigzne ir tevī pašā<br />\r\nUn nākamības melodija-ziedos.<br />\r\nPacel rokas pretī Mīlestībai ,<br />\r\nUn tu tapsi aplaimots.<br />\r\nPacel rokas pretī Saulei,<br />\r\nUn tu tapsi visuvarens.<br />\r\nTava zvaigzne ir cerību zvaigzne<br />\r\nMelna meža koka galotnēs,<br />\r\nKas atnesīs gaismas putna<br />\r\nMūžības dziesmu.', 'Jānis Anmanis', '2012-07-26 14:03:36'),
(238, 'Vēlu Tev mīlēt to,ko ir vērts mīlēt, <br />\r\nAizmirst to,ko nav vērts atcerēties. <br />\r\nGūt atelpu steigas brīdī, <br />\r\nUzcelt sētu starp sevi un raizēm!', '', '2012-07-26 14:03:44'),
(239, 'Ieskatījos kalendārā, <br />\r\nzibens iespēra man prātā ,<br />\r\njopcik copcik spainis piena, <br />\r\nTev tak šodien dzimšansdiena!!!', '', '2012-07-26 14:03:55'),
(240, 'Lai ir kāda<br />\r\nzeltaina kļava arvien,<br />\r\nKam galotnē sirdi tu rudeņos sien,<br />\r\nUn pāri lapām, kad sniegpārslas krīt,<br />\r\nLai ir kāda roze, kas dvēselē zied...<br />\r\nKāds pumpurs, ko neskar sāpes un laiks,<br />\r\nKaut sudrabo mati un skumīgs kļūst vaigs.<br />\r\nStarp ērkšķotiem ceļiem,<br />\r\nko dzīve mums dod<br />\r\narvien ir debesis jāatrod.', '', '2012-07-26 14:04:05'),
(241, 'Šodien....<br />\r\nVisi vēl Tev Laimi.<br />\r\nZiedus klēpiem dāvāt steidz.<br />\r\nArī es.....<br />\r\nTev gribu vēlēt,<br />\r\nDzīvi mīlēt neizbeidz.<br />\r\nDroshi ej .....<br />\r\nPa savu ceļu,<br />\r\nPretī sapņiem steidz<br />\r\nUn lai nepietrūkst Tev spēka,<br />\r\nDzīvē...<br />\r\nSapņus īstenot.', '', '2012-07-26 14:04:14'),
(242, 'Uz tevi, uz tevi šīs puķes iet,<br />\r\nUz tevi šīs ziedošās domas -<br />\r\nSirds saule tevī lai nenoriet!<br />\r\nTo lūdz manas gaidošās domas.<br />\r\nTev vēlu mirdzošās debesis,<br />\r\nTev zaļos kalnus un lejas -<br />\r\nLai tavās acīs šis vēlējums viss!<br />\r\nKā daļa no mūžīgās dzejas.', '', '2012-07-26 14:04:23'),
(243, 'Jālūdz atvasarai, lai tā spēku aizdod,<br />\r\nJālūdz puteņiem, lai gadiem pēdas jauc.<br />\r\nJālūdz paša sirdij - tā vislabāk zinās,<br />\r\nKā tos trakos gadus atpakaļ lai sauc.', '', '2012-07-26 14:04:32'),
(244, 'Dzimšanas dienā lai maigums<br />\r\nIesūcas katrā šūnā.<br />\r\nPaceļ Tevi tik augstu,<br />\r\nCik vari izturēt.', '', '2012-07-26 14:04:40'),
(245, 'Dzimšanas dienas rītam<br />\r\nTāda dīvaina elpa-<br />\r\nSmaržo pēc padarītā<br />\r\nSmaržo pēc jauna darba,<br />\r\nSmaržo pēc izceptas maizes<br />\r\nUn pēc pavasara smaržo.<br />\r\nTikai tev atkal gada aplī<br />\r\nKā vāvere ritenī.', 'A.Ape', '2012-07-26 14:04:54'),
(246, 'Lai ir grūt` vajag spēt,<br />\r\nPeļķē sēžot gavilēt!', '', '2012-07-26 14:05:02'),
(247, 'Kā vēja plūsma aiziet gadi,<br />\r\nIkviens no viņiem tevi skars.<br />\r\nNekad mēs nebūsim vairs tādi,<br />\r\nKādi bijām vakar, aizvakar.', '', '2012-07-26 14:05:10'),
(248, 'Lai gadi iet - bet gadi nevar biedēt,<br />\r\nVar cilvēks puteņos un salnās ziedēt.', '', '2012-07-26 14:05:21'),
(249, 'Var aizpalot gadi, sirds vienmēr būs nemiera pilna, jo dzīve ir nemiers un nemierā skaistuma smeldze.', '', '2012-07-26 14:05:32'),
(250, 'Kur tu slēpies, gadu skaitītāj?<br />\r\nNeredzu es Tevi vaigu vaigā.<br />\r\nTikai jūtu - kāds pa pēdām staigā<br />\r\nun aiz dienas dienā gadus krāj.', '', '2012-07-26 14:05:40'),
(251, 'Dzimeniiti viegli svineet <br />\r\nOtraa dienaa gruuti mineet<br />\r\nkaapeec saap galva <br />\r\nUn es atrodos zem galda!', '', '2012-07-26 14:05:50'),
(252, 'Lai Tevi mīl visi visur daudz un bieži, galvenais , lai tas Tev nestu prieku!', '', '2012-07-26 14:05:58'),
(253, 'Vēl ilgus gadus stipram būt,daudz prieka vēl no dzīves gūt.Un sauli sirdī saglabāt,un dzīvi mīlēt nepārstāt!', '', '2012-07-26 14:06:13'),
(254, 'Skaiti savas naktis pēc zvaigznēm,<br />\r\nbet ne pēc ēnām.<br />\r\nSkaiti savu dzīvi pēc smaidiem,<br />\r\nbet ne pēc asarām.<br />\r\nUn ar prieku katrā dzimšanas dienā,<br />\r\nSkaiti savu vecumu pēc draugiem,<br />\r\nbet ne pēc gadiem.', '', '2012-07-26 14:06:21'),
(255, 'Ar vēlējumu draudzīgu un gaišu<br />\r\nMēs nākam tevi jubilejā sveikt.<br />\r\nLai dzīves dienas nepazīstu raižu,<br />\r\nLai dvēsele sirdsmierā gavilē!', '', '2012-07-26 14:06:29'),
(256, 'Ne jau vienmēr jābūt klāt,<br />\r\nJubilāru sumināt.<br />\r\nVar jau arī klusi sveikt-<br />\r\nNosūtīt un vārdus teikt;<br />\r\nVēlēt daudz? Nē, tomēr vienu-<br />\r\nSagaidīt tev Pastardienu!', '', '2012-07-26 14:06:37'),
(257, 'Es nezinu,<br />\r\nkas Tu esi Dieva brīnumu<br />\r\ndārzā-vai balta mežroze asa vai<br />\r\nsirdslauze sārta un vāra.<br />\r\n<br />\r\nVarbūt Tu ziedoša ieva vai<br />\r\nsaplaucis ceriņu krūms, bet<br />\r\nvarbūt, ka lapainais rabarbers<br />\r\nsētmalē iesēdies drūms?<br />\r\n<br />\r\nAk,vai nav vienalga, kā citi<br />\r\nvēro Tevi un sauc! Galvenais-lai<br />\r\nTavā dārzā ir mīlas un saules daudz!', '', '2012-07-26 14:06:47'),
(258, 'Kā saules stariņš zaigo,<br />\r\nKā ūdens lāse, kas mirdz,<br />\r\nTā tavu sirsniņu maigo,<br />\r\nVēlos es apsveikt no sirds.<br />\r\nLai katra diena tev īsta,<br />\r\nUn dari, jo tā Tu audz,<br />\r\nLai prieku Tu rodi draugos,<br />\r\nUn vienmēr tie pulka daudz..', '', '2012-07-26 14:06:55'),
(259, 'Ja vaicātu Dievs man, ko gribu es vēl -<br />\r\nVai bagātu dzīvi vai garu, -<br />\r\nEs atbildētu:"Lai mīl mani kāds<br />\r\nUn kādu lai mīlēt es varu."', 'A.Priedīts', '2012-07-26 14:07:17'),
(260, 'Līksmai dienai - putnu dziesma,<br />\r\nAukstai - ugunskura liesmu,<br />\r\nLabam miegam - jūras šalku,<br />\r\nLielām slāpēm - rasas malku,<br />\r\nKarstai dienai - vēju lēnu,<br />\r\nNogurumam - vēsu ēnu,<br />\r\nSkumjām - pūpolmīkstu glāstu,<br />\r\nTukšiem brīžiem - brīnumstāstu,<br />\r\nTumšai naktij - zvaigžņu lietu,<br />\r\nSirdī mīlai - siltu vietu!', '', '2012-07-26 14:07:26'),
(261, 'Atvērt dvēseli priekam<br />\r\nUn zināt,ka laime sūrst,<br />\r\nSevī nepalikt liekai<br />\r\nUn pasauli neatgrūst.<br />\r\nSkaistumam atvērt sirdi<br />\r\nUn atļaut asarām līt,<br />\r\nČukstam likt modināt sirdi<br />\r\nUn skatienam sasildīt.', '', '2012-07-26 14:07:35'),
(262, 'Nevajag skaitīt gadus,<br />\r\nKuri aulēkšiem skrien -<br />\r\nGadus tāpat kā naudu,<br />\r\nNoturēt nespēj neviens.<br />\r\nSkaitīsim laimīgos mirkļus,<br />\r\nKuru mūžā bijis tik daudz -<br />\r\nAr tiem mēs bagāti esam<br />\r\nUn novecot nebūs ļauts!', '', '2012-07-26 14:07:44'),
(263, 'Sakrāt pilnu sauju prieka monētu,<br />\r\nNoķert laimes pūķi aiz astes,<br />\r\nUzzināt brīnuma noslēpumu,<br />\r\nVēlēties un piepildīt vēlēšanās,<br />\r\nSaprast gaišo domu lidojumu,<br />\r\nDzīvot mīlestībā,<br />\r\nNoticēt sev...<br />\r\nLai Tev izdodas viss, ko vēlies,<br />\r\nLai izdodas!!!', '', '2012-07-26 14:07:53'),
(264, 'Tukstoš dzidru saules staru<br />\r\nLai vēl ilgi dzīvē mirdz.<br />\r\nPrieku, laimi, veselību<br />\r\nVēlam tev no visas sirds.', '', '2012-07-26 14:08:01'),
(265, 'Baudi dzīvi kamēr vari,<br />\r\njo šī diena ir tikai tava.', '', '2012-07-26 14:08:09'),
(266, 'Negaidi,<br />\r\nka lieti līs,<br />\r\npirms zibeņi saplīsīs<br />\r\ntavas dvēseles debesīs.<br />\r\nNeraudi,<br />\r\nka tas nebūs drīz.<br />\r\nStarp putekļiem brīdis īss<br />\r\ntevi reiz pārbaudīs.<br />\r\nPārbaudīs ?<br />\r\nvai ir kam plīst<br />\r\ntavas dvēseles debesīs...', 'M. Čaklais', '2012-07-26 14:08:23'),
(267, 'Kā rīta rasa dzīve šķiet,<br />\r\nkā spoža rīta rasa,<br />\r\nkā sapnis jauks, kas neaiziet,<br />\r\nkā grāmata, ko never ciet,<br />\r\nko mūžam neizlasa...', '', '2012-07-26 14:08:35'),
(269, 'Debesīs tūkstoši zvaigžņu spīd,<br />\r\nSavu tur grūti saskatīt<br />\r\nMeklē! Roku pēc skaistākās zvaigznes sniedz,<br />\r\nNebaidies, tieši sirdī to liec!', '', '2012-07-26 14:09:00'),
(270, 'Laimi-kā zilu pasaku<br />\r\nSmieklus-kā pavasara lietutiņu,<br />\r\nSpītību-kā akmens krūzi,<br />\r\nBurvību-kā pasakās,<br />\r\nBet,pāri visam-Mīlestību kā varavīksni!!!', '', '2012-07-26 14:09:08'),
(271, 'Tev šodien laimes vēlēs daudzi!<br />\r\nSirds pilna mīlestības būs!<br />\r\nBet kas tad īsti ir tā laime?<br />\r\nVai viegli sasniegt pilnību?<br />\r\nTad zini- tādas nav!<br />\r\nJo cilvēks vienmēr ceļā!', '', '2012-07-26 14:09:16'),
(272, 'Plikumus ja vēlies baudīt,<br />\r\nUz jaunām meičām acis šaudīt, <br />\r\nTad turies, vecīt, cik vien spara,<br />\r\nJo priekšā vēl ir dzīve gara........', '', '2012-07-26 14:09:29'),
(273, 'Aiz muguras jaunības trakums,<br />\r\nVēl tālu vecuma miers<br />\r\nBet vidū tie trakākie gadi,<br />\r\nKā kaklā iesprūdis siers...<br />\r\nUn vidū tie aplamie gadi,<br />\r\nKad vieglums sāk sirdi spiest,<br />\r\nKad gribas par kādu rūpēties,<br />\r\nKad gribas par kādu ciest..', 'A.Skalbe', '2012-07-26 14:09:42'),
(274, 'Nav bijis vēl neviens tāds gads,<br />\r\nKas tikai laimi nestu, no bēdām, rūpēm sargātu<br />\r\nUn laimes kalnā vestu.<br />\r\nBet laimīgs tas, kas smaidīt prot, <br />\r\nŅemt to, ko dzīve pretī dod!', '', '2012-07-26 14:09:52'),
(275, 'Trīs ziedus es tev sūtu, <br />\r\nJo trijos ziedos pateiksts viss!<br />\r\nViens domāts laimei, otrs- mīlai,<br />\r\nBet trešais domāts vāzē likt!', '', '2012-07-26 14:18:28'),
(276, 'Mēs šodien Tevi sveiksim,<br />\r\nDaudz mīļu vārdu teiksim!<br />\r\nTev sniegsim rozes sārtas,<br />\r\nUn bučosim pēc kārtas!!!', '', '2012-07-26 14:18:42'),
(277, 'Un kas par to, ka gadu vesels klēpis,<br />\r\nKā pļavu ziedi laika vāzēs mirdz...<br />\r\nGan sūrumu, gan prieku katrs gads ir slēpis,<br />\r\nUn viss ir bijis vajadzīgs priekš sirds...<br />\r\nLai sajustu, cik dzīve skaista,<br />\r\nKur tā kā bite vari būt,<br />\r\nTo sudrabu, no mirkļu kārēm vācot,<br />\r\nKas neapsūb un vējos nepazūd!', '', '2012-07-26 14:18:53'),
(278, 'un, jā, reizēm vajag<br />\r\nkaut ko mīļu<br />\r\nsirdij tuvu patiesu<br />\r\nmaigu pieskārienu<br />\r\ncīruļa dziesmu<br />\r\nuguns liesmu<br />\r\nzaļu zālīti un pūku liegu<br />\r\nkas matos ieķeras<br />\r\nun priecājas par nieku<br />\r\nkas liek pasmaidīt<br />\r\nun mīlēt saules rietu<br />\r\nreizēm vajag<br />\r\nlai mēs ietu<br />\r\ntālāk triektu<br />\r\nreizēm vajag<br />\r\napstāties<br />\r\nieklausīties<br />\r\nizbaudīt<br />\r\nkatru nieku<br />\r\nCilvēks ir radits laimei-tāpat kā putns lidojumam...', '', '2012-07-26 14:19:13'),
(279, 'Atļauj šodien saviem gadiem ziedēt<br />\r\nkā ziedlapiņām, kuras saulē plaukst,<br />\r\nAtļauj šodien saviem gadiem skanēt<br />\r\nkā melodijai, kura sirdī šalc.<br />\r\nĻauj kādam sapnim šodien zvaigznēs mirdzēt,<br />\r\nLai dienu skrejā tie pie tevis trauc!', 'A.Āre', '2012-07-26 14:19:30'),
(280, 'Dienas vizot un līgojot gājušas -<br />\r\nTās kā buras gar krastmalu iet.<br />\r\nNu kā vēji pret rietu tās stājušas,<br />\r\nTikai sirds vēl - tā sapņo un dzied.<br />\r\nVisas vasaras, vasaras zeltītas<br />\r\nIt kā gaistoši mākoņi skrien,<br />\r\nBet, ja dziesmai un mīlai Tu velti tās,<br />\r\nViņas staro un smaržo arvien.', 'L.Breikšs', '2012-07-26 15:00:18'),
(281, 'Laime lai atnāk un paliek.<br />\r\nLai vēlīgs likteņa prāts!<br />\r\nMīlestības lai pietiek, ko saņemt<br />\r\nun citiem dot.', '', '2012-07-26 15:00:43'),
(282, 'Ar tevi dienas sarunājas,<br />\r\ndarbs padarītais tevi teic.<br />\r\nkā dārgakmeņi gadi krājas,<br />\r\nPie tevis apstājas un sveic.', '', '2012-07-26 15:00:56'),
(283, 'Gads aiztek, aizsteigsies vēl simti,<br />\r\nMēs nespējam tiem tālo ceļu liegt.<br />\r\nBet katrs jaunais sveiks mūs silti<br />\r\nJa spēsim paši laimei roku sniegt.', '', '2012-07-26 15:01:07'),
(284, 'Vai laimei ticēt var, tā ir kā zelta graudiņš,<br />\r\nilgilgi meklēts, visu garu mūžu.<br />\r\nBet ticēt gribas gan, vai dziedi, raudi,<br />\r\nlīdz pēdīgam sirds neprāts lec un lec<br />\r\nkā aušīgs bērns to spēli: "Laimīgs būšu."', '', '2012-07-26 15:01:16'),
(285, 'Maziņajai maziņi sveicieni ar lielu nozīmi un bezvārdu vēlējumiem tieši no sirds uz vietu, kur vien trāpa!', '', '2012-07-26 15:01:25'),
(286, 'Ieklausies, kā ziedi zvana,<br />\r\nIeklausies,kā runā sirds,<br />\r\nVarbūt kādā mazā ziedā <br />\r\nTava lielā laime mirdz!', '', '2012-07-26 15:01:34'),
(287, 'Daudz vairāk par mantu un zeltu<br />\r\nSpēj dāvāt cilvēka sirds,<br />\r\nLūk,tāpēc šīs rindas Tev veltu,<br />\r\nPar to šis paldies Tev mirdz!', '', '2012-07-26 15:01:43'),
(288, 'Ievij savas dzīves prieku saules pavedienā, <br />\r\nlai ar viņas pieskārieniem vari starot dienā!', '', '2012-07-26 15:01:51'),
(289, 'Jubileja vienreiz gadā,neliec draugiem nomirt badā. Uzliec galdā torti,šņabi, Lai var patusēties labi!', '', '2012-07-26 15:02:00'),
(290, 'Gadu straumēm vītas aiziet dienas<br />\r\nDzīves jūrā saplūst vienuviet.<br />\r\nLai vēl ilgi mirdz tev ceļa zvaigzne,<br />\r\nDarbi veicas, gaišas dienas rit', '', '2012-07-26 15:02:11'),
(291, 'Mainīsies dzīvē Tev prieki un bēdas,<br />\r\nCeļi Tev ceļā un kalnā ies.<br />\r\nNekad nemeklē atpakaļ pēdas,<br />\r\nGrūti vai viegli- uz priekšu tiec!', 'L.Rozentāle', '2012-07-26 15:02:26'),
(292, 'Uzrunāt debešos zvaigzni<br />\r\nbezgalību minēt<br />\r\npaieties pretim vējam<br />\r\nik dienas ikdienu svinēt<br />\r\ntad viegli pacelties spārnos<br />\r\nnekur tālu - virs sevis<br />\r\nun redzēt cik pasaule skaista<br />\r\nDieviņš pats Dieviņš ko devis...<br />\r\nun atkal stāvēt uz zemes<br />\r\nreāli stabili droši<br />\r\nbet nezaudēt sidraba staru<br />\r\nto staru kas sirdī noviz...<br />\r\nik rītu tā sākt: celties spārnos<br />\r\nbrīvi kā putns kā vējš<br />\r\nun lidot tik ilgi cik dzīvība<br />\r\nkā spāre<br />\r\nvirs ūdeņiem sēž', 'M.Laukmane', '2012-07-26 15:02:44'),
(293, 'Kad klinģeris smaržo<br />\r\nUn svecītes mirdz,<br />\r\nDaudz laimes tad vēlu<br />\r\nNo visas sirds.', '', '2012-07-26 15:02:53'),
(294, 'Lai viss ko velies-PIEPILDAS.<br />\r\nLai viss ko dari-IZDODAS,<br />\r\nLai viss ko gribi ir SASNIEDZAMS.<br />\r\nBet nekad lai nav ta,ka nav NEKAD!', '', '2012-07-26 15:03:03'),
(295, 'Mazs enģelīts man priekšā teic,<br />\r\nKa Tevi šodien jāapsveic.<br />\r\nLai amoriņš Tev sirdī valda,<br />\r\nUn visas bēdas bultām skalda.', '', '2012-07-26 15:03:11'),
(296, 'Ar labu vārdu sauli vadi<br />\r\nAr mīļu smaidu rītam pretī ej<br />\r\nUn visi tavi nodzīvotie gadi<br />\r\nKā labi vārdi skanēs pasaulei.', '', '2012-07-26 15:03:20'),
(297, 'Tavos svētkos vēlu tev<br />\r\nTo, ko vēlējos es sev:<br />\r\nPrieku, naudu, puķu vāzi,<br />\r\nPudeli un lielu glāzi. <br />\r\nLaimi kā zilu pasaku,<br />\r\nSmieklus kā pavasara lietutiņu,<br />\r\nSpītību kā ūdens burbuli,<br />\r\nSavaldību kā akmens krūzi,<br />\r\nBurvību kā pasakās,<br />\r\nBet pāri visam mīlestību kā varavīksni!', '', '2012-07-26 15:03:29'),
(298, 'Lai Tev laime brauc ar dampi,<br />\r\nVeselība pakaļ skrien,<br />\r\nNaudas maks lai dabū krampi<br />\r\nNo tiem spožiem latiņiem.', '', '2012-07-26 15:03:38'),
(299, 'Mums dzīves ceļos akmeņi un māli,<br />\r\nIr šaubas, vilšanās un prieks.<br />\r\nTam visam pāri varavīksnes vāli<br />\r\nKā cerība, kas laimi sniegs<br />\r\nTūkstoš dzidru saules staru<br />\r\nLai vēl ilgi dzīvē mirdz.<br />\r\nPrieku, laimi, veselību<br />\r\nVēlējām no visas sirds!', '', '2012-07-26 15:03:51'),
(300, 'Vārda dienā neķer mušas,<br />\r\nIedzer vīnu, saņem bučas,<br />\r\nUn lai sirdij jautrāk kļūtu<br />\r\nApēd gabaliņu kūkas!', '', '2012-07-27 10:14:47'),
(301, 'Sveicu Tevi dienā šajā,<br />\r\nKas ir saukta vārdā Tavā,<br />\r\nKo Tev māte,tēvs ir devis,<br />\r\nTas ir skaists un tik priekš Tevis!!!', '', '2012-07-27 10:14:58'),
(302, 'Ar sievišķību trauslu, cēlu,<br />\r\nik dienas tu starp ļaudīm ej,<br />\r\nno sevis labestības gaismu<br />\r\ncaur smaidu viņu sirdīs lej.<br />\r\nTu proti atrast labu vārdu,<br />\r\nikvienam, kas tev pretī nāk,<br />\r\nun arī mierinošu glāstu,<br />\r\nja kādam ļoti, ļoti sāp.<br />\r\nLai vēlēšanās piepildās ikviena,<br />\r\nprieks, laime, mīla ceļā kvēl,<br />\r\nto šodien, tavā vārda dienā,<br />\r\nno visas sirds tev draugi vēl.', '', '2012-07-27 10:15:08'),
(303, 'Draugi ir kā zvaigznes-<br />\r\nne vienmēr tu tos redzi,<br />\r\nBet vienmēr tie būs Tev blakus.....<br />\r\nSveiciens vārda dienā!', '', '2012-07-27 10:15:16'),
(304, 'Veselībā, priekā tīts,<br />\r\nLai aust tev vārdadienas rīts!<br />\r\nUn galvu nenoliekt par niekiem,<br />\r\nLai domas paliek tikai priekiem!', '', '2012-07-27 10:15:26'),
(305, 'Vārda dienā saki tā:<br />\r\n"Tā ir tikai manējā!"<br />\r\nLai šai dienā jautri iet -<br />\r\nVajag glāzē šampi liet!', '', '2012-07-27 10:15:33'),
(306, 'Būs brīnumi, ja vien tiem noticēsim,<br />\r\nBūs laime tad, ja pratīsim to dot.<br />\r\nBūs prieks, ja protam citus ielīksmot.<br />\r\nUn nevajag nemaz tik daudz-<br />\r\nPavisam maz, <br />\r\nPavisam nieku,<br />\r\nLai mirdzošs mirklis nestu prieku!', '', '2012-07-27 10:15:41'),
(307, 'Prieku kā saules glāstu<br />\r\nIk rītu no jauna rast!<br />\r\nLaimi ikdienas stāstā<br />\r\nVienmēr nosargāt prast!', '', '2012-07-27 10:15:48'),
(308, 'Katram apsviedīgam vīram<br />\r\nJāsaplāno vārdadiena:<br />\r\nDienu - svinēt,<br />\r\nRītdien - lāpīt, <br />\r\nVakarpusē ? gultā trāpīt.<br />\r\nTrešā dienā skaitīt naudu,<br />\r\nAtcerēties gūto baudu.<br />\r\nPārrunāt kā cēlies, kritis,<br />\r\nDancojis, zem galda mitis...<br />\r\nBeidzot plānošanu šo -<br />\r\nApsvērt balli nākošo.', 'D.Kamerovska', '2012-07-27 10:16:05'),
(309, 'Ar vārdu šūpulī, kas likts, <br />\r\nMums lemts ir mūžu nostaigāt. <br />\r\nPar viņu nebūs jadzird slikts, <br />\r\nJa pratīsim ap sevi krāt <br />\r\nMēs mīlestības siltos starus; <br />\r\nUn darbu krietni padarīt; <br />\r\nTad mūsu vārds kā ozols zaros <br />\r\nSev vienmēr varēs sauli vīt.', '', '2012-07-27 10:16:13'),
(310, 'Žēl ka nevaram tur būt<br />\r\nKūkas ēst un šņabi sūkt <br />\r\nPastam jāuztic būs teiciens <br />\r\nVārda dienā kvēls lai sveiciens!', '', '2012-07-27 10:16:21'),
(311, 'Kāds eņģelīts man priekšā teic,<br />\r\nKa Tevi šodien jāapsveic.<br />\r\nLai Amoriņš Tev sirdī valda,<br />\r\nNeveiksmes ar bultām skalda!<br />\r\nMīļi sveicu Tevi vārda dieniņā!!!', '', '2012-07-27 10:16:28'),
(312, 'Ļauj novēlēt Tev vārda dienā laimi,<br />\r\nLai Tavā dienā laimes stundu daudz;<br />\r\nJa ceļš būs grūts, lai apkārt draugu saime,<br />\r\nKas roku sniedz un Tevi tālāk sauc!', '', '2012-07-27 10:16:37'),
(313, 'Visu labo un skaisto,<br />\r\nKo tikai vēlies sev!<br />\r\nTavā vārda dienā<br />\r\nSirsnīgi novēlu tev!', '', '2012-07-27 10:16:44'),
(314, 'Kad enģelis atvēra debesu logu, viņš jautāja, kāda ir mana vēlēšanās šodien? Es teicu: Mīļo eņģelīt, parūpējies par manu draugu, kuram šodien ir vārda diena!', '', '2012-07-27 10:16:52'),
(315, 'Viens mazs prieks, lai tev būtu katra diena, <br />\r\nViens skats, viens zieds, viens laimes stūrītis.<br />\r\nViens saules stariņš un mazliet debesis<br />\r\nUn labs vārds, kas dzīvi dara skaistu.', '', '2012-07-27 10:17:00'),
(316, 'Saules stariņš <br />\r\ntavā sejā, <br />\r\nšajā dienā, <br />\r\nšajā vietā,<br />\r\nlai Tev vārdiņš<br />\r\nlaimi nes.', '', '2012-07-27 10:17:08'),
(317, 'Šodien liela diena tev,<br />\r\nDaudz daudz prieka gūsti sev.<br />\r\nDraugu sveikts un apčabots - <br />\r\nLai tev vārdam šodien GODS!', '', '2012-07-27 10:17:15'),
(318, 'Tavs vārds ar pirmās dziesmas žūžu, <br />\r\nNo bērnības ir ceļam līdzi dots, <br />\r\nNo laiku laikiem un uz visu mūžu, <br />\r\nTas būs tavs lepnums, cildenums un gods.', '', '2012-07-27 10:17:23'),
(319, 'Ja es būtu putniņš mazs,<br />\r\nKas uz zara karājas!<br />\r\nVārda dienā Tev par prieku<br />\r\nDāvinātu treknu slieku!', '', '2012-07-27 10:17:32'),
(320, 'Vārds tiek dots, kad dzīvi sākam<br />\r\nKatru dienu daudzināts.<br />\r\nBet ik gdu vienu dienu<br />\r\nLaimes vārdā sumināts!', '', '2012-07-27 10:17:41'),
(321, 'Lai tavā vārdadienā<br />\r\nTev prieku dāvā viss,<br />\r\nLai draugi pasniedz ziedus<br />\r\nUn sauli debesīs!', '', '2012-07-27 10:17:48'),
(322, 'Vārdadienā nepiedzeries,<br />\r\nLabāk manos vārdos veries -<br />\r\nNovēlu tev dzīvot forši,<br />\r\nGarāžā lai pieci porši!', '', '2012-07-27 10:17:55'),
(323, 'Lai domu spārni,<br />\r\nSmalki kā zīds,<br />\r\nNes vienmēr uz varavīksni!<br />\r\nLai allaž uz pleca<br />\r\nSaules pilns rīts<br />\r\nVērš nedienu<br />\r\nZeltainā drīksnā.<br />\r\nLai sveicina<br />\r\nCīrulis mākoņos<br />\r\nUn purene kumeļa pēdā!<br />\r\nLai Laima nes<br />\r\nKlēpjiem mīļumu<br />\r\nUn rieksta čaulā<br />\r\nSlēdz bēdas', '', '2012-07-27 10:18:03'),
(324, 'Novēlu lai tev šodien nolīst zvaigžņu lietus, un lai katra no zvaigznēm nes tev mieru, laimi, mīlestibu, veselību un bagātību.', '', '2012-07-27 10:18:10'),
(325, 'Miķelīšu mākonī, <br />\r\nvārds Tavs tinies, <br />\r\nUn tik skaistu rudeni <br />\r\nTu pat neatminies. <br />\r\nUguntiņas sarkanas, <br />\r\nUguntiņas baltas <br />\r\nTavu vārdu sasilda, <br />\r\nAizdzen vējus saltus!', '', '2012-07-27 10:18:18'),
(326, 'Vārda dienā sirsnīgs sveiciens<br />\r\nUn tam līdzi vecais teiciens:<br />\r\n"Vieglu tavu dzīves gaitu,<br />\r\nPrieka dienām nejust skaitu!"', '', '2012-07-27 10:18:27'),
(327, 'Vārds tiek dots,<br />\r\nKad dzīvi sākam.<br />\r\nKatru dienu daudzināts,<br />\r\nBet ik gadu vienu dienu<br />\r\nLaimes vārdā sumināts!', '', '2012-07-27 10:19:45'),
(328, 'Es jau sen šo dienu gaidu,<br />\r\nLai ar vienu platu smaidu,<br />\r\nTeiktu vienā paņēmienā<br />\r\nSveicu tevi vārda dienā!', '', '2012-07-27 10:19:51'),
(329, 'Vēlu prieku, veselību,<br />\r\nVienu uti kažokā.<br />\r\nJa Tu mani aizmirsīsi,<br />\r\nLai Tev iekož dibenā.', '', '2012-07-27 10:19:59'),
(330, 'Šodien ziņu Tev rakstu<br />\r\nDatoram pogas bakstu<br />\r\nGribu Vārda dienā sveikt<br />\r\nKaut ko gudru arī teikt<br />\r\nLasi ziņu un smaidi<br />\r\nUn no citiem ziņu gaidi!', '', '2012-07-27 10:20:06'),
(331, 'Vārda dienā lielu prieku<br />\r\nDabūt dāvanā kaut nieku<br />\r\nLai tik sirdī dzīvesprieks<br />\r\nUn viss pārējais būs nieks', '', '2012-07-27 10:20:13'),
(332, 'Met projām šodien rūpes,<br />\r\nKā vīns, lai dzirkstī<br />\r\nVārda dienas prieks!<br />\r\nLai dziesmas plūst<br />\r\nNo draugu lūpām<br />\r\nUn līdz pat rītam<br />\r\nNenāk miegs!', '', '2012-07-27 10:20:18'),
(333, 'Ir vārdu pasaulē tik daudz, <br />\r\nBet vienu visu mūžu nesam,<br />\r\nUn labi just, kad paguruši esam -<br />\r\nKāds Tevi atceras, kāds vārdā tevi sauc.', '', '2012-07-27 10:20:27'),
(334, 'Vārda dienu labi svinēt<br />\r\nOtrā dienā var tik minēt<br />\r\nKādēļ guli zālē zaļā,<br />\r\nGalva sāp un bikses vaļā!', '', '2012-07-27 10:20:34'),
(335, 'Vārda dienā strādāts gana,<br />\r\nNotiek maza plostošana;<br />\r\nIedzer, uzkož, jūtas brīvi, <br />\r\nKritizē un slavē dzīvi.', '', '2012-07-27 10:20:40'),
(336, 'Dārzā kur zied puķkāposti <br />\r\nUzplaucis ir milzīgs zieds, <br />\r\nTo tev vārda dienā sūtu, <br />\r\nLai tev būtu milzīgs prieks.', '', '2012-07-27 10:20:46'),
(337, 'Cik daudz tauriņiem krāsu un formu,<br />\r\nTik daudz ziediem atšķirīgu smaržu,<br />\r\nTik mums visiem ir neatkārtojami vārdiņi.<br />\r\nArī Tev! Sveicu Tevi svētkos!', '', '2012-07-27 10:20:52'),
(338, 'Vārdadiena jāsaplāno: <br />\r\nDienu ? svinēt,<br />\r\nRītdien ? lāpīt,<br />\r\nVakarpusē ? gultā trāpīt.<br />\r\nTrešā dienā skaitīt naudu,<br />\r\nAtcerēties gūto baudu.<br />\r\nPārrunāt, kas cēlies, kritis,<br />\r\nDancojis, zem galda mitis....<br />\r\nBeidzot plānošanu šo - <br />\r\nApsvērt balli nākošo.', '', '2012-07-27 10:21:00'),
(339, 'Vārds katram savs mums ir,<br />\r\nKo māte, tēvs ir licis.<br />\r\nVai pašiem patīk tas,<br />\r\nVai tikai neapnicis.<br />\r\nVārds Tevi godā ceļ,<br />\r\nJa proti viņu valkāt,<br />\r\nUn tādēļ Tavējam<br />\r\nMēs varam dziesmu dziedāt.<br />\r\nGan skanīgs, viegls tas,<br />\r\nGan godā celts un slavēts,<br />\r\nLai, mūžu dzīvojot,<br />\r\nTas top vēl vairāk mīlēts!', '', '2012-07-27 10:21:07'),
(340, 'Vārdadiena vienreiz gadā,<br />\r\nNeļauj draugiem nomirt badā,<br />\r\nUzliec galdā torti, šņabi,<br />\r\nLai var palustēties labi!', '', '2012-07-27 11:20:02'),
(341, 'Ar šo es, zvērināta vārda dienas notāre apstiprinu, ka:<br />\r\n<br />\r\nLai bij` jautri, kad bij jautri<br />\r\nVārda svētkos - gan bij` feini<br />\r\nDraugi zvana, draugi raksta,<br />\r\nMīļie bučas dāsni dala...:)!<br />\r\n<br />\r\nApstirpinu, ka augstāk minētais izraksts vārds vārdā atbilsts oriģinālajam ierakstam Galvenajā svētku apsveikumu grāmatā.<br />\r\n<br />\r\n/paraksts/ /paraksts/<br />\r\n<br />\r\nIekasētās nodevas: viens smaids, daudz prieka, pilna deva jautrības šovakar!', '', '2012-07-27 11:20:29'),
(342, 'Laiks kaut ko pateica.<br />\r\nLaiks kaut ko pieteica.<br />\r\nLaiks kaut ko noliedza<br />\r\nun tālāk iet<br />\r\ngaidīdams.<br />\r\nDzirdēji?<br />\r\nTas taču bija tavs vārds.', 'Oj. Vācietis', '2012-07-27 11:20:41'),
(343, 'Veselībā, priekā tīts,<br />\r\nLai aust tev vārdadienas rīts!<br />\r\nUn galvu nenoliekt par niekiem,<br />\r\nLai domas paliek tikai priekiem!', '', '2012-07-27 11:20:49'),
(344, 'Acis platas rītā agrā, <br />\r\nNemiers manu sirdi sagrābj, <br />\r\nSkaļi saucu - Velns ar ārā! <br />\r\nVārds tavs rakstīts kalendārā!', '', '2012-07-27 11:20:58'),
(345, 'Mazs eņģelīts man priekšā teic,<br />\r\nKa tevi šodien jāapsveic!<br />\r\nLai amoriņš Tev sirdī valda,<br />\r\nNelaimes ar bultām skalda!', '', '2012-07-27 11:21:04'),
(346, 'Šodien atkal ir tā diena, <br />\r\nKad tev jāaiziet pēc viena.<br />\r\nJa tev krūtīs mājo sirds<br />\r\nApskaties, kas plauktos mirdz!', '', '2012-07-27 11:21:12'),
(347, 'Šī diena lai skaistāka par citām<br />\r\nTā tikai reizi gadā aust!<br />\r\nLai pietiek spēka katram rītam<br />\r\nLai šodien skaisti ziedi plaukst!', '', '2012-07-27 11:21:19'),
(348, 'Vismīļākais vārds ir citiem mīļvārdiem raudze,<br />\r\nKā saulespuķe tas gaismu ap sevi audzē.<br />\r\nVismīļākais vārds ir kā gredzens ar dimantu pirkstā,<br />\r\nKas sirdīs visapkārt sēj dzīvu cerību dzirksti.<br />\r\nAr mīļiem vārdiem mēs nebūsim smiltis un skaidas,<br />\r\nBet, aizdzenot nestundu, pieveiksim arī naidu.<br />\r\nAr mīļiem vārdiem - kā sveces liesmiņu sevī,<br />\r\nŠo pasauli sildām,<br />\r\nko Dievs mums ir mīlot devis.<br />\r\nEsi sumināta vārdiņa svētkos!', '', '2012-07-27 11:21:27'),
(349, 'Kalendārs ir ļoti pļāpīgs<br />\r\nZin kā katru vārdā cauc,<br />\r\nŠodien datums ļoti trāpīgs<br />\r\nTad ir tevi jāapsveic!!!!', '', '2012-07-27 11:21:34'),
(350, 'Vētraini apskauju un vēlu nežēlīgi daudz prieka un laimes Vārdiņsvētkos!', '', '2012-07-27 11:21:42'),
(351, 'Dievs lai piedod visus grēkus,<br />\r\nOrganismam jaunus spēkus<br />\r\nVairāk naudas, vairāk baudas,<br />\r\nLieliem darbiem vairāk jaudas!', '', '2012-07-27 11:21:49'),
(352, 'Tavs vārds ir tikai ziedlapiņa<br />\r\nPie lielā vārdu koka,<br />\r\nPēc savas gribas liktenis<br />\r\nKo purina un loka.<br />\r\nTavs vārds ir tikai ziedlapiņa,<br />\r\nBet vienu gan Tu vari,<br />\r\nTo gadu gaitā godā celt<br />\r\nAr visu ko vien dari.<br />\r\nTavs vārds ir tikai ziedlapiņa,<br />\r\nKas laika vējos plīvo,<br />\r\nBet lai ar uguns dvēseli<br />\r\nTas savu mūžu dzīvo!', '', '2012-07-27 11:21:55'),
(353, 'Lai Tavā vārda dienā,<br />\r\nTev prieku dāvā viss:<br />\r\nLai draugi atnes ziedus<br />\r\nUn sauli debesīs.', '', '2012-07-27 11:22:06'),
(354, 'Tev izstarot to kluso gaismu,<br />\r\nKas acīm neredzama zied,<br />\r\nAr ko var iet caur paradīzi,<br />\r\nAr ko caur pašu elli iet.', 'Ā.Elksne', '2012-07-27 11:22:17'),
(355, 'Lai ceļaspieķis<br />\r\nir Tavs vārds,<br />\r\nAr kuru dzīvē iesi.<br />\r\nUn visu,<br />\r\nkas būs ļauns un<br />\r\nslikts,<br />\r\nAr vārdu uzvarēsi.<br />\r\n<br />\r\nPie tā Tev turēties<br />\r\nun nest<br />\r\nTo savu vārdu droši,<br />\r\nLai katra diena<br />\r\nuzzied Tev<br />\r\nAr vārdu apburoši!', '', '2012-07-27 11:22:24'),
(356, 'Ļauj novēlēt Tev Vārda dienā laimi,<br />\r\nLai Tavā dienā Laimes stundu daudz.<br />\r\nJa ceļš būs grūts,lai apkārt draugu saime,<br />\r\nKas roku sniedz un Tevi tālāk sauc.', '', '2012-07-27 11:22:31'),
(357, 'Sakarā ar Vārda Dienu,<br />\r\nKatram jādzer glāzi vienu,<br />\r\nBet, ja tostu pateikt prot,<br />\r\nDarbību drīkst atkārtot!<br />\r\nApsveicu Vārda svētkos!!!:))', '', '2012-07-27 11:22:37'),
(358, 'Savs vārds ir skaisti jānes,<br />\r\nTu skaisti viņu nes!<br />\r\nKā trauku pilnu gaismas<br />\r\nun pilnu pasaules.', '', '2012-07-27 11:22:46'),
(359, 'Šī diena vieglāka par spāri,<br />\r\nLai dzīves rūpēm paceļ pāri.<br />\r\nUz mirkli kļūsti kastaņzieds,<br />\r\nJo šodien prieka varavīksnē<br />\r\nTavs vārds ar saules stariem siets!', '', '2012-07-27 11:22:51'),
(360, 'Tavs vārds ar pirmās dziesmas žūžu<br />\r\nNo bernības ir ceļam līdzi dots,<br />\r\nNo laiku laikiem un uz visu mūžu,<br />\r\nTas būs tavs lepnums,<br />\r\nCildenums un gods.', '', '2012-07-27 11:23:06'),
(361, 'Atkal vienu prieka dienu <br />\r\nKalendārs mums paziņo <br />\r\nUn šai dienā, kā arvienu, <br />\r\nGrēks nav iedzert maziņo!', '', '2012-07-27 11:23:13'),
(362, 'Lai Tev katra upīte<br />\r\nBēdas noskalo.<br />\r\nLai Tev katra puķīte<br />\r\nLaimi novēlē!', '', '2012-07-27 11:23:19'),
(363, 'Lai Laimīte ceļā vada<br />\r\nlai turība sētiņā<br />\r\nlai uzlec zelta saule<br />\r\nVārda dienas rītiņā!!!!!!', '', '2012-07-27 11:23:26'),
(364, 'Katram ir savs vārdiņš dots un vārda dienā rādīts gods.<br />\r\nArī mēs no savas saimes novēlam Tev daudz, daudz laimes.<br />\r\nBet aiz sārto rožu vāzes, pielej pilnas arī glāzes.', '', '2012-07-27 11:23:32'),
(365, 'Caur Tavu vārdu šalko jūras vilnis,<br />\r\nCaur Tavu vārdu spēlē gliemežvāks.<br />\r\nTu esi divu melodiju pilna,<br />\r\nViss Tavā pasaulē ir savādāks!', '', '2012-07-27 11:23:40');
INSERT INTO `regards` (`regard_ID`, `poem`, `author`, `created`) VALUES
(366, 'Ja zini blakus, ka ir draugs,<br />\r\nTad noskaņojums vienmēr jauks.<br />\r\nTas nepametīs Tevi vienu,<br />\r\nPat kopā svinēs vārda dienu!', '', '2012-07-27 11:23:47'),
(367, 'Katrs taisīts savā dienā <br />\r\nPalagos vai svaigā sienā, <br />\r\nKatram ir savs vārdiņš dots, <br />\r\nLai tad šodien tiek tam gods!', '', '2012-07-27 11:23:54'),
(368, 'Sveicu vārda dienā! <br />\r\nLai ik minūtei ir sekunde prieka, <br />\r\nlai ik stundai ir minūte saules, <br />\r\nlai ik dienai ir stunda laimes!', '', '2012-07-27 11:24:00'),
(369, 'Vārda diena vienreiz gadā,<br />\r\nNeļauj draugiem nomirt badā;<br />\r\nPolšam korķi vaļā rauj,<br />\r\nZaceni uz galda krauj.', '', '2012-07-27 11:24:08'),
(370, 'Tavs vārds ir kā saulespuķe,<br />\r\nKā ābols zarā, kas mirdz.<br />\r\nTavs vārds ir kā astere koša<br />\r\nPie rudens vējainās sirds.', '', '2012-07-27 11:24:14'),
(371, 'Atrodi laiku draudzībai-<br />\r\ntas ir celš uz laimi.<br />\r\nAtrodi laiku sapņiem-<br />\r\ntie atklās Tev zvaigznes.<br />\r\nAtrodi laiku mīlēt-<br />\r\ntā ir dievu privilēģija.<br />\r\nAtrodi laiku smiekliem-<br />\r\ntie ir dvēseles mūzika.', '', '2012-07-27 11:24:20'),
(372, 'Lai ir dienas, lai ir stundas<br />\r\nCerības jums sirdī aust<br />\r\nUn lai jūsu vārdadienā<br />\r\nBaltas laimes rozes zied.', '', '2012-07-27 11:24:27'),
(373, 'Žēl ka nevaram tur būt<br />\r\nKūkas ēst un šņabi sūkt <br />\r\nDatoram jāuztic ir teiciens <br />\r\nVārda dienā kvēls lai sveiciens!', '', '2012-07-27 11:24:33'),
(374, 'Kautkam šodien liela diena,<br />\r\nLaikam kādam vārda diena,<br />\r\nSkatos es kalendārā,<br />\r\nEdgaram svētki velns arārā!!!', '', '2012-07-27 11:24:41'),
(375, 'Met projām šodien rūpes,<br />\r\nKā vīns, lai dzirkstī<br />\r\nVārda dienas prieks!<br />\r\nLai dziesmas plūst<br />\r\nNo draugu lūpām<br />\r\nUn līdz pat rītam<br />\r\nNenāk miegs! ;)', '', '2012-07-27 11:24:48'),
(376, 'Katrai apsviedīgai sievai<br />\r\nJāsaplāno vārdadiena:<br />\r\nDienu - svinēt,<br />\r\nRītdien - lāpīt, <br />\r\nVakarpusē ? gultā trāpīt.<br />\r\nTrešā dienā skaitīt naudu,<br />\r\nAtcerēties gūto baudu.<br />\r\nPārrunāt kā cēlies, kriti,<br />\r\nDancoji, zem galda miti...<br />\r\nBeidzot plānošanu šo -<br />\r\nApsvērt balli nākošo.', '', '2012-07-27 11:24:56'),
(377, 'Vienreiz gadā diena tāda<br />\r\nKo par vārdenīti sauc,<br />\r\nDraugi sveic un radi sveic,<br />\r\nUzslavas tev skaistas teic!', '', '2012-07-27 11:25:04'),
(378, 'Odiņš dūc pie loga rūts.<br />\r\nĶer tik ciet un spied pie krūts,<br />\r\nArī viņš tev grib ko teikt,<br />\r\nLaikam vārdadienā sveikt.', '', '2012-07-27 11:25:12'),
(379, 'Sargā gaismu savās acīs,<br />\r\nNelaid ēnas viņai klāt,<br />\r\nRaugies jautrās sveču liesmās,<br />\r\nVairies uzpūst,<br />\r\nSatraukt,<br />\r\nRāt.<br />\r\nSargā mīlestību sirdī,<br />\r\nNepārstāj pat mīlēt to,<br />\r\nKas tev skarbus vārdus sacīs,<br />\r\nAtstās sirdi rasojot.', '', '2012-07-27 11:25:17'),
(380, 'Ko lai vārda dienā vēlam, stipram mūsu zemes dēlam?<br />\r\nP---i gaisā vienmēr staltu, autiņu ar šipiem kaltu,<br />\r\nTad vēl tautu meitu baltu, ko cits nesauktu par mauku!', '', '2012-07-27 11:25:29'),
(381, 'Lai tev vārdadienā jautri iet,<br />\r\nVajag kaut ko glāzē liet,<br />\r\nUzsaukt tostu, dzert to sausu,<br />\r\nLai tev būtu dzīvot prieks.', '', '2012-07-27 11:25:35'),
(382, 'Sveicu Tevi dienā šajā,<br />\r\nKas ir saukta vārdā Tavā,<br />\r\nKo Tev māte,tēvs ir devis,<br />\r\nTas ir skaists un tik priekš Tevis!!! <br />\r\nApsveicu! :*:)', '', '2012-07-27 11:25:42'),
(383, 'Vārda diena laba lieta, vajadzīga tikai vieta -<br />\r\nTur, kur draugiem pulcēties,<br />\r\ngardā kūkā iekosties.<br />\r\nVarbūt šampanieti dos, ar ko<br />\r\nvārdu spodrināt!', '', '2012-07-27 11:25:52'),
(384, 'Lai vēlēšanās piepildās ikviena,<br />\r\nprieks, laime, mīla ceļā kvēl,<br />\r\nto šodien, tavā vārda dienā,<br />\r\nno visas sirds tev draugi vēl.', '', '2012-07-27 11:25:58'),
(385, 'Sirsnīgs sveiciens vairs nav modē <br />\r\nPārdevu to krāmu bodē <br />\r\nTagad jauna mode valda- <br />\r\nJums no manis buča salda!', '', '2012-07-27 11:26:06'),
(386, 'Noplēšamā kalendāra 124. lapiņā ir rakstīts kāds vārds. To atļauts nosaukt visiem, bet nēsāt drīkst tikai īpaši izvēlētie...', '', '2012-07-27 11:26:13'),
(387, 'Ja vārda dieniņas nebūtu Tev,<br />\r\nTad kā gan Tu vārdiņu zinātu sev?<br />\r\nBet tā kā tas ir un laimi tev nes,<br />\r\nDaudz prieka un saules Tev novēlu es.', '', '2012-07-27 11:26:19'),
(388, 'Vārda dienu svinēt prieks,<br />\r\nJa ir galdā trīsgraudnieks!<br />\r\nRosolus un raušus nost,<br />\r\nVajag tikai kost un kost!!!!', '', '2012-07-27 11:26:25'),
(389, 'Lai ik dienas, lai ik stundas,<br />\r\nLaime, prieks un cerības Tev sirdī aust,<br />\r\nUn, lai Tavā vārda dienā,<br />\r\nBaltas laimes rozes plaukst!', '', '2012-07-27 11:26:31'),
(390, 'Jau saulīte ikdienas pastaigas sāk <br />\r\nUn putni debesīs zīmēt pasauli nāk <br />\r\nŠodien tik mīlīgi vasara pasauli sveic <br />\r\nJo daiļākais vārdiņš ir šūpulī celts!', '', '2012-07-27 11:26:38'),
(391, 'Šķība nāca vārda diena,<br />\r\nKā no vēja saliekusies.<br />\r\nNav no vēja saliekusies,<br />\r\nBet gan šņabi sadzērusies.', '', '2012-07-27 11:26:52'),
(392, 'Pašķīr kalendāra lapiņ<br />\r\nBrīnos, laiks tik makten skrien...<br />\r\nAk tu mīļais debess papiņ,<br />\r\nTev tak šodien vārda dien.', '', '2012-07-27 11:26:59'),
(393, 'Katram mums savs vārdiņš dots,<br />\r\nVai viņš siventiņš vai ods.<br />\r\nKatram vārdiņam ir dienas,<br />\r\nKad viņš karājas pie sienas.', '', '2012-07-27 11:27:06'),
(394, 'Brīžiem liekas - dzīve ir bez vainas, <br />\r\nBrīžiem šķiet , ka tikai krāpties prot. <br />\r\nStarp šiem dzirnakmeņiem allaž maļas vārds), <br />\r\nKā nu katru reizi spēj un prot.', '', '2012-07-27 11:27:13'),
(395, 'Ar atraktīvu palēcienu, <br />\r\nElegantu pagriezienu, <br />\r\nVārda dienu nosvinēt, <br />\r\nBaigi labi ietusēt:)', '', '2012-07-27 11:27:20'),
(396, 'Lai sit bungas, pūš un spēlē,<br />\r\nUrā sauc un laimes vēlē,<br />\r\nVisa pasaule jau zin,<br />\r\nAndrejam vārda dienu svin.', '', '2012-07-27 11:27:27'),
(397, 'Tavs vārds ir kā saulespuķe,<br />\r\nKā ābols zarā, kas mirdz,<br />\r\nTavs vārds ir kā astere koša<br />\r\nPie rudens vējainās sirds!!!', '', '2012-07-27 11:27:34'),
(398, 'Vārda svētki - kāda skaista diena, <br />\r\nAllaž visiem sauli atnest spēj. <br />\r\nUzziedējis saltu vēju vidū, <br />\r\nVasaru tavs vārds mums sirdī sēj.', '', '2012-07-27 11:27:40'),
(399, 'Ir vārdu pasaulē tik daudz,<br />\r\nBet vienu visu mūžu nesam,<br />\r\nUn labi just, kad paguruši esam -<br />\r\nKāds Tevi atceras, kāds vārdā tevi sauc.', '', '2012-07-27 11:27:48'),
(400, 'Ja vārda dienas nebūtu Tev, <br />\r\nKā gan Tu vārdu zinātu sev? ;)', '', '2012-07-27 11:28:02'),
(401, 'Kad uz visas zemeslodes<br />\r\nTavu vārda dienu svin,<br />\r\nTad lai slimo tas ar gripu,<br />\r\nKas šo dienu nepiemin.', '', '2012-07-27 11:28:14'),
(402, 'No pirmām dzīves dienām<br />\r\nKad šūpulis ir kārts,<br />\r\nTiek cilvēkam ikvienam<br />\r\nUz mūžu iedots vārds!', '', '2012-07-27 11:28:24'),
(403, 'Tavs vārds ir smuks,<br />\r\nTavs vārds ir jauks,<br />\r\nMan prieks par to, <br />\r\nKa tevi (vārds) sauc!', '', '2012-07-27 11:28:34'),
(404, 'Dus Rīga paģirainā snaudā,<br />\r\nNav tautas laime tikai naudā!<br />\r\nEs novēlu Tev spirgtam būt,<br />\r\nUn šodien viesus ciemos lūgt!', '', '2012-07-27 11:28:45'),
(405, 'Augstu augstu debesīs<br />\r\nDivi putni lidinās<br />\r\nPacel galvu paskaties<br />\r\nTie no manis sveicieni!!!!!!!!!!!', '', '2012-07-27 11:28:59'),
(406, 'Kāds eņģelīts man priekšā teic,<br />\r\nKa Tevi šodien jāapsveic.<br />\r\nLai Amoriņš Tev sirdī valda,<br />\r\nNeveiksmes ar bultām skalda!', '', '2012-07-27 11:29:07'),
(407, 'Vārda dienā šņabi dzert,<br />\r\nMeitas abām rokām tvert,<br />\r\nDancot, dziedāt, līksmoties,<br />\r\nUn ar pohām pamosties...', '', '2012-07-27 11:29:21'),
(408, 'Katrs putniņš, kas uz zara,<br />\r\nLai tev vārdadienā zvana!', '', '2012-07-27 11:29:31'),
(409, 'Ko vēlēt Tev?<br />\r\nLai prieks ir man,<br />\r\nKad vārds tavs skan.<br />\r\nKo vēlēt sev?<br />\r\nLai prieks ir tev,<br />\r\nKad vārds mans skan.', '', '2012-07-27 11:30:57'),
(410, 'Noplēš kalendāre lapiņ,<br />\r\nBrīnes - ak tu mīļe debes papiņ`<br />\r\nLaiks tik mudig skrien,<br />\r\nTev tak šodien vārdadien !', '', '2012-07-27 11:31:04'),
(411, 'Lai tev vārdadieniņā<br />\r\nĶīselis peld piniņā,<br />\r\nĶiļķēni no katla sauc,<br />\r\nApsveicēji apkārt trauc!', '', '2012-07-27 11:31:11'),
(412, 'Lai visus ziedus<br />\r\nJums saule veltī.<br />\r\nLai sirdi un dvēsli<br />\r\nAr laimi zeltī!', '', '2012-07-27 11:31:21'),
(413, 'Vārda dienu viegli svinēt,<br />\r\nOtrā dienā grūti minēt,<br />\r\nKāpēc šausmīgi sāp galva...<br />\r\nUn es pamostos zem galda!!! :)', '', '2012-07-27 11:31:27'),
(414, 'Lai šodien šampanietis līst,<br />\r\nLai vāzē ziedi nenovīst.<br />\r\nLai draugi kuplā pulkā nāk<br />\r\nUn"sveiks,lai dzīvo"dziedād sāk!<br />\r\nSveiciens dzimšanas dienā!;))))', '', '2012-07-27 11:31:33'),
(415, 'Šodien liela diena tev,<br />\r\nDaudz daudz prieka gūsti sev,<br />\r\nDraugu sveikts un apčabots -<br />\r\nLai tev vārdam šodien gods!', '', '2012-07-27 11:31:40'),
(416, 'Tik labai būt kā miegam<br />\r\napsegt,sildīt un mierināt.<br />\r\nUn dienas ceļos rūpju izdangātos<br />\r\nar baltu mieru klāt.', 'Ā.Elksne', '2012-07-27 11:31:53'),
(417, 'Tevi sveicam dienā šajā<br />\r\nNosaukta, kas vārdā Tavā,<br />\r\nKuru, neatkarīgi no Tevis,<br />\r\nTēvs un māte Tev ir devis!', '', '2012-07-27 11:32:00'),
(418, 'Ir katrs taisīts savā dienā,<br />\r\nKāds baltos palagos, cits svaigā sienā,<br />\r\nUn katram ira vārdiņš dots,<br />\r\nTas jāapsveic, lai ir tam gods!', '', '2012-07-27 11:32:10'),
(419, 'Shii diena vieglaaka par spaari,<br />\r\nLai dziives ruupeem pacelj paari!<br />\r\nUz mirkli kljuusti kastanjzieds,<br />\r\nJo shodien prieka varaviiksnee<br />\r\nTavs vaards ar saules stariem siets!', '', '2012-07-27 11:32:17'),
(420, 'Tu vari izgaismoties,<br />\r\njopavasaris ir Tevī<br />\r\nUn izgaismot kādu citu,<br />\r\nKam dvēselē rūs saules zvani,<br />\r\njo<br />\r\nkādam ir vajadzīgs<br />\r\nplecs pie kura var<br />\r\nizraudāties<br />\r\nvaigs kam savējo priekos<br />\r\nun bēdās glaust klāt!<br />\r\nSveiciens vārda dieniņā un lai Tev izdodas!', '', '2012-07-27 11:32:27'),
(421, 'Šī diena vieglāka par spāri,<br />\r\nLai dzīves rūpēm paceļ pāri.<br />\r\nUz mirkli kļūsti kastaņzieds,<br />\r\nJo šodien prieka varavīksnē<br />\r\nTavs vārds ar saules stariem siets!', '', '2012-07-27 11:32:38'),
(422, 'Ne miglā tīts,<br />\r\nBet priekā vīts,<br />\r\nLai autaust<br />\r\nTavs vārdadienas rīts.', '', '2012-07-27 11:32:53'),
(423, 'Sen jau es šo dienu gaidu<br />\r\nLai ar labi platu smaidu<br />\r\nTeiktu vienā paņēmienā:<br />\r\n"Sveicu tevi vārda dienā!"', '', '2012-07-27 11:33:02'),
(424, 'Zilus neaizmirstulīšu ziedus<br />\r\nUpes viļņos kaisu es,<br />\r\nLai tie vārda dienā Tev<br />\r\nNemirstīgas laimes nes!', '', '2012-07-27 11:35:51'),
(425, 'Vārda diena vienreiz gadā,<br />\r\nNeļauj radiem nomirt badā.<br />\r\nLiec uz galda siļķes asti,<br />\r\nUn zem galda šņaba kasti!:)', '', '2012-07-27 11:35:58'),
(426, 'Sveicu Tevi dienā šajā,<br />\r\nKura saukta vārdā Tavā,<br />\r\nKuru neatkarīgi no Tevis<br />\r\nTēvs un māte ir Tev devis', '', '2012-07-27 11:36:04'),
(427, 'Cik daudz tauriņiem krāsu un formu,<br />\r\nTik daudz ziediem atšķirīgu smaržu,<br />\r\nTik mums visiem ir neatkārtojami vārdiņi.<br />\r\nArī Tev! Sveicu Tevi svētkos!', '', '2012-07-27 11:36:11'),
(428, 'Katrs putniņš, kas uz zara,<br />\r\nLai tev vārda dienā zvana!', '', '2012-07-27 11:36:18'),
(429, 'Kalendārs ir ļoti viltīgs,<br />\r\nTeic, kā katru vārdā sauc!<br />\r\nŠodien datums ļoti zīmīgs,<br />\r\nJo pie jums tas ciemos sauc!', '', '2012-07-27 11:36:25'),
(430, 'Novēlu tev dzīvot forši,<br />\r\nAr mersi braukt un poršu,<br />\r\nVārdadienā nepiedzerties,<br />\r\nVisus izklaidēt un ģērbties forši', '', '2012-07-27 11:36:33'),
(431, 'Rudenī kad lapas virmo <br />\r\nTevi vārda dienā sveic. <br />\r\nPacelsim mēs glāzi pirmo <br />\r\nPar Tevi, kas tik daudz ko veic.', '', '2012-07-27 11:36:40'),
(432, 'Ja es būtu putniņš mazs,<br />\r\nKas uz zara šūpojas.<br />\r\nVārda dienā Tev par prieku<br />\r\nDāvātu es treknu slieku.', '', '2012-07-27 11:36:48'),
(433, 'Vai Tu zināji,ka vārdiņš Tavs<br />\r\nKalendārā skaistākais?<br />\r\nNebūtu tur vārda tava,<br />\r\nZustu kalendāra slava!', '', '2012-07-27 11:36:54'),
(434, 'nē - tas nav grēks<br />\r\nja mīli<br />\r\nja mīli pasauli un sevi<br />\r\nnē - tas nav grēks<br />\r\nja esi -<br />\r\nšai pasaulē un dienā <br />\r\nlaimīgs esi<br />\r\nnē - tas nav grēks<br />\r\nja dāvā savu sirdi -<br />\r\ntu cilvēkiem un ziediem dāvā silti<br />\r\nnē - tas nav grēks<br />\r\nja mīli -<br />\r\nik ziedā Dieva elpu jūti<br />\r\nnē - tas nav grēks<br />\r\ntu esi <br />\r\nšajā pasaulē tu cilvēks esi...', 'Inese Tora', '2012-07-27 11:37:09'),
(435, 'Veselībā, priekā tīts,<br />\r\nLai aust tev vārdadienas rīts!<br />\r\nUn galvu nenoliekt par niekiem,<br />\r\nLai domas paliek tikai priekiem', '', '2012-07-27 11:37:17'),
(436, 'Vārdadiena vienreiz gadā,<br />\r\nNeļauj visiem nomirt badā,<br />\r\nLiec uz galda siļķes asti,<br />\r\nUz zem galda alus kasti.', '', '2012-07-27 11:37:23'),
(437, 'Ja dzīve ir grūta<br />\r\nUz visu ir naids,<br />\r\nTad labākais līdzeklis<br />\r\nVisam ir smaids!', '', '2012-07-27 11:37:30'),
(438, 'Dzīve ir sapnis ? <br />\r\nBurvīgi skaists.<br />\r\nDzīve ir ilgas,<br />\r\nKas kvēlo un gaist!', '', '2012-07-27 11:37:37'),
(439, 'Saules stariņš<br />\r\ntavā sejā,<br />\r\nšajā dienā,<br />\r\nšajā vietā,<br />\r\nlai Tev vārdiņš<br />\r\nlaimi nes.', '', '2012-07-27 11:37:47'),
(440, 'Laiks kaut ko pateica.<br />\r\nLaiks kaut ko pieteica.<br />\r\nLaiks kaut ko noliedza<br />\r\nun tālāk iet gaidīdams.<br />\r\nDzirdēji?<br />\r\nTas taču bija tavs vārds.', '', '2012-07-27 11:37:53'),
(441, 'Pāri kalniem, pāri lejām,<br />\r\nTriecas doma, kurā teikts,<br />\r\nTam kam šodien vārda diena,<br />\r\nTiek no manis mīļi sveikts.', '', '2012-07-27 11:38:03'),
(442, 'Sveicam tevi dienā šajā,<br />\r\nKura saukta tavā vārdā,<br />\r\nKuru neatkarīgi no tevis<br />\r\nTēvs un māte tev ir devis.', '', '2012-07-27 11:38:09'),
(443, 'Ja Tu mīli kādu puķi, <br />\r\nKas atrodas uz zvaigznes, <br />\r\nTad ir tik jauki naktī raudzīties debesīs.<br />\r\nVisas zvaigznes škiet ziedam!', '', '2012-07-27 11:38:17'),
(444, 'Vai sit bungas, pūt vai spēlē,<br />\r\nTorti cep vai maizes smērē;<br />\r\nTik un tā jau visi zin - <br />\r\nRita vardadienu svin!', '', '2012-07-27 11:38:23'),
(445, 'Vēlu prieku, veselību,<br />\r\nVienu uti kažokā.<br />\r\nJa Tu mani aizmirsīsi,<br />\r\nLai Tev iekož dibenā.', '', '2012-07-27 11:38:35'),
(446, 'Ja zini blakus, ka ir draugs,<br />\r\nTad noskaņojums vienmēr jauks.<br />\r\nTas nepametīs Tevi vienu,<br />\r\nPat kopā svinēs vārda dienu!', '', '2012-07-27 11:38:43'),
(447, 'Mūsu svētkos vēlu tev<br />\r\nTo, ko vēlējos es sev:<br />\r\nPrieku, naudu, puķu vāzi,<br />\r\nPudeli un lielu glāzi......', '', '2012-07-27 11:38:50'),
(448, 'Kad uz visas Zemeslodes<br />\r\nTavu Vārdadienu svin,<br />\r\nTad lai slimo tas ar gripu,<br />\r\nKas šo dienu nepiemin!', '', '2012-07-27 11:38:57'),
(449, 'Lai Tev šodien labi iet<br />\r\nUn lai skumjas nemāk diet!<br />\r\nJa Tev rūpes vaigu māc,<br />\r\nNeaptumšojas lai prāts...', '', '2012-07-27 11:39:04'),
(450, 'Es gaidīju Mārtiņdienu<br />\r\nTā ir lielu svētku diena:<br />\r\nMīksts plācenis muldiņā,<br />\r\nZosu gaļa bļodiņā.<br />\r\n<br />\r\nPriecīgu Mārtiņdienas vakaru!', '', '2012-07-27 11:39:13'),
(451, 'Acis platas rītā agrā.<br />\r\nLecu es no gultas ārā,<br />\r\nSkaļi saucu-velns ar ārā!<br />\r\nVārds tavs rakstīts kalendārā.', '', '2012-07-27 11:39:20'),
(452, 'Šis dieniņš labo vārdu varā..<br />\r\nŠim dieniņam plats smaidiņš sejā..<br />\r\nŠai dienā krāsaini taureņi skrien trakā vēderdejā..<br />\r\nKāpēc?!<br />\r\nTāpēc, ka šodienai Tavs vārdiņš dots..<br />\r\nMīļsveiciens vārdiņdienu svinot..', '', '2012-07-27 11:39:28'),
(453, 'Kā ābols zarā spulgo <br />\r\nUn aicina pie sevis ciemos steigt <br />\r\nUn...Es jau nāku <br />\r\nEs viens pulkā <br />\r\nŠī jaukā vārda īpašnieci sveikt!', '', '2012-07-27 11:39:38'),
(454, 'Vārda dienā neķer mušas,<br />\r\nIedzer vīnu, saņem bučas,<br />\r\nUn lai sirdij jautrāk kļūtu<br />\r\nApēd gabaliņu kūkas!', '', '2012-07-27 11:39:46'),
(455, 'Žēl ka nevar klātu būt<br />\r\nKūkas štopēt tā ka jūt<br />\r\nPastam jāuztic nu ir teiciens<br />\r\nVārda dienā kvēls Tev sveiciens!', '', '2012-07-27 11:39:53'),
(456, 'Lai tavā vārdadienā<br />\r\nTev prieku dāvā viss,<br />\r\nLai draugi pasniedz ziedus<br />\r\nUn sauli debesīs!', '', '2012-07-27 11:40:01'),
(457, 'Cik daudz tauriņiem krāsu un formu, <br />\r\nTik daudz ziediem atšķirīgu smaržu, <br />\r\nTik mums visiem ir neatkārtojami vārdiņi!', '', '2012-07-27 11:40:10'),
(458, 'Vārda dienā neķer mušas,<br />\r\nIedzer vīnu, saņem bučas,<br />\r\nUn lai sirdij jautrāk kļūtu<br />\r\nApēd gabaliņu kūkas!<br />\r\nsveiciens tavam otram varinam!!!!', '', '2012-07-27 11:40:17'),
(459, 'Vai sit bungas, pūt vai spēlē,<br />\r\nTorti cep vai maizes smērē;<br />\r\nTik un tā jau visi zin -<br />\r\nRita vardadienu svin!', '', '2012-07-27 11:40:27'),
(460, 'Lai Tavā vārda dienā,<br />\r\nTev prieku dāvā viss:<br />\r\nLai draugi atnes ziedus<br />\r\nUn sauli debesīs.', '', '2012-07-27 11:40:36'),
(461, 'Aiz augstā kalna saule spīd.<br />\r\nŠis kalnu varenums to neļauj saskatīt.<br />\r\nTu vari atkāpties, lai saules staru ķertu,<br />\r\nBet iekarotā virsotnē Tev saule spožāk spīd<br />\r\nUn visa pasaule tad ir pie kājām tavām.', '', '2012-07-27 11:40:43'),
(462, 'Vien abi kopā<br />\r\nJūs esat debesis,<br />\r\nVien abi kopā <br />\r\njūs esat strauts,<br />\r\nkam kristāla lāses<br />\r\nno pazemes dziļumiem <br />\r\nlīdz jūrai vien divatā<br />\r\naizšūpot ļauts.<br />\r\nVien abi kopā<br />\r\nJūs esat patvērums <br />\r\nviens otram,<br />\r\nkad dvēseles nestunda klāt-<br />\r\nAr vīrieša spēku<br />\r\nun sievietes maigumu<br />\r\nNav baiļu pret bargāko negaisu stāt.', '', '2012-07-27 13:25:51'),
(463, 'Būs brīnumi, ja vien tiem noticēsim.<br />\r\nBūs laime tad, ja pratīsim to dot.<br />\r\nBūs saskaņa, ja vien to radīt spēsim,<br />\r\nVien dodot citiem, varam laimi gūt.', '', '2012-07-27 13:26:06'),
(464, 'Lai jums labi, lai jums ļoti labi<br />\r\nLai jums brīnišķīgi iet<br />\r\nLai jums visi labie putni<br />\r\nKatru rītu logā dzied<br />\r\nLai jums skaistas debeszilas,<br />\r\nKošas, jaukas puķes zied<br />\r\nUn lai spožā siltā saule<br />\r\nNekad dzīvē nenoriet<br />\r\nLai jūs savas dzīves grožus<br />\r\nVarat cieši turēt ciet<br />\r\nLai jums labi, ļoti labi<br />\r\nLai jums brīnišķīgi iet!', '', '2012-07-27 13:26:16'),
(465, 'Kā balta puķe bērns ir uzziedējis<br />\r\nLai kādreiz dzīvē spētu tālu iet.<br />\r\nNo nedienām, no salta vēja <br />\r\nŠo mazo dvēselīti sargājiet', '', '2012-07-27 13:26:26'),
(466, 'Šodien Jūsu kāzu dienā<br />\r\nSveicam Jūs no sirds!<br />\r\nLai Jums vienmēr tā kā šodien<br />\r\nAcis laimē mirdz.<br />\r\nGaiša lai Jums dzīves taka,<br />\r\ndzīves ceļš lai jauks.<br />\r\nVienmēr cieši ejiet blakus<br />\r\nun viens otram esiet draugs!', '', '2012-07-27 13:26:34'),
(467, 'Nu jūs ieburti viens otra gredzenā -<br />\r\nDzīves zelta gredzenā slēgti,<br />\r\nNu Jums kopīgas dienas un nedienas,<br />\r\nPusnakts tumsa un saules lēkti.<br />\r\n-----------------------------------------<br />\r\n<br />\r\nUn Tavs ceļš būs manējais,<br />\r\nTava māja un Tavs liktenis<br />\r\nMans.<br />\r\nKur iesi Tu, tur iešu es,<br />\r\nTo šodien solos<br />\r\nLaicīgi un mūžīgi.<br />\r\n--------------------------<br />\r\n<br />\r\nSOLĪJUMS<br />\r\n<br />\r\nEs Tev izdziedāšu zelta rozi,<br />\r\nKad aiz loga vēlīns rudens līs,<br />\r\nIzdziedāšu žuburainu osi,<br />\r\nKura, zaros zvaigzne ligzdu vīs.<br />\r\n<br />\r\nEs Tev izdziedāšu lakstīgalu,<br />\r\nJāņu nakti, rasu, papardes,<br />\r\nIzdziedāšu baltu puķu salu, -<br />\r\nUz tās salas tikai Tu un Es!<br />\r\n<br />\r\nEs Tev izdziedāšu mīlestību:<br />\r\nKādu vēlies, kāda labāk tīk ?<br />\r\nĪsu, žilbīgu kā zibens zibu,<br />\r\nTādu, kas liek visu mūžu tvīkt!<br />\r\n<br />\r\nEs Tev izdziedāšu savas rokas,<br />\r\nSavas lūpas, kas Tev pieri skar,<br />\r\nIzdziedāšu itin visas mokas,<br />\r\nKas, mūs tuvināt vai atgrūst var.<br />\r\n<br />\r\nEs Tev izdziedāšu laimes dvašu,<br />\r\nTavam spītam savu jaukšu klāt,<br />\r\nTikai, laikam mīļais, Tevi pašu<br />\r\nNespēšu nekad es izdziedāt.', 'Ārija Elksne', '2012-07-27 13:27:06'),
(468, 'Paveikt nepaveicamo,<br />\r\nSaglabāt neiespējamo,<br />\r\nPar spīti ikdienišķībai<br />\r\nUzplaucēt brīnumu dzīvē,<br />\r\nLaimīgiem būt!', '', '2012-07-27 13:27:15'),
(469, 'Piedod, māt, mūsu steigu un nevaļu, piedod mūsu neratnības un neizdarītos darbus -kopš bērnības visā dzīves garumā, piedod, ka pārāk reti esam tev teikuši pateicības un mīļos vārdus! Mēs tomēr katrs agri vai vēlu saprotam, ko nozīmē mātes sirds, un tu vienmēr mūsu atmiņās spulgosi gaiša, skaidra un neaizmirstama - tu -karsta asins lāse gadu baltumā.', '', '2012-07-27 13:27:23'),
(470, 'Liec, Laimīte, baltu ziedu<br />\r\nMazajā rociņā,<br />\r\nLai ir balta tā dzīvīte<br />\r\nKurā būs jādzīvo!', '', '2012-07-27 13:27:31'),
(471, 'Nav nekā lielāka, nav nekā svētāka,<br />\r\nKad kopā sakusušas <br />\r\ndebesis un zeme zied.<br />\r\nUn tur pa vidu - jūsu mīlestība iet.<br />\r\nJūs baltu dvēseli zem kājām <br />\r\nviņai paklājiet.', 'O.Vācietis', '2012-07-27 13:27:42'),
(472, 'To mīlas uguntiņu, kas jums sirdī krita<br />\r\nNo visiem dzīves vējiem sargājiet,<br />\r\nNekad lai nedziest tā, lai neiedegas cita,<br />\r\nJa ceļš reiz sākts, tad kopā staigājiet!', '', '2012-07-27 13:27:53'),
(473, 'Lai jūs saprot un mīl uz zemes un sargā no debesīm !', '', '2012-07-27 13:28:05'),
(474, 'Manas trīs laimītes<br />\r\nvienmēr satiekas ceļā,<br />\r\nes sastopu viņas pie akas,<br />\r\nkad likteni smeļu.<br />\r\nun saka pagātnes laime,<br />\r\nsevī vērdamās klusi:<br />\r\n- Zinu, ka iespēju panest it visu,<br />\r\nko pārcietusi.<br />\r\n- Bet es zināšu visu, kas būs,-<br />\r\nbilst nākotnes laime<br />\r\nnepacietīgi līksmā,-<br />\r\ntas ir kā steigties uz pavasari,<br />\r\nkad vari cerēt un drīkstēt.<br />\r\n- Es arī zinu visu, kas bija,<br />\r\nun man ir bailes,<br />\r\nkad nākotne zirgus jūdz,-<br />\r\nteic tagadnes laime un bijīgi<br />\r\npar saviem mīļiem lūdz.', 'Lija Brīdaka', '2012-07-27 13:28:24'),
(475, 'Tiem gariem gadiem,<br />\r\nKo par mūžu sauc,<br />\r\nTu lemta man,<br />\r\nUn tas ir ļoti daudz.<br />\r\nUn citu Tavā vietā neatrast<br />\r\nUn nevajag-<br />\r\ntā laika ir par maz,<br />\r\nko mums zem saules <br />\r\nabiem dzīvot ļauts<br />\r\nTie īsie gadi:<br />\r\nKo par mūžu sauc.', 'A.Purvs', '2012-07-27 13:28:39'),
(476, 'Ir saule, debesis,<br />\r\nUn maize abrā rūgst.<br />\r\nUn bērni dzimst-<br />\r\nnekā jau nepietrūkst,<br />\r\nbet skaidri jūti,<br />\r\npirmais sniegs, kad snieg,<br />\r\nka nepietrūkst,<br />\r\nbet arī nepietiek.<br />\r\nJo tas, kas dabūts,<br />\r\nit nekur nav liekams,<br />\r\nun tas, kas liekams,<br />\r\nir tik nepietiekams.<br />\r\nUn tad, kad zvaigzne krīt,<br />\r\ntā it kā iekož:<br />\r\nviss nepaliekošs ir<br />\r\nun nepietiekams.', 'I.Ziedonis', '2012-07-27 13:28:53'),
(477, 'Kaut reizi dzīvē, lai tavs sapnis,<br />\r\nKļūst par īstenību!<br />\r\nKaut reizi dzīvē, lai tava viskvēlākā<br />\r\nVēlēšanās piepildās!<br />\r\nKaut reizi dzīvē, lai tu patiešām<br />\r\nBūtu tā pa īstam laimīga<br />\r\nUn tomēr ir kas tāds, kam jāpavada<br />\r\nTevi visi tavu dzīvi...<br />\r\nTā ir ticība!<br />\r\nTicība sev un saviem spēkiem...<br />\r\nTā ir cerība!<br />\r\nCerība, ka viss izdosies...<br />\r\nTas ir spēks!<br />\r\nSpēks, kas uzveic it visu...<br />\r\nBet pāri visam ? tā ir mīlestība!<br />\r\nMīlestība, kas nekad nezudīs...', '', '2012-07-27 13:29:02'),
(478, 'Nāc ar mani zemi ar<br />\r\nun nāc ar mani maizi ēst,<br />\r\nun puķes sēt,<br />\r\nun bērnus gaidīt.<br />\r\nNāc ar mani bēdu noburt,<br />\r\nkopā mosties, kopā nogurt.<br />\r\nNāc ar mani.<br />\r\nEs ar Tevi.', '', '2012-07-27 13:29:15'),
(479, 'Balta, balta šodien diena,<br />\r\nLaikam divas saules spīd.<br />\r\nJūsu mīlas pilnās sirdīs<br />\r\nSilts un zeltains mirdzums krīt.<br />\r\nSaudzējat šo gaismu abi,<br />\r\nDraugi patiesi to vēl,<br />\r\nLai tā, arī vētrai dunot,<br />\r\nSirdī gaiši, gaiši kvēl.<br />\r\nTad jūs nenobiedēs vecums,<br />\r\nTad ik diena prieku dos,<br />\r\nKāpjot augstā gadu kalnā<br />\r\nMīla ceļu apgaismos.<br />\r\nTas nekas, ka daudz vēl snigs,<br />\r\nTas nekas, ka soļi sniegos stigs,<br />\r\nSniegā spēka pietiek tam,<br />\r\nKas tic saules stiprumam.', '', '2012-07-27 13:29:25'),
(480, 'Mirkli padomājiet, pirms kausus pilnus lejat.<br />\r\nMirkli padomājiet, pirms tālāk ejat.<br />\r\nTālāk - kopējas takas, kopēja laiva un irkļi,<br />\r\nTālāk - kopēja bēda un kopēji laimes mirkļi.', 'A. Vejāns', '2012-07-27 13:29:40'),
(481, 'Dzīvē vieglu ceļu nav, nedz taku.<br />\r\nKatram laimes brīdim ir savs rūgtums klāt.<br />\r\nVēja laikā ejiet cieši blakus,<br />\r\nLai līdz galam varat nostaigāt!<br />\r\n<br />\r\nNāc ar mani zemi art,<br />\r\nNāc ar mani maizi ēst,<br />\r\nPuķes sēt un bērnus gaidīt.<br />\r\nNāc ar mani bēdu noburt,<br />\r\nKopā mosties, kopā nogurt.<br />\r\nNāc ar mani - es ar tevi.', 'M. Kuliņa', '2012-07-27 13:29:56'),
(482, 'Esi ar manām plaukstām,<br />\r\nEs ar Tavējām būšu.<br />\r\nPasildi manus pirkstus.<br />\r\nMirklīti.<br />\r\nStundiņu.<br />\r\nMūžu.', 'M.Laukmane', '2012-07-27 13:30:12'),
(483, 'Lai aizskrien jaunība, kā kāzinieku zvani,<br />\r\nCik baltas ābeles un egles sarmā zied.<br />\r\nVai skaisti bija tikai pavasari mani?<br />\r\nNē, zelta zīle arī sniegputenī dzied!', '', '2012-07-27 13:30:20'),
(484, 'Kāzās: Paši esiet vainīgi,-<br />\r\nDzīvojiet nu laimīgi!', '', '2012-07-27 13:30:28'),
(485, 'Un atkal laimes vēl. Tas laiks ir klāt.<br />\r\nLai dzīvē jaunā viņiem labi sokas.<br />\r\nNu tavi bērni atstāj ligzdu, māt,<br />\r\nUn atstāj tavas piegurušās rokas.<br />\r\nUn tad būs mazbērni... Un svarīgi nav tas,<br />\r\nVai viņi piedzimst decembrī vai maijā,<br />\r\nBet katrs ķipars sākumā ir mazs<br />\r\nUn lūdz lai tavas rokas viņu aijā.<br />\r\nUn viņš grib ēst un dzert, un viņš ir mazs,<br />\r\nViņš nāk un ķeras tavu svārku krokās,<br />\r\nLūdz: - vecmāt, nes! - un viņam tas nekas, <br />\r\nKa dažreiz nogurušas tavas rokas.<br />\r\nTā bērnu mūžs ir māšu rokām lemts<br />\r\nNo baltiem autiņiem līdz kāzu štātei,<br />\r\nUn tālāk vēl. Tik žēl, ka to, kas ņemts,<br />\r\nMēs visu nevaram vairs atdot mātei.<br />\r\nBij arī viņai diena, sauca: - rūgts!<br />\r\nBij arī viņai rožu ziedi saldi,<br />\r\nViss radu raksts uz kāzām tika lūgts,<br />\r\nKaut jau aiz kalniem viņas kāzu galdi.<br />\r\nKad šodien viesi atkal durvis ver,<br />\r\nRads blakus radam sēž, un brāļi māsām,<br />\r\nMums tomēr viena glāze jāiedzer<br />\r\nPar mātes, katram savas mātes kāzām.', 'I.Ziedonis', '2012-07-27 13:30:46'),
(486, 'Bij divas sirdis reiz...<br />\r\nUn līksmē tās<br />\r\nkā divas dziesmas pēkšņi satikās...<br />\r\nViens otrā toreiz pavērāmies klusi<br />\r\nun sapratām, ka laime atnākusi.<br />\r\nBij divas takas reiz...<br />\r\nBet tagad tās<br />\r\nies kopā gadu tālēs saulainās.', 'L.Vāczemnieks', '2012-07-27 13:31:03'),
(487, 'Aizrit mani zelta rati<br />\r\nPašā Laimas pagalmā:<br />\r\nBalts galdiņis, bārkstu sega,<br />\r\nPati Laima krēslu cēla!', 'A.Brigadere', '2012-07-27 13:31:16'),
(488, 'Tu! Tā esi tu, kas plūsti<br />\r\nKā rāma gaisma pāri.<br />\r\nTu - stars, kas nesalūsti,<br />\r\nKaut vētra posta āri.<br />\r\nMāt! Tikai tevī, tevī<br />\r\nMīt mierinājums īsts,<br />\r\nKad sajūk pašam sevī,<br />\r\nKas mīlējams, ko nīst...', 'A.Pormale', '2012-07-27 13:31:29'),
(489, 'No bērnības līdz šai dienai, caur dārziem ilgi iets - no ziedošas ābeles vienas, pie deniņiem pieglaudies zieds....', '', '2012-07-27 13:31:44'),
(490, 'Vairs skaistāku brīžu dzīvē nav,<br />\r\nKad laime, ko gribēji, tagad ir tava,<br />\r\nKad divi ceļi par vienu var kļūt,<br />\r\nUn divas sirdis vienotas būt.', '', '2012-07-27 13:31:53'),
(491, 'Katrai modernai sievietei dzīvē vajag četrus zvērus:<br />\r\n1.Lauvu gultā<br />\r\n2.Lapsu ap kaklu<br />\r\n3.Jaguāru garāžā <br />\r\n4.Un vienu ēzeli,kas par to visu maksā.', '', '2012-07-27 13:32:02'),
(492, 'Sirds nejautā, kāds būs tai guvums,<br />\r\nkas gaida mūs, kas notiks rīt,<br />\r\nLai dienas trauc. Mums paliek tuvums,<br />\r\nKo gadi nevar nolaupīt.', '', '2012-07-27 13:32:14'),
(493, 'Saskan divas vīna glāzes,<br />\r\nSaskan divi gredzentiņi.<br />\r\nTā lai saskan jūsu dzīve:<br />\r\nMūžu kopā dzīvojot.', '', '2012-07-27 13:32:23'),
(494, 'Lai jums vienmēr saules gana,<br />\r\nLai ir silti, tad kad vēji brāzmot sāk,<br />\r\nLai no jūsu satikšanās pirmās<br />\r\nVisu mūžu laime līdzi nāk.', '', '2012-07-27 13:32:32'),
(495, 'Ak, ja varētu nomest no pleciem<br />\r\ngadus piecdesmit,<br />\r\npieglausties atkal mātes plecam<br />\r\nun pateikt, kas palika nesacīts...<br />\r\nAk, ja varētu nomest no pleciem<br />\r\ngājušos gadus kā mēteli,<br />\r\ndroši vien, vēlētos atkal pa vecam,<br />\r\nuz dzimto pusi, lai nes mani svētelis.<br />\r\nAk dienas, manas dieniņas jaukās,<br />\r\naiz loga jau kļava kailot sāk..<br />\r\ncik ātri pil dienas no mūža trauka <br />\r\nun pirmās salnas nāk..<br />\r\nAk, ja varētu!...Vai tas ir žēlums,<br />\r\nkas dzērvju atvadu kliedzienā skan,<br />\r\nvai arī šai rudens naktī vēlu<br />\r\njaunība sveicienus sūta man.<br />\r\nTālu, tālu caur sirmajiem siliem, kur šaura taciņa iet-<br />\r\nspīd zarā kā asara rasas piliens -<br />\r\ntur manas jaunības vizbules zied...', '', '2012-07-27 13:32:44'),
(496, 'Ne katram mīlu dzīvē atļausts rast<br />\r\nTā jāmeklē, kā dzintars jūras krastā,<br />\r\nBet ja šis dzintars plauķstā ņemts -<br />\r\nLai nepazūd tā spožums skaistais.', '', '2012-07-27 13:32:54'),
(497, 'Dvēselīte rasas lāsēs -<br />\r\nNe jau zvaigžņu, tikai rasas -<br />\r\nViņpus prieka, viņpus bēdas<br />\r\nDvēselīte krustceļos.<br />\r\nMūža gaismas pielijusi,<br />\r\nIet pa rasas ceļu klusi,<br />\r\nKlusi, klusi, ļoti klusi...<br />\r\nViss ieguvums - tik sīkas zvaigžņu dzirkstis,<br />\r\nKas salīst divās tuvās dvēselēs...', '', '2012-07-27 13:33:03'),
(498, 'Es gribu, lai mana mūža sekundes<br />\r\ntava sirds skaita krūtīs,<br />\r\nLai manām drūzmainām domām<br />\r\nPar sargu tava mierīgā elpa.<br />\r\nLai zem kājām mums vieni ceļi,<br />\r\nGribu, lai tur - aiz gadsimtu telpas<br />\r\nTava balss ar manējo balsi<br />\r\nCitā - trešajā<br />\r\nSarunājas.', '', '2012-07-27 13:33:12'),
(499, 'Gadiem kāpuši vienā kalnā<br />\r\nDivi cilvēki saprot un jūt - <br />\r\nPēc kopīgi pārciestām salnām<br />\r\nPat rūgtajā saldums var būt.', '', '2012-07-27 13:33:20'),
(500, 'Ko lai jaunajai līgavai vēlam, <br />\r\nJaunajam vīram ko padomā dot?<br />\r\nLai tiem pēc gada pa meitai un dēlam<br />\r\nUn lai ar kaimiņiem sadzīvot prot.', '', '2012-07-27 13:33:28'),
(501, 'Lai jūsu laimei jūras plašums<br />\r\nUn mīlestībai - saules mūžs!', '', '2012-07-27 13:33:35'),
(502, 'Ko šodien mēs Tev vēlēt varam,<br />\r\nkad balti sniegi zemi klāj,<br />\r\nkad tava mūža gadu kokam<br />\r\nir zelta krāsas lapa klāt?<br />\r\nTik klusa, rimta katru dienu,<br />\r\ntik nemanāma daudziem mums,<br />\r\nbet Tevi satiekot mēs jūtam-<br />\r\nnāk labestības strāvojums.<br />\r\nUn tādēļ mēs no sirds Tev vēlam,<br />\r\nlai balta katra diena aust,<br />\r\nlai Tavā dzīves zelta kausā<br />\r\nir laimes, prieka, saules daudz.', 'Lauma Daugiša', '2012-07-27 13:33:52'),
(503, 'Būs kalngali, straumes un kraujas,<br />\r\nBūs ezeri, meži kur tumst,<br />\r\nBet sauli, ja turēsiet saujās<br />\r\nBūs silti gan citiem, gan jums.', '', '2012-07-27 13:34:00'),
(504, 'Man patīk, kad mājā ir daudz pulksteņu. Māja tad liekas dzīva. Mājai ir sirds, kas pukst. Man patīk māja ar sirdi. Ar sirdi un dvēseli. Dvēsele ir reti kurā mājā, reti. Jo dvēselei vajag plašumu, gaišumu, labestību. Ne jau kvadrātmetru plašumu, nē, ne to, jūs jau mani saprotat. Kāpēc tas tā, nezinu, bet tā ir.<br />\r\nVarbūt, atnākot šovakar katrs savā mājā, ieklausīsimies. Mēs noteikti kaut ko sadzirdēsim, esmu pārliecināts par to, ieklausīsimies. <br />\r\n<br />\r\nMēs taču tāpēc dzīvojam, lai mums katram būtu māja ar sirdi un dvēseli. <br />\r\n<br />\r\nMūsu māja.', 'Bils', '2012-07-27 13:34:14'),
(505, 'Atskaties,-<br />\r\nNoietos<br />\r\nVisus sāc svērt,<br />\r\nGadu pie gada<br />\r\nKā pērles vērt.<br />\r\nUn brīnies:<br />\r\nCik virtene raiba.<br />\r\nTur pagadās gadi -<br />\r\nTik pilni kā dzirkstoši rauši,<br />\r\nUn pagadās pelēki,<br />\r\nVētrās un puteņos lauzti.<br />\r\nTukši,<br />\r\nKā pamestu mājokļu grausti.<br />\r\nTaču visi tie vienlīdz tev smeldz,<br />\r\nKad steidzies tiem cauri atmiņu dzelts,<br />\r\nUn redzi-<br />\r\nCik virtene īsa!<br />\r\nVisu to ietver<br />\r\nViens vievnīgs skatiens-<br />\r\nNo klupiena pirmā<br />\r\nUz bērnības ietves,<br />\r\nLīdz sirmajiem matiem-<br />\r\nViens ugunīgs mirklis <br />\r\nViss mūžs...', '', '2012-07-27 13:34:25'),
(506, 'Lai jūsu mūžs ir līdzīgs rudzu maizei,<br />\r\nKam tikai zemes spēks ir īstais raugs,<br />\r\nGaist rūgtums, bēdas, nedienas un raizes,<br />\r\nJa maizes riecienu sniedz draugam draugs.', 'L. Vāczemnieks', '2012-07-27 13:34:39'),
(507, 'Tev pateicos par zvaigzni svētku dienā,<br />\r\nPar oglēm pavardā, kad ārā snieg,<br />\r\nPar veldzes bezgalību ūdens malkā vienā,<br />\r\nKas mīlestībā sniegts.', '', '2012-07-27 13:34:49'),
(508, 'Glabā plīvuru balto,<br />\r\nNeizkar debesu lietiem,<br />\r\nTaureņa spārns<br />\r\nTik samircis nevarēs diet..<br />\r\nVisbaltāko uzticību<br />\r\nJel iekaliet dvēselēs savās,<br />\r\nNo sniega līdz sniegam<br />\r\nCauri tālumiem ievas zied.', '', '2012-07-27 13:34:58'),
(509, 'Ne tikai jaunību un prieku, bet arī sirmo matu sniegu, lai Jūs viens otrā mīlētu!', '', '2012-07-27 13:35:08'),
(510, 'Divas baltas laivas<br />\r\nVējš pa jūru triec<br />\r\nKaut kāds ērkšķains reibums <br />\r\nLiek tām satikties<br />\r\n<br />\r\nVēja spēks ir patiess<br />\r\nLaivas izšķirt spēs<br />\r\nBet, kas būs ja laivas<br />\r\nTo vairs negribēs?<br />\r\n<br />\r\nVaigu skauj pie vaiga<br />\r\nMēle mēli dzer<br />\r\nTava āda maiga<br />\r\nParadīzi tver<br />\r\n<br />\r\nIzdzīvot šo laiku<br />\r\nTā lai reibst un slāps<br />\r\nIzdzert saldo tvaiku<br />\r\nTomēr slāpst un slāpst<br />\r\n<br />\r\nZināms - tas vien mirklis<br />\r\nGan jau beigsies drīz<br />\r\nRealitātes dzirkles<br />\r\nZiedu nogriezīs<br />\r\n<br />\r\nLilijas tik baltas<br />\r\nŠodien klēpī krīt<br />\r\nLeduspuķes saltas<br />\r\nSagaida jau rīt<br />\r\n<br />\r\nPaliec tur, kur esi<br />\r\nNenāc tuvāk vairs<br />\r\nSaindēt var sveču<br />\r\nAromāts tik kairs<br />\r\n<br />\r\nOāzē tik skaistā<br />\r\nPašlaik peldam mēs<br />\r\nTuksnesi lai izgaist<br />\r\nLiekais izplēnē<br />\r\n<br />\r\nPlaisas, ķēdes, žņaugi<br />\r\nNeeksistē vairs<br />\r\nPaziņas un draugi<br />\r\nKaut kur tālē gaist<br />\r\n<br />\r\nCik Parīze ir tuvu<br />\r\nUn fraņču skūpsts man šķiet<br />\r\nTak nāvējošs, ko guvu<br />\r\nUn tāpēc laiks man iet...', '', '2012-07-27 13:35:29'),
(511, 'Tu mīļuma lāsi un skaistuma lāsi <br />\r\nDod padzerties mums, kad slāpst.<br />\r\nTu saudzīgiem vārdiem dvēseli glāsti<br />\r\nUn iecel vasarā zudušu sapni,<br />\r\nKad liekas - pazaudēts viss.<br />\r\nTu tumšākā naktī siltuma avots,<br />\r\nKad pavards ir nodzisis.<br />\r\nTu priekos un sāpēs vienīgā, māt,<br />\r\nNo dzīves sākuma esi mums klāt.', 'K. Apškrūma', '2012-07-27 13:35:45'),
(512, 'Saskan divas vīna glāzes,<br />\r\nSaskan divi gredzentiņi.<br />\r\nTā lai saskan Jūsu dzīves,<br />\r\nVisu mūžu dzīvojot!', '', '2012-07-27 13:35:53'),
(513, 'Mirkli padomājat, pirms kausus pilnus lejat,<br />\r\nMirkli padomājat, pirms dzīvē tālāk ejat.<br />\r\nTālāk - kopējas takas, kopēja laiva un irkļi,<br />\r\nTālāk - kopēja bēda un kopēji laimes mirkļi.', '', '2012-07-27 13:36:01'),
(514, 'Reizēm nemanot, pavisam klusi,<br />\r\nNe vien soļi, sirdis rakstā skan,<br />\r\nDod no sava prieka mazo pusi,<br />\r\nDod no bēdām lielo pusi man.', '', '2012-07-27 13:36:10'),
(515, 'Ak tu tavu tikumiņu,<br />\r\nAk tu tavu likumiņu,-<br />\r\nSvešs ar svešu sasatika,<br />\r\nMīļi mūžu nodzīvoja.', '', '2012-07-27 13:36:30'),
(516, 'Lai labie vārdi, gadiem krāti<br />\r\nNu nogāžās pār sievasmāti!', '', '2012-07-27 13:36:41'),
(517, 'Ja nav ne zvaigznītes, kas gaismu dod,<br />\r\nVar pasaule bez ziediem un bez skaņām būt - <br />\r\nLikt visam gavilēt un vizuļot <br />\r\nVar maza, sīka cilvēkbērna sirds,<br />\r\nJa viņa prieku jūt.', 'E.Zālīte', '2012-07-27 13:36:53'),
(518, 'Sakas liktas labprātīgi,<br />\r\nVezums jāvelk saprātīgi.<br />\r\nKas pār ilksi kāju liks,<br />\r\nTam ar pātadziņu tiks.', '', '2012-07-27 13:37:01'),
(519, 'Es strauta ūdeni tev krūzē nesu,<br />\r\nCik dīvaini, ka sagadījās tā -<br />\r\nMan pašai sudrablāse palika uz rokām,<br />\r\nKo it nevienam tagad neatņemt.<br />\r\nVēl kaut kur avots viz,<br />\r\nVēl kaut kur ozols šalc,<br />\r\nUn pāri visam - kāds putns visu nakti sauc un sauc....', '', '2012-07-27 13:37:09'),
(520, 'Šinī vietā būs Jums labi,<br />\r\nTo kliedz visi sētas stabi<br />\r\nDurvju zvans lai skaļi kauc<br />\r\nMūs lai bieži ciemos sauc!', '', '2012-07-27 13:37:18'),
(521, 'Dzīvē vieglu ceļu nav, nedz taku.<br />\r\nKatram laimes brīdim ir savs rūgtums klāt.<br />\r\nVēja laikā ejiet cieši blakus,<br />\r\nLai līdz galam varat nostaigāt!', '', '2012-07-27 13:37:26'),
(522, 'Lai mums vienmēr saules gana,<br />\r\nLai ir silti, tad kad vēji brāzmot sāk,<br />\r\nLai no mūsu satikšanām<br />\r\nMīlestība plaukst un plaukst.', '', '2012-07-27 13:37:37'),
(523, 'Nāc ar mani zemi art,<br />\r\nNāc ar mani maizi ēst,<br />\r\nPuķes sēt un bērnus gaidīt.<br />\r\nNāc ar mani bēdu noburt,<br />\r\nKopā mosties, kopā nogurt.<br />\r\nNāc ar mani - es ar tevi.', '', '2012-07-27 13:37:45'),
(524, 'Būs kalngali, straumes un kraujas,<br />\r\nBūs meži, kur ezeri tumst,<br />\r\nBet sauli ja turēsiet plaukstās,<br />\r\nBūs silti gan citiem, gan jums.', '', '2012-07-27 13:37:53'),
(525, 'Roku rokā tā kā šodien<br />\r\nVisu mūžu nostaigāt!', '', '2012-07-27 13:38:01'),
(526, 'Lai arī tad, kad sarmā mirdzēs mati,<br />\r\nJums mīlestība ir tā pati!', '', '2012-07-27 13:38:09'),
(527, 'Zelta rudens zelta pārslas bārsta,<br />\r\nZelta mūžs ar darbu vaiņagots,<br />\r\nVisiem mums no Jūsu dienu pūra<br />\r\nIr pa kādai riekšaviņai dots.', '', '2012-07-27 13:38:18'),
(528, 'Būs rītausmas, kas mūžos neizkvēlos,<br />\r\nUn saulrieti, kas dzīvei cauri degs.<br />\r\nJa sirdis mīl, tad novakaros vēlos<br />\r\nNekādi sniegi ziedus neapsegs.', '', '2012-07-27 13:38:26'),
(529, 'Šodien māēs jums laimi vēlam ,<br />\r\nPirmam jābūt ņipram dēlam !<br />\r\nTālāk kā jums pašiem tīk <br />\r\nja tie mazie neapnīk!!', '', '2012-07-27 13:38:38'),
(530, 'No spārniem veidojas ilgas,<br />\r\nNo ilgām dzimst pieskārieni.<br />\r\nBet tad,kad tiekamies atkal,<br />\r\nLai vēlreiz paliktu vieni,-<br />\r\nTad uzkrātais izzūd tikk spēji<br />\r\nKā celtspēja<br />\r\nkuģim ar sūcēm<br />\r\nUn viss ir tāpat ,kā bijis,-<br />\r\nAr senām<br />\r\nun svaigām brūcēm.<br />\r\nJo uzplēsto neaizlāpīt,<br />\r\nJa paši no jauna plēšam.<br />\r\nVar jautājums apdzist,<br />\r\nbet paliks<br />\r\nNo atmiņas <br />\r\nneizdzēšams.<br />\r\nUn nelīdz ne šķīstīšanās,<br />\r\nNe līdz pat debesīm kāpes...<br />\r\n<br />\r\nNo ilgām<br />\r\ndzimst pieskārieni,<br />\r\nNo pieskārieniem<br />\r\ndzimst slāpes.', '', '2012-07-27 13:38:48'),
(531, 'Lai jūsu sirdis tāpat kā ogle prot<br />\r\nDot spēku citiem, pati sadegot.', 'J. Silazars', '2012-07-27 13:39:00'),
(532, 'Tev pateicos par zvaigzni svētku dienā,<br />\r\nPar oglēm pavardā, kad ārā snieg,<br />\r\nPar veldzes bagātību ūdens malkā vienā,<br />\r\nkas mīlestībā sniegts.', '', '2012-07-27 13:39:09'),
(533, 'Nu Jūs ieburti viens otra gredzenā,<br />\r\ndzīves zelta gredzenā slēgti.<br />\r\nNu Jums kopīgas dienas un nedienas,<br />\r\nPusnakts tumsa un saules lēkti.', '', '2012-07-27 13:39:17'),
(534, 'Saules mūžu nodzīvot,<br />\r\nGredzentiņus nemainot.', '', '2012-07-27 13:39:28'),
(535, 'Pirms ceļā dodies ir jāzin, kurp iet,<br />\r\nIr jāmācās atrast un nepagurt,<br />\r\nNo slapjiem žagariem uguni kurt,<br />\r\nNo nespodra krama dzirksteli šķilt,<br />\r\nUn sasildīt citus, gan pašam silt.', '', '2012-07-27 13:39:37'),
(536, 'No dienas šīs sāk divi bērzi šalkt,<br />\r\nNo sulu salduma un plauksmes apreibuši,<br />\r\nLai nebeidz tie pēc pavasara alkt<br />\r\nVēl tad, kad birze būs tiem apkārt saaugusi.', '', '2012-07-27 13:39:48'),
(537, 'Sakļaujas bērzi un vējā šalc-<br />\r\nEsiet laimīgi abi!<br />\r\nEzerā atsaucas vilnis balts - <br />\r\nDiviem kopā būs labi.', '', '2012-07-27 13:40:00'),
(538, 'Dzīves ceļu šodien sāciet laimei,<br />\r\nEjiet to līdz sirmam vecumam!', '', '2012-07-27 13:40:11'),
(539, 'Tas ir sauciens, kuru nevar aizmirst,<br />\r\nTā ir mīla, kura nedrīkst rimt,<br />\r\nTrejas dzīves ja jums tiktu dotas,<br />\r\nCitas mīlas mūžam nedrīkst dzimt!', '', '2012-07-27 13:40:20'),
(540, 'Nav iekdienības vairs, bet savāds svētums<br />\r\nTev apņem katru mātes rūpju soli.<br />\r\nUn neredzams kāds staru vaiņags vijas<br />\r\nAp pieri tev, kad bērniņu tu šūpo.<br />\r\nAk, tā jau tikai parasta vien aina,<br />\r\nBet cik tur dziļas, dziļas brīnišķības.', '', '2012-07-27 13:40:28'),
(541, 'Liec ausi pie zemes un klausies,<br />\r\nKur tālumā zeme trīc:<br />\r\nNāk saule, nāk baltas dienas,<br />\r\nNāk Tava laime līdz.', '', '2012-07-27 13:40:37'),
(542, 'Mūžam saulainas lai divas dzīves,<br />\r\nKo gredzens šodien vienā sien!', '', '2012-07-27 13:40:48'),
(543, 'Noliec galvu drauga klēpī,<br />\r\nDzīves vēji skarbi pūš.<br />\r\nSarmos koki, sarmos mati,<br />\r\nDiviem vieglāk kopā būs.', '', '2012-07-27 13:40:57'),
(544, 'Darbos gāju, grūt` redzēju,-<br />\r\nBet neteicu māmiņai -<br />\r\nLai tā visu nezināja,<br />\r\nLai tā žēli neraudāja', '', '2012-07-27 13:41:04'),
(545, 'Vējš aizšķir aizgājušo dienu lapas klusi,<br />\r\nTās vārsmās meklēju,<br />\r\nKur ierakstīts bij svētums...<br />\r\nKā baltu naktsvijoli<br />\r\nVakars sapni manās rokās liek....', '', '2012-07-27 13:41:17'),
(546, 'Sakas liktas labpratigi, <br />\r\nVezums javelk sapratigi,<br />\r\nKas par ilksi kaju liks,<br />\r\nTam ar patadzinu tiks.', '', '2012-07-27 13:41:27'),
(547, 'Man vajag tevi, tikai tevi -<br />\r\nredzēt, dzirdēt, sajust,<br />\r\nun tad es varu kā bite ienirt<br />\r\nrododendros košos,<br />\r\npārlidot pasauli,<br />\r\nizkust kā medus lāsīte<br />\r\nvalgajās lūpās.<br />\r\n<br />\r\nLai pasaulē būtu,<br />\r\nman atkal vajag tevi,<br />\r\nvien tevi - mīlētu,<br />\r\npriekos un bēdās iepazītu,<br />\r\npār laiku un attālumu zināmu...<br />\r\nJo tikai tad, kad esi ar mani,<br />\r\nes pati plaukstu un mīlu!<br />\r\n<br />\r\nAtļauj šo mirkli no dienas dienā,<br />\r\ncaur mēnešiem un gadiem,<br />\r\nlai kopā varam iet pa lielceļiem<br />\r\nplatiem un tāliem,<br />\r\nkad skatiens skatienam<br />\r\nun plauksta plaukstai saka:<br />\r\n- Mums vajag, lai esam viens otram!', 'M. Snipke', '2012-07-27 13:41:47'),
(548, 'Lai vienmēr būtu sapnis ko piepildīt,<br />\r\nKādas alkas un ilgas, ko remdēt,<br />\r\nKāda laba un jūtīga cilvēka sirds,<br />\r\nDaži draugi un darbs, kuru darīt ļauts.', '', '2012-07-27 13:54:18'),
(549, 'Smaidi šodien!<br />\r\nŠī diena ir tava!<br />\r\nSkumjas un ilgas lai gaist,<br />\r\nSmejies šodien, šī diena ir tava,<br />\r\nViss šai dienā, lai veltīts ir tev!', '', '2012-07-27 13:54:32'),
(550, 'Lai saule smaida Tev ik dienu,<br />\r\nNaktis lai ar zvaigžņu gaismu grezno, <br />\r\nLai sirdī mīlestība plaukst<br />\r\nUn nenovīst nekad!', '', '2012-07-27 13:54:41'),
(551, 'Gadi neiet, gadi trakā steigā skrien,<br />\r\nUn ne attapties ne saprast-<br />\r\nTie jau tālu kāpās brien.<br />\r\nLai gadi iet, lai gadi skrien,<br />\r\nTos atpakaļ vairs neatsaukt.<br />\r\nUn katrs gads, kas ciemos nāk, <br />\r\nLai prieku, laimi nes Tev klāt !', '', '2012-07-27 13:54:55'),
(552, 'Ņem tasīti saules gaismas<br />\r\nUn pievieno to smaidam.<br />\r\nTad pārkaisi ar laimi<br />\r\nUn, nepārtraukti maisot,<br />\r\nPievieno nedaudz maiguma,<br />\r\nNedaudz smieklu,<br />\r\nTasi mirdzošas rīta rasas<br />\r\nUn zvaigzni no zilajām debesīm.<br />\r\nPievieno nedaudz pacietības,<br />\r\nTad lēnām iecilā ritmisku melodiju<br />\r\nUn atkal labi samaisi.<br />\r\nRīkojies saudzīgi ar šo maisījumu.<br />\r\nPievieno vēl vienu smaidu,<br />\r\nUn Tu sapratīsi, ka esi ieguvusi<br />\r\nTiešām vērtīgu recepti..', '', '2012-07-27 13:55:05'),
(553, 'Tam baltumam matos neticiet, jaunie<br />\r\nTo nesauc par mūža pāragro ziemu<br />\r\nTas ir no lielceļu putekļiem krājies,<br />\r\nTas ir no plaukstošo pīlādžu laika,<br />\r\nNo junija dienām,kad ziedēja rudzi..', '', '2012-07-27 13:55:14'),
(554, 'Saule grimst jūrā<br />\r\nKā sarkana mīna<br />\r\nZelta ragiem.<br />\r\nTur mūsu kurss.<br />\r\nGriezīsim sānis<br />\r\nVai taisni saulē?<br />\r\nStūrman!?', 'I.Ziedonis', '2012-07-27 13:55:32'),
(555, 'Dienas zied, jo tu tām ziedēt liec,<br />\r\nNaktis mirdz, jo tu tām gaismu dedz!<br />\r\nUn par mazu gaismu pati tu tiec tiem,<br />\r\nKas tavās acīs prieku redz!', '', '2012-07-27 13:55:40'),
(556, 'Mans prieks ir manas puķes -<br />\r\nTāds kluss un vienkāršs prieks,<br />\r\nCaur to man krietni vieglāk,<br />\r\nŠo dzīvi dzīvot tiek.<br />\r\nJo cilvēks, kam ir puķes,<br />\r\nKam puķes ziedus ver,<br />\r\nArvienu kaut ko gaida,<br />\r\nArvienu kaut ko cer.', '', '2012-07-27 13:55:48'),
(557, 'Lai saule Tev smaida, <br />\r\nLai gavilē prieks,<br />\r\nLai paliek no manis<br />\r\nMazs atmiņu prieks!', '', '2012-07-27 13:55:57'),
(558, 'Visskaistākie ir ziedi, ja rasa tajos mirdz. <br />\r\nVisskaistākie ir vārdi, ja tos saka no sirds!', '', '2012-07-27 13:56:05'),
(559, 'Ja prieku, tad stipru kā pērkona lietu,<br />\r\nJa bēdas - kā dzērvenes purvā,<br />\r\nBet enerģiju un spēku ikdienas rūpēm<br />\r\nPāri tam visam kā varavīksni.', '', '2012-07-27 13:56:13'),
(560, 'Lai ir kāda zeltaina kļava arvien<br />\r\nKam galotnē sirdi tu rudeņos sien,<br />\r\nUn pāri takai kad sniegpārslas krīt,<br />\r\nLai kāda roze Tev sirdī mīt!', '', '2012-07-27 13:56:26'),
(561, 'Lai tavi smiekli jautri skan kā gaismas stari mirdzoši virs ūdens burbuļa;<br />\r\nLai tava dzīve deg pie laiku robežas kā rasas pērle lapas galotnē.', 'R. Tagore', '2012-07-27 13:56:40'),
(562, 'Labais,kas mīt cilvēkā,ir kā ūdens dziļā akā:<br />\r\njo vairāk mēs smeļam no tās,<br />\r\njo vairāk svaiga ūdens satecēs.', 'Dens Mindao', '2012-07-27 13:56:53'),
(563, 'Bez ciešanām nav dzīve,<br />\r\nBez ērkšķiem nevar rozes būt.<br />\r\nBez pacietības dzīves ceļā<br />\r\nPie īstas laimes nenokļut!<br />\r\n<br />\r\nLai skatiens skaidri redz,<br />\r\nja dzīvē gadās līkums.<br />\r\nUn to,kas īsti liels,<br />\r\nLai neizposta sīkums!', '', '2012-07-27 13:57:02'),
(564, 'Lai laime Tev visur smaida,<br />\r\nUn mīlestības zvaigzne mirdz,<br />\r\nUn vēl - prieks allaž lai Tevi gaida<br />\r\nTo novēlam no visas sirds.', '', '2012-07-27 13:57:10');
INSERT INTO `regards` (`regard_ID`, `poem`, `author`, `created`) VALUES
(565, 'Būt savā patiesībā drošai,<br />\r\nBūt skaistai tā kā rozes zieds,<br />\r\nBūt tādai, kuru nevar nodot,<br />\r\nBūt tādai, kura pieburt prot.', '', '2012-07-27 13:57:19'),
(566, 'Tu esi atnācis šeit,<br />\r\nLai pasauli pārveidotu,<br />\r\nLai zvaigznes celtu<br />\r\nUn varavīkšņu tiltus būvētu.<br />\r\nTu esi atnācis šeit<br />\r\nLai būtu šajā laikā<br />\r\nLai būtu šajā vietā', '', '2012-07-27 13:57:27'),
(567, 'Veroties horizontā, mēs meklējam atbildi uz jautājumu: <br />\r\nKas ir laime...?<br />\r\nTāpat kā nav pasaulei malas, nav atbildes uz šo jautājumu.<br />\r\nMēs katrs pats esam savas dzīves ceļotājs un mūsu laime ir mūsu atklātā zeme. <br />\r\nSveiciens dzimšanas dienā!!', '', '2012-07-27 13:57:35'),
(568, 'Lai dzīve tik skaista un dzirkstoša viz<br />\r\nKā šampanietis kristāla glāzē,<br />\r\nLai piepildās sapņi, kas tālāk trauc<br />\r\nUn laimes, lai bezgala bezgala daudz.', '', '2012-07-27 13:58:01'),
(569, 'Lai dzīves ugunis<br />\r\nUn domas neapdziest,<br />\r\nLai sadedzināt<br />\r\nVisas ēnas vari.<br />\r\nPret sauli dzīvi griez,<br />\r\nUn vienmēr noturies<br />\r\nUz viņas zaļā zara!', '', '2012-07-27 13:58:13'),
(570, 'Nav bijis vēl neviens tāds gads,<br />\r\nKas tikai laimi nestu<br />\r\nNo bēdām,rūpēm sargātu<br />\r\nUn laimes kalnā vestu.<br />\r\nBet laimīgs tas,kas smaidīt prot,<br />\r\nŅemt pretī to ,ko dzīve dod!', '', '2012-07-27 13:58:28'),
(571, 'Vai tiešām labākais jau pagājis!?<br />\r\nNĒ...<br />\r\nLabākais vēl priekšā...<br />\r\nDrīz logā smiesies pavasara rīti,<br />\r\nkas uzdāvinās<br />\r\nprieka vizbulīti..<br />\r\nVēl laiks tev atvēlējis daudz-<br />\r\nteikt labus vārdus,<br />\r\nsmaidus dāvināt,<br />\r\nun mīlestības varavīksnes krāt....', '', '2012-07-27 13:58:38'),
(572, 'Kaut laikmets spītīgs, bargs un viltīgs,<br />\r\nKaut gadsimtenis šķībs un greizs,<br />\r\nNo dzīves nebēdz, dzīvi svētī,<br />\r\nKā svētkus, kuri beigsies reiz.<br />\r\n<br />\r\nUn naudu neskaiti, nedz gadus,<br />\r\nBet mirkļus, kuros proti degt.<br />\r\nJo vieglprātībai ir tāds mirdzums,<br />\r\nKo smilts, ne zeme nespēj segt. <br />\r\n<br />\r\nŅem spārnus, rūpēm pāri laidies,<br />\r\nKā taurenis zem saules spožs,<br />\r\nJa nevari ar naudu šķiesties, <br />\r\nAr prieku šķiesties vari drošs.', '', '2012-07-27 13:58:47'),
(573, 'Tikai vienu zeltainu smaidu<br />\r\npaņem plaukstā un pasaulei dod,<br />\r\nTikai vienu pūkainu domu<br />\r\nsavā skarbajā ikdienā rodi.<br />\r\nTikai vienu glāstošu vārdu<br />\r\nsaki cilvēkam, lietus kad līst,<br />\r\nTikai pilienu savējā prieka<br />\r\nneliedz draugam, kam dvēsele dzīst,<br />\r\nTikai sapni un cerību staru<br />\r\natstāj savējai sirdij - lai plaukst!', '', '2012-07-27 13:59:00'),
(574, 'Tu šodien skaties gadu kalnā,<br />\r\nUn diena tev tik gaiša šķiet.<br />\r\nMirdz tavos matos sudrabsalna,<br />\r\nBet acīs jaunība vel zied.', '', '2012-07-27 13:59:11'),
(575, 'Kaut šo vienu dienu gadā<br />\r\nApstājies un padomā,<br />\r\nKas Tavu dzīvi vada,<br />\r\nVai esi mierā ar sasniegto.', '', '2012-07-27 13:59:20'),
(576, 'Uzņem dzīvi kā spēli, <br />\r\nKurā spēlējam visi.<br />\r\nViens nevar spēlēt<br />\r\nŠajā dzīves skolā.', '', '2012-07-27 13:59:32'),
(577, 'Dzīve nevar apnikt,<br />\r\nJa to uzņem kā spēli.<br />\r\nSpēlē nevar vienmēr<br />\r\nTikai uzvarēt.', '', '2012-07-27 13:59:43'),
(578, 'Kaut Tu gada garumā<br />\r\nNepazītu mīlas badu.<br />\r\nKaut Tev neapsīktu <br />\r\nVēlme citus mīlēt.', '', '2012-07-27 13:59:57'),
(579, 'Katru mirkli - lielu, mazu-<br />\r\nIetin dzīves kamolā.<br />\r\nTie, kad savu ceļu iesi,<br />\r\nVērsies skaistā paklājā.', '', '2012-07-27 14:00:10'),
(580, 'Pie šampja glāzes tu sēdīsies<br />\r\nUn savu 21 dzimenīti svinēsi<br />\r\nTik žēl,ka blakus neesmu <br />\r\nUn saldo buču nedodu!', 'L.Isakoviča', '2012-07-27 14:00:25'),
(581, 'Aizplūdīs dienas kā skanīgi ūdeņi,<br />\r\nApšalcot gadu vistālāko līci.<br />\r\nTāda būs ziema, un tādi būs rudeņi,<br />\r\nCik mēs no vasaras paņemsim līdzi.', 'L.Vāczemnieks', '2012-07-27 14:00:48'),
(582, 'Gadi ir dīvaini dārgumi draugs,<br />\r\nBirst tie kā ziedi no ābeles kalnā.<br />\r\nNobirst un aizspurdz kā putni<br />\r\nRudens vizošā salnā.', '', '2012-07-27 14:01:01'),
(583, 'Dienas steidzas kā baloži zili<br />\r\nUn Tu velti tās atpakaļ sauc,<br />\r\nPazūd takas un aizsnieg sili,<br />\r\nVakars atmiņu kamanās brauc.', '', '2012-07-27 14:01:22'),
(584, 'Tas nekas, ja zelta liesmu<br />\r\nDedz jau kļavas zars.<br />\r\nSirds pat rudenī zin dziesmu<br />\r\nKurā pavasars.', '', '2012-07-27 14:03:34'),
(585, 'Izej rītausmā agri,<br />\r\nJa tev daudz vēl ko pasaulei sniegt.<br />\r\nParādi - ieva un ķezbere ziedlaimē zied.<br />\r\nParādi - debesis rītausmā sārtā,<br />\r\npalīdzi zīlīti sadzirdēt vecajos ozolos.<br />\r\nMāci pie tulpes un narcises noliekties..<br />\r\nIzej rītausmā agri - rīts pa rasa ceļiem un lielceļiem,<br />\r\nGaismiņu atsudrabo.....', '', '2012-07-27 14:03:43'),
(586, 'Es zinu nāks diena,<br />\r\nkad laime tev savas rokas pretī sniegs,<br />\r\nkad tava seja smaidīs, <br />\r\njo tavā sirdī būs mīlestība - <br />\r\njūtas, kas paceļ debesīs!', '', '2012-07-27 14:03:52'),
(587, 'Ai, gadi, ai, gadi,<br />\r\nVai tiešām Jūs pamazām žiglāki kļūstat?<br />\r\nNesteidzies novecot, sirds,<br />\r\nVēl jau mēs tiekam tiem līdz!', '', '2012-07-27 14:04:00'),
(588, 'Šodien mēs Tev svinam bēres, mēs tevi guldam zārkā kurš tapsēts ar baltu zīdu, pašu tevi esam ietērpuši melnā uzvalkā no Kardēna un Kurpēs no Belluči, apkārt daudz pavadītāju, muziku spēlē simfoniskais orķestris un līdz kapam tavu zārku aizvedīs melnā limuzīnā, pats zārksgatavots no simtgadīga ozola. Tad lai to ozolu iestāda vēl pēc simts gadiem, daudz laimes dzimšanas dienā un ilgu mūžu.', '', '2012-07-27 14:04:13'),
(589, 'Nepūlies iemantot pasauli,<br />\r\nTā jau ir Tevī.<br />\r\nTu tikai ej pa sauli<br />\r\nUn saullēktus glabā sevī.', 'L.Ikere', '2012-07-27 14:04:27'),
(590, 'Tev gadu mija,ko lai vēl?<br />\r\nJo dzīvē daudz kas skaists vēl notiks,<br />\r\nBet lai nekad nav gadu žēl,<br />\r\nKas tajā nodzīvoti.', '', '2012-07-27 14:04:42'),
(591, 'Lai gaisā jūtams viegls flirts,<br />\r\nBet ūdenim lai piejaukts spirts!!', '', '2012-07-27 14:04:50'),
(592, 'Pa ilgu ceļiem ejam visu mūžu,<br />\r\nLūdzam mēnesim un zvaigznēm mazliet mīlestības....<br />\r\n<br />\r\nLai jaukas dzimšanas dienas noskaņas un lai sirds gavilē!<br />\r\nApsveicu dzimšanas dienā!', '', '2012-07-27 14:04:58'),
(593, 'Ar tavu vārdu ievu ziedi<br />\r\nAr ķiršiem kopā dārzos ziedēt sāk<br />\r\nTo rotā tulpju līksmie stari<br />\r\nAr tūkstoš ziediem pavasaris!!!!', '', '2012-07-27 14:05:07'),
(594, 'Tie mazie sīkie velniņi<br />\r\nKas ellē dzied tik priecīgi<br />\r\nlai naktī tevi modina<br />\r\nUn no manis mīļi sveicina!!!!!!!!!!', '', '2012-07-27 14:05:17'),
(595, 'Ir jāprot atrast un nepagurt,<br />\r\nNo slapjiem žagariem uguni kurt,<br />\r\nNo nespodra krama liesmu šķilt,<br />\r\nGan sasildīt citus, gan pašam silt.', '', '2012-07-27 14:05:25'),
(596, 'Nu Tu vari pirkstā kost, <br />\r\nGadiņš klāt, nevis nost <br />\r\nLec uz augšu, lec uz leju,<br />\r\nGrumbiņas Tev bojā seju.', '', '2012-07-27 14:05:35'),
(597, 'Aizzied ziedi un virpuļo sniegi.<br />\r\nGadi tāpat.<br />\r\nPlīvo pavardā silta guns.<br />\r\nAtmiņas arī tāpat.<br />\r\nPaldies tev, dzīve, par to, kas bija.<br />\r\nPar to, kas būs - tāpat.', '', '2012-07-27 14:05:43'),
(598, 'Lai Tavā dzīvē allaž jauns ceļš.<br />\r\nLai Tavās burās pūš ceļa vējšs.<br />\r\nLai saule silti apspīd Tevi<br />\r\nUn lai lietus, kas līst Tev virsū,<br />\r\nIr veldzējošs un maigs!', '', '2012-07-27 14:05:51'),
(599, 'Lai pagātne ar vēja spārniem aizplūst<br />\r\nUn tagadne kā zelta stariņš mirdz,<br />\r\nBet nākotne, lai atnes visu,<br />\r\nIt visu to, ko ilgojas Tev sirds.', '', '2012-07-27 14:06:02'),
(600, '"Cilvēka sirds ir visvērtīgākais dimants uz Zemes, kas ir tik tīrs un tik stiprs, ka visus pārējos dārgumus spēj piepildīt ar gaismu.Mīli savu sirdi.Mīli savu mīlestību.Mīli visas būtnes uz Zemes, un tava dzīve kļūs vistīrākā vērtība."', 'Satja Sai Baba', '2012-07-27 14:06:36'),
(601, 'Laime ir sirdi pret sirdi dot,<br />\r\nSev prieku cita priekā iemantot,<br />\r\nSajust, ka pasaulē šajā esi vajadzīgs...', '', '2012-07-27 14:06:45'),
(602, 'Glabā savu dvēselīti,<br />\r\nTā kā baltu taurenīti!<br />\r\nNeļauj kājām samīdīt,<br />\r\nDzīves dubļos aptraipīt!', '', '2012-07-27 14:06:54'),
(603, 'Sirds no maziem spožiem mirkļiem <br />\r\npārtop krāsu melodijā,<br />\r\npasniedz roku dzīves priekam,<br />\r\nkas vēl būs un kas jau bija.', '', '2012-07-27 14:07:02'),
(604, 'Dejo un dziedi, skūpsti un mīli...<br />\r\nSkaistākos sapņus pārvērt par dzīvi!', '', '2012-07-27 14:07:13'),
(605, 'Novēlu Tev pārsteigumu, kas rada prieku,<br />\r\nPrieku, kas rada iedvesmu;<br />\r\nIedvesmu, kas iepriecina draugus,<br />\r\nDraugus, kas sagādā pārsteigumus !', '', '2012-07-27 14:07:22'),
(606, 'Vecums trakumam nebūs par šķērsli,<br />\r\nvecums jau arī aiz kalniem vēl vīd,<br />\r\nTagad tev labākie gadi tik sākas,<br />\r\nacīs nemiera dzirksteles spīd.<br />\r\nPar taviem gadiem pacelsim kausu,<br />\r\nnovēlot laimi, izdzersim sausu,<br />\r\nlai visu, ko vēlies, spētu tu gūt<br />\r\nun vienmēr ar mīļiem cilvēkiem būt.', 'Lauma Daugiša', '2012-07-27 14:07:36'),
(607, 'Apkārt - draugus,<br />\r\nApkārt - smaidus,<br />\r\nApkārt - jokus,<br />\r\nApkārt - prieku,<br />\r\nLejā - zemi,<br />\r\nAugšā - zvaigznes,<br />\r\nUguntiņu vidiņā!', '', '2012-07-27 14:07:51'),
(608, 'Lai gadi iet, tie nevar biedēt,<br />\r\nPat puteņos un salnās tie var ziedēt!', '', '2012-07-27 14:08:01'),
(609, 'Lai laika avotos <br />\r\nTu neizsīksti.<br />\r\nLai esi tā kā <br />\r\nmežezers arvien.<br />\r\nKas viļņu dziesmai<br />\r\ndāvā sūpļa līksti.<br />\r\nBet sevi ūdensrozes<br />\r\nsirdī sien!', 'Kornēlija Apškrūma', '2012-07-27 14:08:16'),
(610, 'Kāp kalnā, nepaliec lejā,<br />\r\nJa dzīve Tevi sauc.<br />\r\nUn smaidi laimei sejā,<br />\r\nKaut šķēršļu ir tik daudz.', '', '2012-07-27 14:08:24'),
(611, 'Tiem, kas krustceļos vieglumu meklēt ies,<br />\r\nIzbirs pēdējie cerību graši.<br />\r\nMēs no grūtuma nedrīkstam baidīties,<br />\r\nJo tas lielāks, jo lieli mēs paši.', 'L.Vāczemnieks', '2012-07-27 14:08:38'),
(612, 'Lai mērķus tādus sev spraužam,<br />\r\nKo sasniegt bez mūžības spēj.<br />\r\nLai dzīvojot rozes tik laužam,<br />\r\nKo palīdz, kad asaras sēj?', 'Ed. Veidenbaums', '2012-07-27 14:08:54'),
(613, 'Šajā dienā sveicam Tevi, <br />\r\nVisu labu vēlējam.<br />\r\nVisu Tev piedodam<br />\r\nLai Tu mums piedotu.', '', '2012-07-27 14:09:03'),
(614, 'Atkal Tu tik nevainīga<br />\r\nKā savā pirmajā dienā<br />\r\nUz šīs zemes<br />\r\nPaliec ilgāk tāda!', '', '2012-07-27 14:09:12'),
(615, 'Lai laimes daudz,<br />\r\nLai bēdas steidzas garām,<br />\r\nLai sirds ik dienu<br />\r\nPrieku atrast prot!', '', '2012-07-27 14:09:20'),
(616, 'Lai vai kādu darbu sākt,<br />\r\nLai ir mīlestība klāt.<br />\r\nLai ir rokās labi darbi,<br />\r\nnepateikti vārdi skarbi.', '', '2012-07-27 14:09:29'),
(617, 'Neskumstiet, ka gadi nāk un iet,<br />\r\nSkatiet atpakaļ un pasmaidiet mazliet-<br />\r\nTur bija mīlestība, ziedonis un prieks, <br />\r\nKo nebiedēja rudenis un sniegs.', '', '2012-07-27 14:09:37'),
(618, 'Vai tādēļ jānoskumst, ka dzīve ātri mainās,<br />\r\nka mirkļi zūd un solījumi gaist?<br />\r\nTu vari nolūkoties jaunās ainās<br />\r\nun jaunos strautos sapņu kuģus laist.', '', '2012-07-27 14:09:48'),
(619, 'Ik reiz kad Tavu dienu svinam <br />\r\nMēs lielo gadu skaitu minam <br />\r\nUn vienmēr kaut kā sanāk tā, <br />\r\nKa paliekam Tev parādā <br />\r\nNU, TAD UZ TIEM 18 !', '', '2012-07-27 14:09:58'),
(620, 'Dziesmā aust lai aukstais rīts,<br />\r\nsudrab` dzīpariem tas tīts.<br />\r\nUn ar zelta maliņu,<br />\r\nbagātību saliņu !!!', '', '2012-07-27 14:10:07'),
(621, 'Ar cerību gaišajiem stariem<br />\r\nPār klusajiem gadsimta mūžības galiem<br />\r\nJubilāram vecumiņš kaulos jau lien<br />\r\nKā sniegbalts tētiņš tam matos gudrību sien<br />\r\nUn Laimes māte viņam pidžamu diedz!', '', '2012-07-27 14:10:16'),
(622, 'Ko lai šajā dienā vēlam<br />\r\nBrašam mūsu zemes dēlam,<br />\r\nAutiņu ar šipiem kaltu <br />\r\nUn vēl tautumeitu baltu<br />\r\nKo cits nesauktu par mauku.', '', '2012-07-27 14:10:25'),
(623, 'Drīz sveicienus Tev dāmas sūtīs,<br />\r\nLai saule atspīd Tavās rūtīs,<br />\r\nKad ausīs dzimenītes rīts,<br />\r\nAr prieku, laimi cieši vīts.........', '', '2012-07-27 14:10:34'),
(624, 'Tik maz ir vajadzīgs, lai diena jauka kļūtu, <br />\r\nLai kāds man nejauši tik uzsmaidījis būtu.<br />\r\nTai dienā staigātu man blakus prieks, <br />\r\nKā kāds labestības ziedputeksnis liegs.', '', '2012-07-27 14:10:43'),
(625, 'Ej Laimīte Tu pa priekšu<br />\r\nEs Tavās pēdiņās', '', '2012-07-27 14:10:52'),
(626, 'Lai vienmēr ir radoša doma,<br />\r\nKas apvāršņa tālumos skrien.<br />\r\nLai esi kā jūra, kā joma,<br />\r\nKas dzintara pilna arvien.', '', '2012-07-27 14:11:01'),
(627, 'Cik dzīve tomēr skaista, kur tu kā darba bite vari būt.<br />\r\nTo sudrabu no mirkļu kārēm vācot, kas nepsūb un vējos nepazūd!<br />\r\nPriecīgi nosvinēt!', '', '2012-07-27 14:11:12'),
(628, 'Lai Tev veselība stipra<br />\r\nNesāp kauli, neķer gripa<br />\r\nLatiņi lai makā krīt,<br />\r\nLaime smaida katru rīt!', '', '2012-07-27 14:11:21'),
(629, 'Vēlu mazas laimītes,<br />\r\nLielu nav ko vēlēt....<br />\r\nLiela laime lieliski<br />\r\nPaslēpes prot spēlēt!', '', '2012-07-27 14:11:29'),
(630, 'Ar tālēm tāles sarunājas, <br />\r\nAiz gada nāk un aiziet gads. <br />\r\nVar nogurt kājas, nogurt rokas, <br />\r\nBet sirds lai nenogurst nekad.', '', '2012-07-27 14:11:38'),
(631, 'Atnāk dienas,<br />\r\nAiziet dienas<br />\r\nGadi vijas gredzenā<br />\r\nBalti sniegi,<br />\r\nZelta sapņi<br />\r\nSatin dzīvi kamolā.', '', '2012-07-27 14:11:47'),
(632, 'Kas skaistāks- ceriņi vai rozes?<br />\r\nTo allaž grūti pasacīt,<br />\r\nBet cilvēks skaistāks vēl par ziediem,<br />\r\nJo viņā zieda gaisma mīt', '', '2012-07-27 14:11:55'),
(633, 'Kam dzīve Tev šķiet nasta esam? <br />\r\nMēs katrs savu nastu nesam - <br />\r\nTā netiks vieglāka paties, <br />\r\nJa sūdzies Tu un sūrojies, <br />\r\nEj svilpodams pa dzīves taku, <br />\r\nUn nasta viegla kļūs, Tev saku.', '', '2012-07-27 14:12:04'),
(634, 'Vairāk smaidu, vairāk laimes,<br />\r\nMazāk nopūtu un naida.<br />\r\nViss ko darāt, lai Jums sokas<br />\r\nUn lai nepagurtu rokas!', '', '2012-07-27 14:12:13'),
(635, 'Sirsnīgs sveiciens vairs nav modē,<br />\r\nPārdevām to krāmu bodē,<br />\r\nTagad jauna mode valda,<br />\r\nTev no manis ir bučas saldas.:*:*:*', '', '2012-07-27 14:12:23'),
(636, 'Par tiem gadiem neteiksim nevienam,<br />\r\ntikai sapņos pastāvēsim klāt.<br />\r\nLai Tu pati vari šajā dienā<br />\r\napsnigušos mirkļus pārcilāt.', '', '2012-07-27 14:12:32'),
(637, 'Lai Tev vienmēr siltas kājas <br />\r\nUn, lai mājās gaida mājas. <br />\r\nZvaigznītes lai acīs spīd, <br />\r\nKreisā kāja nepaslīd..', '', '2012-07-27 14:12:41'),
(638, 'Lai tev veselība stipra<br />\r\nNesāp kauli, neķer gripa<br />\r\nLatiņi lai makā krīt<br />\r\nLaime smaida katru rīt!', '', '2012-07-27 14:12:51'),
(639, 'Atkal vienu prieka dienu <br />\r\nKalendārs mums paziņo, <br />\r\nUn šai dienā, kā arvienu, <br />\r\nGrēks nav iedzert maziņo!', '', '2012-07-27 14:13:00'),
(640, 'Dzīvo tu ar sirmiem matiem,<br />\r\nBrauc ar tamborētiem ratiem,<br />\r\nMeklē dzīvē tādas takas,<br />\r\nKur uz celmiem augtu bekas.', '', '2012-07-27 14:13:15'),
(641, 'Lai mākoņi izklīst, saule laistās,<br />\r\nLai cerību koki debesis skar,<br />\r\nLai visu labo, cēlo un skaisto<br />\r\nDzīvē vēl izbaudīt vari!!!', '', '2012-07-27 14:13:24'),
(642, 'Dzimenīti viegli svinēt,<br />\r\nOtrā dienā grūti minēt,<br />\r\nKāpēc šausmīgi sāp galva,<br />\r\nUn es atrodos zem galda...', '', '2012-07-27 14:13:32'),
(643, 'Gaisā kaut ko virmojam jūtu,<br />\r\nMīļu bučiņu tev sūtu ,<br />\r\nSveicu tevi dzimšanas dienā , <br />\r\nUn no laimes peldēt pienā !!!! :):)', '', '2012-07-27 14:13:42'),
(644, 'Gaisā kaut ko virmojam jūtu,<br />\r\nMīļu bučiņu tev sūtu ,<br />\r\nSveicu tevi Dzimšanas dienā ,<br />\r\nUn no laimes peldēt pienā !!!!', '', '2012-07-27 14:13:52'),
(645, 'Dzīvot brīnumā un noturēties,<br />\r\nŠūpoles pār šķērskoku dzīt,<br />\r\nLai tad aizturētā elpā spētu<br />\r\nPasauli kā jaunu ieraudzīt!', '', '2012-07-27 14:14:01'),
(646, 'Būt savā patiesībā drošai,<br />\r\nBūt skaistai tā kā rozes zieds,<br />\r\nBūt tādai, kuru nevar nodot,<br />\r\nBūt tādai, kura pieburt prot.', '', '2012-07-27 14:14:10'),
(647, 'Šai ziemas saltajā rītā gribas TEV Trīs rudzupuķes uzdāvāt,Kuras šonakt sniegotā pļavā tik silti smaidīja.Pēc mirkļa jau plaukstās bija Pļavas puķes Vienkāršās, debesu sūtītās,lai dvēselēs vasaras siltums un gaisma ielītu un Tavās acīs zvaigznes mirdzētu un sirsniņu saulīte sildītu.Mīļi sveicieninji Mūsu dieninjā.Tevi Miilot Valducis Tavs', '', '2012-07-27 14:14:21'),
(648, 'No vasaras mācies mūžu dzīvot -<br />\r\nMācies krāsas un siltumu krāt,<br />\r\nKatram dzīvotam mirklim,<br />\r\nPieliekot mīļumu klāt.', '', '2012-07-27 14:14:37'),
(649, 'Vai Tu atceries to dienu,<br />\r\nKad pirmo reizi dzēri pienu,<br />\r\nRatos gulēji un bļāvi<br />\r\nDupsi savu bučot ļāvi!', '', '2012-07-27 14:14:55'),
(650, 'Justies kā enģelim, spēt saklausīt savus smieklus un katrā peļķē ieraudzīt debesis!', '', '2012-07-27 14:15:05'),
(651, 'Dzīve ir raiba dzija,<br />\r\nUn tās gājiens grūts,bet skaists.<br />\r\nNeskaitīsim dienas kurās lija,<br />\r\nMirkļus skaitīsim,ko žēl bij projām laist.', '', '2012-07-27 14:15:14'),
(652, 'Lai vienmēr<br />\r\ndvēsele Tava<br />\r\nkā brīnumsvecīte zied,<br />\r\nlai ziedi, zvaigznes un sapņi<br />\r\ntur savākti vienuviet!<br />\r\nLai vienmēr ir<br />\r\nradoša doma,<br />\r\nkas apvāršņa<br />\r\ntālumos skrien,<br />\r\nlai esi kā jūra,<br />\r\nkā joma,<br />\r\nkas dzintara<br />\r\npilna arvien!', 'Kornēlija Apškrūma', '2012-07-27 14:15:28'),
(653, '..lai jums gadi skrien joņiem,<br />\r\nJums būs stipras vēl delnas.<br />\r\nNo ābeļu baltās rasas,<br />\r\nno pašu zemītes melnās,<br />\r\nkur sāta maizīte izaug,<br />\r\nkur brūni pumpuri veras..<br />\r\nLīdz mūža gājumam vēlam<br />\r\nšī svētība jāatdzeras.', 'M.Bārbale', '2012-07-27 14:15:45'),
(654, 'Saules tik daudz, cik pelnījis esi,<br />\r\nZemes, cik atrast sev proties<br />\r\nUz pleca saule, zem kājām zeme<br />\r\nVari pasaulē doties.', 'M.Bārbale', '2012-07-27 14:15:58'),
(655, 'Gadi nāk un pazūd tālē,<br />\r\ngadi prom ar vēja spārniem skrien<br />\r\nun tavā dzīves pavedienā<br />\r\nmezglu 24 jau sien.', '', '2012-07-27 14:16:14'),
(656, 'Skaistums tavs pienākums ?<br />\r\nStāv tavā varā;<br />\r\nAugstākais sasniegums<br />\r\nIr tikai garā.', 'J. Rainis', '2012-07-27 14:16:28'),
(657, 'Lai Tev visu gadu<br />\r\n<br />\r\nŠī diena spēku dod,<br />\r\n<br />\r\nĻauj izturēt visu, <br />\r\n<br />\r\nKas liktens dots.', '', '2012-07-27 14:16:41'),
(658, 'Nedomā, ko dāvās Tev, <br />\r\nKādas balvas iegūsi sev.<br />\r\nPacenties pasaulei kaut ko dot,<br />\r\nAr savām veltēm aplaimot.', '', '2012-07-27 14:16:51'),
(659, 'Cik labi dzīvot ir un būt<br />\r\nDot citiem, caur to arī gūt.<br />\r\nTas nozīmē - laimīgiem kļūt,<br />\r\nLai arī citi šo laimi jūt!!!', '', '2012-07-27 14:17:00'),
(660, 'Paliec tāla, vakarblāzma,<br />\r\nNesteidzies pār kalnu pretī.<br />\r\nVēl jau atvasaras stunda<br />\r\nDruvā vārpu vilni metīs.', '', '2012-07-27 14:17:14'),
(661, 'Pāri lido gadu putni zili<br />\r\nUn no viņu spārniem pārslas krīt.<br />\r\nNeredz tās ne pakalni, ne sili,<br />\r\nTikai matos tās var samanīt.', '', '2012-07-27 14:17:23'),
(662, 'Rožu mīlīgums un ērkšķu spīts,<br />\r\nKallu gaišums lai ir Tavā dabā.<br />\r\nŠodien to ar ziediem paņem līdz,<br />\r\nPaņem līdz un visu mūžu glabā.', '', '2012-07-27 14:17:32'),
(663, 'Neklausies par novecošanu uzdotos jautājumus - viņi Tavās kamanās vēl sēdēs... Vajag mīlēt - tas ir viss - mīlēt dabu, mākoņus, ciešanas, dzīvību, cilvēku....', '', '2012-07-27 14:17:42'),
(664, 'Nāk ziedons un atkal aiziet,<br />\r\nTik atmiņas negaist un kvēl<br />\r\nTev dzīvē daudz skaista būs priekšā,<br />\r\nBet bijušā vienmēr būs žēl.', '', '2012-07-27 14:17:51'),
(665, 'Gadi lieli, gadi smagie<br />\r\nAiziet līdz ar gājputniem. <br />\r\nBaltās dienas, nebaltās dienas<br />\r\nAiziet līdz ar gadiņiem.', '', '2012-07-27 14:18:01'),
(666, 'Pa reizei ir mīlēts, pa reizei ir smiets ,pa reizei līdz devītam kreklam ir svīsts ,un tikai pa reizei,reizītei vien un liekās šo reižu nav bijis nemaz', '', '2012-07-27 14:18:11'),
(667, 'Visi vēle šo un to<br />\r\nEs tev vēlešu kaut ko:<br />\r\nLai tu pupā pakārtos<br />\r\nUn ar zirni nošautos!', '', '2012-07-27 14:18:20'),
(668, 'Lai saule spoži mirdzot it-kā saka:<br />\r\n"Daudz koku savā ceļā iestādiet"<br />\r\nLai krāšņi plaukst Tava dzīves taka,<br />\r\nLai tā pret kalnu zaļā lokā iet.', '', '2012-07-27 14:18:29'),
(669, 'Dzimšanas diena vienreiz gadā <br />\r\nNeļauj draugiem nomirt badā<br />\r\nLiec uz galda siļķes asti,<br />\r\nBet zem galda alus kasti<br />\r\n<br />\r\nDaudz laimes!!!', '', '2012-07-27 14:18:38'),
(670, 'Trīs ziedus es Tev šodien sūtu, jo trijos ziedos izteikts viss, viens - laimei, otrs - mīlai, bet trešais tiekot vāzē likts!', '', '2012-07-27 14:18:49'),
(671, 'Lai tavs pavards dvēseli silda<br />\r\nGadi sudraba ledos kaut iet.<br />\r\nVēl tavā dārzā puķu ir gana,<br />\r\nvēl kā dārzs tava dvēsele zied!', '', '2012-07-27 14:18:58'),
(672, 'Ar tālēm tāles sarunājas,<br />\r\nAiz gada nāk un aiziet gads.<br />\r\nVar nogurt kājas, nogurt rokas,<br />\r\nBet sirds lai nenogurst nekad.', '', '2012-07-27 14:19:08'),
(673, 'Laimi ar kaudzēm,<br />\r\nPrieku ar pūriem,<br />\r\nnauda lai ienāk<br />\r\nNo visiem stūriem.', '', '2012-07-27 14:19:17'),
(674, 'Caur gadu birzi dzīvē<br />\r\nTik vienreiz katrs iet.<br />\r\nŠai gaitā vienīgajā<br />\r\nLai staro patiess prieks.', '', '2012-07-27 14:19:26'),
(675, 'Laimi ar kaudzēm,<br />\r\nPrieku ar pūriem,<br />\r\nnauda lai ienāk<br />\r\nNo visiem stūriem.', '', '2012-07-27 14:19:36'),
(676, 'Tikai dzimšanas dienā var uzzināt cik pasaulē ir daudz nevajadzīgu lietu.', '', '2012-07-27 14:19:45'),
(677, 'Daudz, Daudz Laim un Lustīg prāt,<br />\r\nLai Tev nauda čupēm nāk!<br />\r\nLai Tu Vinnē Golf vai Merss,<br />\r\nLai Tev netrūkst Šamps un Droug!!!<br />\r\nMīļš Sveicens Tovos Svētkos, Buča!', '', '2012-07-27 14:19:54'),
(678, 'Lai gadi rit, tā tam ir jābūt <br />\r\nUn lai par tiem nekad nav žēl. <br />\r\nTik jautru prātu, sauli sirdī <br />\r\nUn daudzus skaistus gadus vēl.', '', '2012-07-27 14:20:03'),
(679, 'Pārsteigumus, kas rada prieku!<br />\r\nPrieku, kas ragad iedvesmu!<br />\r\nIedvesmu, kas iepriecina draugus!<br />\r\nDraugus, kas rada pārsteigumus!', '', '2012-07-27 14:20:12'),
(680, 'Lai Tava sirsniņa būtu kā atvērta plauksta, kurā daudz mīlestības tauriņu nosēsties var!<br />\r\nLai vienmēr šie tauriņi mīlu un laimi nestu un nekad nepamestu!', '', '2012-07-27 14:20:22'),
(681, 'Paši skaistākie gadi<br />\r\nIr tie kuros dzīvojam<br />\r\nNe pagātne ne nākotne<br />\r\nPati lielākā mīlestība<br />\r\nIr tad kad atklājam<br />\r\nKa bez šī cilvēka<br />\r\nDiena neuzaust<br />\r\nKaut saule debesīs', '', '2012-07-27 14:20:30'),
(682, 'Paši skaistākie gadi<br />\r\nIr tie kuros dzīvojam<br />\r\nNe pagātne ne nākotne<br />\r\nPati lielākā mīlestība<br />\r\nIr tad kad atklājam<br />\r\nKa bez šī cilvēka<br />\r\nDiena neuzaust<br />\r\nKaut saule debesīs', '', '2012-07-27 14:20:40'),
(683, 'Saujiņu prieka,<br />\r\nmaisiņu aizrautības,<br />\r\npauniņu humora,<br />\r\nkulīti neatlaidības,<br />\r\nspaini gribasspēka<br />\r\nun kalnu mīlestības!', '', '2012-07-27 14:20:50'),
(684, 'Ne jau vieta dara cilvēku lielu,<br />\r\nBet cilvēks dzīvodams vietu pasaulē ceļ,<br />\r\nUn nav jau svarīgi, kuru tu pārej ielu,<br />\r\nIr svarīgi tas, UZ KURIENI, DRAUDZIŅ, TU EJ.<br />\r\n<br />\r\nNe jau gadi dara cilvēku vecu,<br />\r\nTikai laiks viesu noteiktā kārtībā liek<br />\r\nUn man vajag sajust vien kādu plecu<br />\r\nUn palūgt gadiem,lai drusku lēnāk tie skrien.<br />\r\n<br />\r\nNe jau apstākļi lietas būtību maina,<br />\r\nTik no virspuses katram viss citādāks šķiet.<br />\r\nUn es nezinu, vai tā ir vietas vaina,<br />\r\nKa no vienādām saknēm tik dažādi ziedi zied.', '', '2012-07-27 14:21:01'),
(685, 'Kā septiņkrāsu varavīksne,<br />\r\nKā dzintarotais saules sieks,<br />\r\nKā zaļojoša burvju vīksna<br />\r\nLai Tavā dzīvē staro prieks!', '', '2012-07-27 14:21:10'),
(686, 'To mazo, mazo laimīti!<br />\r\nLielo nav ko vēlēt...<br />\r\nLiela laime lieliski<br />\r\nPaslēpes prot spēlēt.', '', '2012-07-27 14:21:22'),
(687, 'Tavos svētkos vēlu tev<br />\r\nTo, ko vēlējos es sev:<br />\r\nPrieku, naudu, puķu vāzi,<br />\r\nPudeli un lielu glāzi.', '', '2012-07-27 14:21:32'),
(688, 'Visu to labo un skaisto<br />\r\nVisu, ko vēlies Tu sev,<br />\r\nTo šajā skaistajā dienā<br />\r\nNo sirds es novēlu Tev!', '', '2012-07-27 14:21:41'),
(689, 'Atļauj pieneņu<br />\r\npūkai dejot<br />\r\natļauj dvēseles<br />\r\nasarai dzejot<br />\r\niemāci šodienai<br />\r\nnesteigties beigties<br />\r\niemāci acu kaktiņam<br />\r\nsmieties<br />\r\niemāci domai<br />\r\nkā apinim vīties<br />\r\niemācies ļauties<br />\r\nun nebaidīties<br />\r\natrodi pieneņu<br />\r\npūkas sevī<br />\r\nneplūc<br />\r\nbet atļauj<br />\r\nlai vējā tās<br />\r\ndejo ...', '', '2012-07-27 14:21:51'),
(690, 'Es raugos tevī kā brīnumā,<br />\r\nJo tava dvēsele silta un vienkārša zied.<br />\r\nUn liekas, ka laiks šajā jaukajā vakarā<br />\r\nCauri vasaras ziedošām pļavām iet.<br />\r\nEs raugos tevī kā brīnumā-<br />\r\nCik vienkārši un jauki tavi gadi zied.<br />\r\nUn ir tik mīļi, ka varam līdzās tiem iet<br />\r\nUn tavā ziedlapu rasā iemirdzēt.', '', '2012-07-27 14:22:00'),
(691, 'Vien gaišas mīlestības vārdā, <br />\r\nmazs cilvēciņš no nebūtības izkāpj, <br />\r\nno mīlas ņemts un mīlai atdots, <br />\r\ntam mīlestību tālāk nest.', '', '2012-07-27 14:25:19'),
(692, 'Liec, Laimīte, baltu ziedu<br />\r\nMazajā rociņā,<br />\r\nLai ir balta tā dzīvīte<br />\r\nKurā būs jādzīvo!', '', '2012-07-27 14:25:30'),
(693, 'Saviem bērniem mēs varam atstāt<br />\r\ndivus vērtīgus mantojumus.<br />\r\nViens no tiem ir saknes,<br />\r\notrs-spārni.', '', '2012-07-27 14:25:41'),
(694, 'Mazs cilvēks šodien iziet dzīvē, māt,<br />\r\nUn redzi – ļaudis skatās, ļaudis gaida,<br />\r\nCik tīru spēsi viņu nosargāt,<br />\r\nCik spēsi dot no sava mīļā smaida.<br />\r\nMazs cilvēciņš iet šodien dzīvē, tēt,<br />\r\nViņš nezin rūpju, nepazīst vēl naida.<br />\r\nTev vajag viņā spēku iepotēt,<br />\r\nLai dzīvē viņu nemētā kā skaidu.', 'I.Ziedonis', '2012-07-27 14:25:55'),
(695, 'Kā balta puķe bērns ir uzziedējis, <br />\r\nLai kādreiz dzīvē spētu tālu iet. <br />\r\nNo nedienām, no salta dzīves vēja <br />\r\nŠo mazo dvēselīti sargājiet!', '', '2012-07-27 14:26:07'),
(696, 'Mazi bērni pēc cālēniem smaržo, <br />\r\nPēc pieneņu pūkainām galvām. <br />\r\nUn tu saproti, citas vairs nebūs- <br />\r\nŠī ir vislielākā balva.', '', '2012-07-27 14:26:18'),
(697, 'Varavīksnes košās krāsas<br />\r\nŠodien katrā sirdī mīt,<br />\r\nJo pie mums tu atnākusi<br />\r\nEsi, mazā, dvēselīt!<br />\r\n<br />\r\nAtnākusi, iesējusi<br />\r\nCerību, kam lielai augt,<br />\r\nKa tu mūsu vidū spēsi,<br />\r\nTā kā ozols plaukt,<br />\r\n<br />\r\nDarot gaišas mūsu dienas,<br />\r\nVisu saviem smaidiem vīt,<br />\r\nAugot lielākam, bez ēnām,<br />\r\nLai tavs ceļš ir, mazulīt!', '', '2012-07-27 14:26:32'),
(698, 'Lai aug mazais puisēniņš<br />\r\nTā kā stalts ozoliņš;<br />\r\nPaaugsies, tad būs viņš<br />\r\nMeitām siržu lauzējiņš.', '', '2012-07-27 14:26:42'),
(699, 'Bērniņš ir tik vēlīgs stādīņš, <br />\r\nKas bez saules nevar augt. <br />\r\nSaule, mīlestība-abas ļauj tam atraisīties, plaukt. <br />\r\nTēvs, māte-otra saule. <br />\r\nLai tā ilgi nenoriet, un, lai mīlestības radīts, bērniņš gaismas ceļu iet.', 'P. Bārda', '2012-07-27 14:26:53'),
(700, 'Starp dzīvības zvaigznēm daudzām <br />\r\nNu arī Tavējā mirdz! <br />\r\nTo sargās no pasaules vējiem <br />\r\nVecāku mīlošā sirds.', '', '2012-07-27 14:27:24'),
(701, 'Tā kā saule ābeļziedam<br />\r\nEsiet savam bērnam klāt,<br />\r\nLai no jūsu mīlestības<br />\r\nViņš var mūžu darināt!', '', '2012-07-27 14:27:34'),
(702, 'Šis mazais cilvēks ir Tev uzticēts <br />\r\nAk, kāda liela dabas uzticība, <br />\r\nJo katrs bērniņš piedzimst svēts, <br />\r\nBez nevienas melnas svītras <br />\r\n<br />\r\nUn Tev ir jābūt sargeņģelim.', '', '2012-07-27 14:27:45'),
(703, 'Nez no kurienes uzradies tas- <br />\r\nKlēpī ievēlies kamolīts mazs. <br />\r\nSaulē sārti vaidziņi tvīkst, <br />\r\nVai lielāku laimi maz vēlēties drīkst!', '', '2012-07-27 14:27:56'),
(704, 'Saules zelta piebiruši<br />\r\nVisi mazi pudurīši,<br />\r\nBūt tādam saulainam<br />\r\nTavam dzīves gājumam!', '', '2012-07-27 14:28:07'),
(705, 'Pagaidiet, lieli kungi, <br />\r\nMazs vēl jūras braucējiņš. <br />\r\nPaies dienas, aizskries gadi, <br />\r\nDroši stūri turēs viņš.', '', '2012-07-27 14:28:18'),
(706, 'Mazi saules dzīpariņi<br />\r\nManos vējos un saulītē tinas,<br />\r\nKā mīļi, mazi cipariņi<br />\r\nPret gaismiņu vijas un vijas.<br />\r\nŠiem mazajiem saules pirkstiņiem<br />\r\nLai saulīte ceļu vada,<br />\r\nUn gaišajiem acu plakstiņiem<br />\r\nLai prieks un laimīte rada.', 'Ā. Āre)', '2012-07-27 14:28:33'),
(707, 'Enģelītis pa gaisu trauc,<br />\r\nVisus šodien mīlēt sauc!<br />\r\nMīlestības bultas saldas,<br />\r\nLai Tev garām neaiztraucas!', '', '2012-07-27 15:00:47'),
(708, 'Sapņos Tu pie manis biji<br />\r\nUn man mīļi uzmaidīji.<br />\r\nNoskumu, kad pamodos,<br />\r\nProm no Tevis atrodos.', '', '2012-07-27 15:01:01'),
(709, 'Gribu skūpstīt tavas lūpas,<br />\r\nGribu izjust tavu kvēli šo,<br />\r\nGribu pateikt, ka es vēlos<br />\r\nTikai tevi vienīgo!Gribu skūpstīt tavas lūpas,<br />\r\nGribu izjust tavu kvēli šo,<br />\r\nGribu pateikt, ka es vēlos<br />\r\nTikai tevi vienīgo!', '', '2012-07-27 15:01:14'),
(710, 'Katra mīlestība ir īsta - <br />\r\nVai tā būtu mūžu gara<br />\r\nVai vienu vien mirkli īsa.<br />\r\nTā smaržo pēc pavasara,<br />\r\nTā atnāk kā maiga trīsa<br />\r\nVai zibens rīkste tik asa.<br />\r\nVienalga, vai purpurā tīta<br />\r\nVai aukstumā kaila un basa.<br />\r\nKā puķe vainagā vīta, <br />\r\nVai bāka uz klinšaina krasta<br />\r\nKatra mīlestība ir īsta<br />\r\nUn reizē - tik neparasta.', '', '2012-07-27 15:01:24'),
(711, '"...vārdi, vārdi...<br />\r\nCik daudz uz pasaules ir vārdu...<br />\r\nBet katram no mums ir sirds,<br />\r\nViena sirds un tajā mājo -<br />\r\nMīlestība."', '', '2012-07-27 15:01:35'),
(712, 'Amoriņš ar asām bultām<br />\r\nLēkā tik pa mīkstām gultām,<br />\r\nKas tad šim būs uznācis<br />\r\nLaikam bultu uzdūris!', '', '2012-07-27 15:01:48'),
(713, 'Mīlu tikai trīs lietas!<br />\r\nSauli, mēnesi, un Tevi!<br />\r\nSauli - dienā,<br />\r\nMēnesi -naktī,<br />\r\nun Tevi uz mūžu!', '', '2012-07-27 15:01:59'),
(714, 'Nav mīla jāmeklē, tā atnāks pati,<br />\r\nUn visām durvīm droši cauri ies,<br />\r\nJo mīlestība - tā ir dzīve pati,<br />\r\nNo kuras cilvēks nevar noslēpties.', '', '2012-07-27 15:02:12'),
(715, 'Paši siltākie vēji,<br />\r\npašas visskaistākās sniegpārsliņas,<br />\r\nvismīļākie vārdi,<br />\r\nvissaldākās bučiņas,<br />\r\nviskrāšņākkie ziedi,<br />\r\nlai tas viss tikai tev!', '', '2012-07-27 15:02:23'),
(716, 'Cik tūkstoš rasas pilienu ik rītu puķes slacina, tik tūkstoš mīļu bučiņu es tev no sevis nosūtu...', '', '2012-07-27 15:02:34'),
(717, 'Lai ir, ko mīlēt un ir, kas mīl.<br />\r\nLai ir, ko gaidīt un ir, kas gaida.<br />\r\nLai ir, ko darīt un ir, kas novērtē.<br />\r\nLai sargā no debesīm un saprot uz zemes!', '', '2012-07-27 15:02:45'),
(718, 'Love is strong and love is great, an endless search for a perfect mate<br />\r\nLove is hard and love is deep unspoken words and promises to keep<br />\r\nLove is good and love is blind, learning and laughing with an open mind<br />\r\nLove is the future and love is the past, sowing the seeds and children at last<br />\r\nLove is kind and love is giving, sharing souls and faithful living<br />\r\nLove is hurt and love is pain, too much to lose and everything to gain<br />\r\nLove is sweet and love is sincere, we smile, frown and shed a tear<br />\r\nLove is strange and love reaches from the heartland to the mighty beaches<br />\r\nLove is grand and love is real, sweet emotions with kisses to steal<br />\r\nLove is danger and love is bright, huddling close `till our bodies unite<br />\r\nLove is tight and love is tragic, together as one and always magic', '', '2012-07-27 15:02:57'),
(719, 'Es prasīju Dievam ūdeni un <br />\r\nViņš deva okeānu.<br />\r\nEs prasīju Dievam zvaigzni, <br />\r\nViņš deva debesis.<br />\r\nEs prasiiju Dievam eņģeli, <br />\r\nViņš deva man TEVI!', '', '2012-07-27 15:03:10'),
(720, 'Ir valentīndiena... <br />\r\nVisi putniņi darīs TO, <br />\r\nVisas bizbizmārītes darīs TO, <br />\r\nPat tauriņi darīs TO..<br />\r\nTu arī gribi darīt TO? <br />\r\nAizmirsti! Tu nemāki lidot!', '', '2012-07-27 15:03:21'),
(721, 'Dodu Tev erotisku, asins stindzinošu, lūpas dedzinošu, jaukāku kā paradīze, karstāku kā elle, saldāku kā zemene, super garu skūpstu!', '', '2012-07-27 15:03:33'),
(722, 'Pasaki, čalīt, kāpēc tas tā,<br />\r\nKāpēc man par Tevi jādomā?<br />\r\nSaule aust, pēc tam tumst,<br />\r\nMana sirds pēc Tevis skumst...', '', '2012-07-27 15:03:45'),
(723, 'Kad amoriņš šai dienā atnāk,<br />\r\ntas skatās kur lai bultas šauj;<br />\r\nBet nejau tikai to viņš dara,<br />\r\nViņš sūta sveicienus no manis.', '', '2012-07-27 15:04:08'),
(724, 'Trīs vārdi.<br />\r\n<br />\r\nTrīs vārdi uz papīra<br />\r\nTur izlikta dvēsele mana<br />\r\nTrīs vārdi uz papīra<br />\r\nBet kāda savādi jauka skaņa<br />\r\n<br />\r\nTie izsaka visu...un neko<br />\r\nTie satrauc, iedvesmo un saviļņo<br />\r\nTie patiesi mēdz būt un lēti<br />\r\nBet man tie vienmēr paliks svēti<br />\r\n<br />\r\nTos māte saka savam bērnam dzimušam<br />\r\nTos puisis saka meitenei tik jaunai<br />\r\nTos saka vīrs, kas karā dodas<br />\r\nTie vārdi, kur, no kurienes tie rodas? <br />\r\n<br />\r\nIr spēks šais vārdos iekšā likts<br />\r\nTur burvība, tur virzība, tur dzīves jēga<br />\r\nTrīs vārdi<br />\r\nEs<br />\r\nMīlu<br />\r\nTevi', 'Bils', '2012-07-27 15:04:19'),
(725, 'Viss izirst, viss izūd, <br />\r\nviss dzīvē ir nieks. <br />\r\nar tevi man tikties <br />\r\nir vienīgais prieks.', '', '2012-07-27 15:04:45'),
(726, 'Mīļi sveicieni Valentīndienā, miljoniem saldu buču, mīlestības spēku kā 100000 voltu un kaisles augstāko punktu kā Rīgas 800 noslēguma salūtu!<br />\r\nEsiet laimīgi un mīlestības pilni!', '', '2012-07-27 15:04:56'),
(727, 'Es zini, kā roze plaukst:<br />\r\nKlusi, klusi!<br />\r\nTā nosarkst un iemirdzas<br />\r\nKlusi, klusi!<br />\r\nEs zinu, kā mīlē sirds:<br />\r\nKlusi, klusi!<br />\r\nTā sapņo un ilgojas<br />\r\nKlusi, klusi!', 'J. Poruks', '2012-07-27 15:05:09'),
(728, 'Es esmu tev līdzās & būšu arī tad kad, kad tu būsi simtiem kilometru tālu no manis.<br />\r\nEs esmu tev & tu esi man', '', '2012-07-27 15:05:37'),
(729, 'Lai cik reižu es tev teiktu, ka mīlu tevi, <br />\r\natceries, ka ar katru nākošo reizi<br />\r\nes mīlu tevi vēl vairāk.', '', '2012-07-27 15:05:48'),
(730, 'Mīlestība - skaista rota,<br />\r\nTā par visu spožāk mirdz!<br />\r\nLaimīgs tas, kam tā ir dota,<br />\r\nKo var glabāt katra sirds.', '', '2012-07-27 15:06:02'),
(731, 'Angļu: I love you<br />\r\nAlbāņu: Te dua<br />\r\nArābu (sieviete vīrietim): Ana behibak<br />\r\nArābu (vīrietis sievietei): Ana behibek <br />\r\nArmēņu: Yes kez si`rumem<br />\r\nČečenu: Ne mohotatse<br />\r\nEbreju (vīrietis sievietei): Ani ohev otah <br />\r\nEbreju (sieviete vīrietim): Ani ohev et otha <br />\r\nEtiopiešu: Afgreki` <br />\r\nFranču: Je t`aime <br />\r\nGēlu: Ta gra agam ort <br />\r\nGrieķu: S` ayapo <br />\r\nHavajiešu: Aloha wau ia oi<br />\r\nHolandiešu: Ik houd van jou<br />\r\nHoupi: Nu`umi unangwa`ta<br />\r\nItāliešu: Ti amo<br />\r\nĪru: Taim i` ngra leat<br />\r\nJapāņu: Kimi o ai shiteru<br />\r\nJidiša: Ikh hob dikh lib <br />\r\nKatalāņu: T`estimo <br />\r\nKhmeru: Kh_nhaum soro_lahn nhee_ah<br />\r\nKorejiešu: Dangsinul saranghee yo <br />\r\nKreoliešu: Mi aime jou <br />\r\nĶīniešu: Wo ie ni <br />\r\nLatīņu: Te amo<br />\r\nLibāņu: Bahibak<br />\r\nLietuviešu: Tave myliu<br />\r\nMarokiešu: Kanbhik<br />\r\nNavaho: Ayor anosh`ni <br />\r\nPoļu: Kocham cie<br />\r\nPortugāļu: Amo<br />\r\nSpānu: Te quiero <br />\r\nTurku: Seni seviyorum <br />\r\nUngāru: Szeretlek<br />\r\nVācu: Ich liebe dich <br />\r\nVjetnamiešu (vīrietis sievietei): Anh ye^u em <br />\r\nVjetnamiešu (sieviete vīrietim): Em ye^u anh <br />\r\nZviedru: Jag älskar dig <br />\r\nMalaju: Saya sayang awak', '', '2012-07-27 15:06:21'),
(732, 'Sieviete un vīrietis - tās ir divas notis, bez kurām cilvēka sirds stīgas nevar atskaņot nevienu pareizu un pilnīgu akordu. Mīlestības pilnus šos svētkus!', '', '2012-07-27 15:06:33'),
(733, 'Es mīlu Tevi ar sirdi un dvēseli, ar miesu, kas kūst Tavā priekšā, ar asinīm, kas vārās Tavā tuvumā. Es mīlu Tevi!', '', '2012-07-27 15:06:45'),
(734, 'Tevi mīlu<br />\r\n<br />\r\nTu esi mans cilvēks ar visu,<br />\r\nkas tevī labs ir un ļauns.<br />\r\nAr to, ko tevī mīlu,<br />\r\nar to, par ko man ir kauns.<br />\r\n<br />\r\nTu esi mans kopā ar velnu,<br />\r\nkas tev sirds stūrītī sēž<br />\r\nun, izbāzis roķeli melnu,<br />\r\naiz matiem man dvēseli plēš.<br />\r\n<br />\r\nTu esi mans kopā ar Dievu,<br />\r\nkas baltu ceļu klāj<br />\r\nun, pasakas kaktiņā sēžot,<br />\r\nsargā un neatstāj.<br />\r\n<br />\r\nTu esi mans cilvēks ar visu,<br />\r\nKas tevī apdziest vai skan,<br />\r\nkas tevī nomirst un piedzimst,-<br />\r\ntu esi un paliec mans.', 'L.Sēle', '2012-07-27 15:06:59'),
(735, 'Es jūtu tikai Tevi,<br />\r\nTavus pieskārienus maigos,<br />\r\nSaldi teiktos vārdus.<br />\r\nTu esi man dzīve<br />\r\nManas jūtas<br />\r\nMani sapņi neizsapņotie<br />\r\nVārdi neizteiktie...', '', '2012-07-27 15:07:11'),
(736, 'Горячий Секс и нежная Любовь <br />\r\nОднажды познакомились, скучая. <br />\r\nИ вечером в божественный клубок <br />\r\nОни сплелись свободно и случайно. <br />\r\n<br />\r\nАх, этот Секс, он был немного груб, <br />\r\nНетерпелив и чуточку развратен; <br />\r\nОн языком касался тайных губ, <br />\r\nКогда Любовь присела на кровати. <br />\r\n<br />\r\nОна пустилась танцевать в ночи. <br />\r\nСначала в удивительных нарядах, <br />\r\nПотом кружилась голая почти, <br />\r\nЕдва касаясь, пролетая рядом. <br />\r\n<br />\r\nХотелось Сексу нежности, любви. <br />\r\nЛюбви хотелось секса и оргазма. <br />\r\nДо этого желания свои <br />\r\nИм не случалось исполнять ни разу', '', '2012-07-27 15:07:22'),
(737, 'Es jūtu tikai Tevi,<br />\r\nTavus pieskārienus maigos,<br />\r\nSaldi teiktos vārdus.<br />\r\nTu esi man dzīve<br />\r\nManas jūtas<br />\r\nMani sapņi neizsapņotie<br />\r\nVārdi neizteiktie...', '', '2012-07-27 15:07:33'),
(738, 'Es slīgstu domās par Tevi...<br />\r\nPar Tevi ..... par sevi....<br />\r\nEs kavējos atmiņās par Tevi...<br />\r\nPar Tevi ... par sevi...<br />\r\nEs acu priekšā redzu tik Tevi...<br />\r\nTik Tevi... tik Tevi...<br />\r\nVai arī tev tā ir?<br />\r\nPar mani, par sevi... par mums...?', '', '2012-07-27 15:07:51'),
(739, 'Es sirdī staroju.....<br />\r\ntu esi mans vienīgais kuru es mīlu savādāk...īpašāk....', '', '2012-07-27 15:08:02'),
(740, 'Ja Tevis nebūtu,<br />\r\nKur liktu mīlestību savu,<br />\r\nVai apmainītu to<br />\r\nPret nopelniem un slavu?<br />\r\nJa Tevis nebūtu, <br />\r\nMan pietiktu ar prātu,<br />\r\nBet skumstot vienatnē,<br />\r\nEs Tevi izdomātu.', '', '2012-07-27 15:08:13'),
(741, 'Mums pieder pasaule<br />\r\nmēs piederam viņai,<br />\r\ntu - man, es- tev,<br />\r\ntā līdz bezgalībai.', '', '2012-07-27 15:08:25'),
(742, 'Mīlestība ir slimība, kurai nepieciešams gultas režīms!', '', '2012-07-27 15:08:37'),
(743, 'Domu spārnus piešūt sev pie rokām. Zinu, ka sāpēs, bet tomēr... Šķīstu skūpstu nolikt uz Tavām lūpām, jālido tālu, bet tomēr... Tikai vienu glāstu no Tavām rokām... Zinu, ka sapnis, bet tomēr... Būt Tev cieši blakus, būt ar Tavām domām... Tu tik tālu, bet tomēr... es piešuju domu spārnus pie savām rokām.. Līst vientulības asinis, bet tomēr... Alkas būt Tev blakus ir tik kvēlas kā vienmēr...', 'KristiiKei', '2012-07-27 15:08:55'),
(744, 'Mīlestība ir tad, kad divi cilvēciņi skatās vienā virzienā, nevis viens uz otru....', '', '2012-07-27 15:09:17'),
(745, 'Nav saule tā mīlējusi jūru, nav jūra tā mīlējusi zemi, kā mīlu es Tevi...', '', '2012-07-27 15:09:29'),
(746, 'To laimi, ko tu meklē, <br />\r\nTo savā sirdī rod. <br />\r\nTo sirdi, ko tu mīli, <br />\r\nTo citam neatdod!', '', '2012-07-27 15:09:41'),
(747, 'Mēs katrs esam eņģelis, tikai ar vienu spārnu.<br />\r\nUn lidot varam, kad cieši apkampjam un apmīļojam mums līdzīgu būtni.', 'Lučiāno de Kreščenco', '2012-07-27 15:09:56'),
(748, 'Amors kā no dropes nācis,<br />\r\nSirdij garām šaut ir sācis,<br />\r\nSirde brīnās,apjukusi,<br />\r\nBulta ne tur trapījusi.<br />\r\n<br />\r\nSen jau ješka Grietu mīlē,<br />\r\nBet pa vidu jaucas Trīne;<br />\r\nŽanis Trīnei rozi nes,<br />\r\nAnna jautā - kā tad es?<br />\r\n<br />\r\nGailis brīnās,galvu groza,<br />\r\nJāieņem būs nikna poza;<br />\r\nKaimiņduksis sparu lielu,<br />\r\nAplidot sāk Gaiļa sievu.<br />\r\n<br />\r\nLai starp niekiem,smukumlietām,<br />\r\nPaliek vieta īstam priekam;<br />\r\nMīlestība skaista rodas,<br />\r\nĪstās sirdīs lai tā dodas!:)', '', '2012-07-27 15:10:22'),
(749, 'Tos brīžus, kad mums jautri gāja, tos atceries un piemini. Bet tos, kas Tevi sāpināja, tos aizmirsti un piedod!!!', '', '2012-07-27 15:10:35'),
(750, 'Cik labi ar Tevi var neizlikties,<br />\r\nEs tikai ar Tevi gribu tikties,<br />\r\nEs gribu, lai, tikai Tu manī skaties,<br />\r\nKad Tu manī skaties, es esmu patiess.', 'I.Ziedonis', '2012-07-27 15:10:51'),
(751, 'Kā saules gaisma, tik spoži iekriti man acīs<br />\r\nka rotaļieta, kuru samīļot<br />\r\nar dvēseli tu mani apbūri.. tu ienāci manā dzīve<br />\r\nun par to es tevi mīlu<br />\r\npaskaties, kur asaras līst... kāda cilveka dvēsele aizklīst<br />\r\nmana ir šeit un smaida,<br />\r\njo tu esi pie manis=)', '', '2012-07-27 15:11:02'),
(752, 'Skūpsts ir elektriskais zvaniņš <br />\r\nMīlestības priekšnamā.<br />\r\nMīlestība - cukurgraudiņš <br />\r\nDzīves rūgtā kafijā!', '', '2012-07-27 15:11:14'),
(753, 'Ceru, ka Tu jūti cik daudz un cik stipri par Tevi domāju!<br />\r\nEs Tev atdotu visas savas jūtas un domas un netikai... :) <br />\r\nArī savu sirsniņu! Tā pieder Tev vienīgajam!', '', '2012-07-27 15:11:26'),
(754, 'Kad svētki atkal klāt, un Valentīns sāk rokas māt, tad mīlestība apkārt valda un sirsniņa kļūst cukursalda...', '', '2012-07-27 15:11:37'),
(755, 'Es ceru, ka tu nepaiesi man garām,<br />\r\nEs ceru, ka mums nebūs garlaicīgi,<br />\r\nEs ceru, ka mums būs par ko parunāt,<br />\r\nEs ceru, ka pamodīsimies kopā...', '', '2012-07-27 15:11:49'),
(756, 'Es Tevi mīlēšu vienmēr, <br />\r\nLīdz dienai, kad varavīksne debesīs izdedzinās visas zvaigznes.<br />\r\nLīdz dienai, kad okeāns pārklās augstākos kalnus.<br />\r\nLīdz dienai, kad delfīni sāks lidot un papagaiļi dzīvos jūrā.<br />\r\nLīdz dienai, kad mēs sapņosim par dzīvi un dzīve kļūs par sapni.<br />\r\nEs Tevi mīlēšu Tevi mūžīgi...', '', '2012-07-27 15:12:00'),
(757, 'Eņģeļi to sauc par debesu prieku, <br />\r\nVelni - par elles mokām, <br />\r\nBet mēs cilvēki to dēvējam par mīlestību!!!', '', '2012-07-27 15:12:11'),
(758, '- Mīlestība ir lēnprātīga,<br />\r\nMīlestība ir laipna,<br />\r\nTā neskauž,<br />\r\nMīlestība nelielās,<br />\r\nTā nav uzpūtīga.<br />\r\nTā neizturas netikli,<br />\r\nTā nemeklē savu labumu,<br />\r\nTā neapskaišas,<br />\r\nTā nepiemin ļaunu.<br />\r\nTā nepriecājas par netaisnību,<br />\r\nBet priecājas par taisnību.<br />\r\nTā apklāj visu, tā tic visu,<br />\r\nTā cerē visu,<br />\r\nTā panes visu.<br />\r\nMīlestība nekad nebeidzas.', 'citāts no Bībeles', '2012-07-27 15:12:27'),
(759, 'Tu esi viens no eņģeļiem, kas mīt uz šīs zemes, bet vienīgais, kurš ir mans enģelis. Tu esi viens no vismīļākajiem, skaistākajiem eņģeļiem, bet vienīgais pēc kura es ilgojos ...', '', '2012-07-27 15:12:39'),
(760, 'Kaut kur vējā, tevi jūtu<br />\r\nKaut kur gaismā, tevi redzu<br />\r\nKaut kur putnos, tevi dzirdu<br />\r\nKaut kur lietū, tevi skūpstu<br />\r\n<br />\r\nKaut kur domās, tevi mīlu.', '', '2012-07-27 15:12:52'),
(761, 'Pie tevis agri rītā esmu,<br />\r\nPie tevis vēlu vakarā,<br />\r\nPie tevis glaužos saldos sapņos,<br />\r\nPie tevis jūtas nomodā.<br />\r\nTu mīti manā karstā elpā,<br />\r\nIkkatrā dvašas vilcienā -<br />\r\nNo tevis viss ap mani telpā<br />\r\nSkan simtkārīgātīgā atbalsā.', 'Aspazija', '2012-07-27 15:13:08'),
(762, 'Kad mani tu skūpsti, man liekas,<br />\r\nKa sirdī kāds vīraku lej,<br />\r\nKa zeme ar debesīm tiekas,<br />\r\nUn velni ar eņģeļiem dej!<br />\r\nAp mani viss griežas un grīļo,<br />\r\nViss smaržo un reibumu dveš,<br />\r\nTe laistās, te mirgo, te zvīļo,<br />\r\nTe satumst kā pusnaktī mežs.', 'E. Zālīte', '2012-07-27 15:13:23'),
(763, 'Teic, kā man tevi paijāt,<br />\r\nCik mīļus vārdus teikt?<br />\r\nKā maigā miegā aijāt,<br />\r\nKā modināt un sveikt?<br />\r\nMan divas vien ir rokas,<br />\r\nUn sirds, - tā viena vien;<br />\r\nBet mīlas saldās mokas<br />\r\nAug augumā arvien.', 'J. Jaunsudrabiņš', '2012-07-27 15:13:37'),
(764, 'Ja nebūtu jūras, es nezinātu, kas ir plašums,<br />\r\nJa nebūtu debess, es nezinātu, kas ir zvaigznes, <br />\r\nJa nebūtu Tevis, es nezinātu, kas ir ĪSTA MĪLESTĪBA.', '', '2012-07-27 15:13:49'),
(765, 'Glabā manu sirsniņu<br />\r\nTā kā baltu tauriņu,<br />\r\nNeļauj viņu samīdīt,<br />\r\nDzīves dubļos notraipīt.', '', '2012-07-27 15:14:01'),
(766, 'Tev 5as bučiņas uz lūpām,<br />\r\n5as uz kakla.<br />\r\n10 uz vaiga,<br />\r\nUn pārējās 75<br />\r\nKur vien vēlies!!!', '', '2012-07-27 15:14:12'),
(767, 'Tavas acis vienmēr<br />\r\nKārdinās manējās,<br />\r\nTavas lūpas vienmēr<br />\r\nUzsmaida manējām,<br />\r\nBet mana sirds,<br />\r\nAlkst pēc tavējās!', '', '2012-07-27 15:14:24'),
(768, 'Mana adrese: cerību rajons,<br />\r\nmīlestības pilsēta, <br />\r\nsvētlaimes iela,<br />\r\nnummurs - es,<br />\r\ndzīvoklis - tu,<br />\r\nindekss - es tevi mīlu!', '', '2012-07-27 15:15:45');
INSERT INTO `regards` (`regard_ID`, `poem`, `author`, `created`) VALUES
(769, 'Reiz dzīvoja divi cilvēki,<br />\r\nKas nezināja, kam patikt.<br />\r\nPaldies, ka tu mani satiki<br />\r\nUn ļāvi man sevi satikt.<br />\r\n<br />\r\nMēs mīlestību audzējām<br />\r\nTā vietā, lai dzīvotu spēli.<br />\r\nPaldies, ka tavā skatienā<br />\r\nEs lasu gan mīlu, gan kvēli.<br />\r\n<br />\r\nTu aizvien krāsaināks izliecies,<br />\r\nUn citās toņkārtās skani.<br />\r\nPaldies, ka negribi augt viens pats,<br />\r\nBet tikai kopā ar mani.<br />\r\n<br />\r\nPar brīžiem, kad varu ar sevi būt,<br />\r\nUn spārniem ko tu man devi -<br />\r\nPaldies, ka dāvā man brīvību,<br />\r\nUn līdzi - visu sevi.<br />\r\n<br />\r\nKāda mīļa zīmīte virtuvē,<br />\r\nVāzē - sarkana, smaržīga puķe...<br />\r\nPaldies, ka vēl aizvien izskatos<br />\r\nKā samīlējusies skuķe.<br />\r\n<br />\r\nBez vārdiem un ārējas sajūsmas -<br />\r\nEs tevi vienkārši mīlu.<br />\r\nPaldies, ka mūsu gaismiņu<br />\r\nTu paturi sirdī dzīvu.<br />\r\n<br />\r\nPaldies, ka atļauj man vājai būt<br />\r\nLai pēcāk stiprāka kļūtu -<br />\r\nTāpēc, ka esi Vīrietis,<br />\r\nEs sevī Sievieti jūtu...', '', '2012-07-27 15:15:59'),
(770, 'Mīlestība - skaista roze,<br />\r\nTa par visam skaistāk mirdz!<br />\r\nLaimīgs tas ,kam ta ir dota ,<br />\r\nKo var glabāt katra sirds!', '', '2012-07-27 15:16:11'),
(771, 'Ja es būtu velniņš mazs, Un zem zemes dzīvotu. Tad es uz zemes rādītos, lai ar tevi bučotos!', '', '2012-07-27 15:16:23'),
(772, 'Tā diena ir klāt, kad mīlestība nāk! Tā nepametīs jūs, tā samīļos jūs!', '', '2012-07-27 15:16:37'),
(773, 'Bez mīlestības nedzīvojiet,<br />\r\nBez mīlestības viss ir mazs!<br />\r\nBez mīlestības dūmo krāsnis<br />\r\nUn maizi negriež nazis ass.', 'I. Ziedonis', '2012-07-27 15:16:53'),
(774, 'Roze ož,<br />\r\nBite kož, <br />\r\nMīlestība pakaļ jož!', '', '2012-07-27 15:17:16'),
(775, 'Kad amoriņš šai dienā atnāk,<br />\r\ntas skatās kur lai bultas šauj;<br />\r\nBet nejau tikai to viņš dara,<br />\r\nViņš sūta sveicienus no manis.', '', '2012-07-27 15:17:27'),
(776, 'Katra mīlestība ir īsta - <br />\r\nVai tā būtu mūžu gara<br />\r\nVai vienu vien mirkli īsa.<br />\r\nTā smaržo pēc pavasara,<br />\r\nTā atnāk kā maiga trīsa<br />\r\nVai zibens rīkste tik asa.<br />\r\nVienalga, vai purpurā tīta<br />\r\nVai aukstumā kaila un basa.<br />\r\nKā puķe vainagā vīta, <br />\r\nVai bāka uz klinšaina krasta<br />\r\nKatra mīlestība ir īsta<br />\r\nUn reizē - tik neparasta.', '', '2012-07-27 15:17:41'),
(777, 'Vīrietis sniedz mīlestību.....bet sieviete pati ir mīlestība', '', '2012-07-27 15:17:51'),
(778, 'Es esmu kā koks bez lapām<br />\r\nEs mīlu tevi<br />\r\nEs esmu kā visums bez laika<br />\r\nEs meklēju tevi<br />\r\nPasaulē citā, ne šajā<br />\r\nDvēsele mājo bez laika<br />\r\nKā dzīvība pret sauli tiecas<br />\r\nEs mīlu tevi<br />\r\nSapnī, kas neīsts, kas prātā<br />\r\nNe šajā, bet citā<br />\r\nEs meklēju tevi<br />\r\nTu nāc<br />\r\nKā zvans no debesīm<br />\r\nMums katram skan šāds zvans<br />\r\nTas jāsadzird, to dzird<br />\r\nAr dvēseli<br />\r\nTu dzirdi? <br />\r\nSkan...', 'Bils', '2012-07-27 15:18:03'),
(779, 'Kad mēs tiekamies, mīļais,<br />\r\nTad runājam niekus,<br />\r\nUn nepateiktais kā akmens<br />\r\nGuļ starp mums nekustināts.<br />\r\nMēs pļāpājam vien,<br />\r\nBet pie sevis<br />\r\nKlusībā akmeni bīdām<br />\r\nUn stumjam, un netiekam klāt<br />\r\nNe tam, ne arī viens otram,<br />\r\nJo abi runājam niekus,<br />\r\nBet galvenais tā arī paliek,<br />\r\nPaliek nepateikts?', 'Gunta Kraulere', '2012-07-27 15:18:32'),
(780, 'Tu esi viens no eņģeļiem, kas mīt uz šīs zemes, bet VIENĪGAIS, kurš ir mans Eņģelis ...<br />\r\nTu esi viens no eņģeļiem, kas runā ar mani, bet VIENĪGAIS, kurā es ieklausos ...<br />\r\nTu esi viens no eņģeļiem, kas naktīs nāk pie manis, bet VIENĪGAIS, kuram es ļauju sev pieskarties ...<br />\r\nTu esi viens no vismīļākajiem, vislabākajiem eņģeļiem, bet VIENĪGAIS, pēc kura es ilgojos ...', '', '2012-07-27 15:18:44'),
(781, 'Ne jau skaistums nosaka, ko mēs mīlam,<br />\r\nMīlestība ir tā, kas nosaka, ko mēs uzskatām par skaistu...', '', '2012-07-27 15:19:12'),
(782, 'Tās dienas, kad ar tevi nesatiekos,<br />\r\nNekur nav miera man,<br />\r\nUn sirds tad neatplaukst nekādos priekos,<br />\r\nKā iesists zvaniņš skan.<br />\r\nTais dienās, kad ar tevi nesatiekos,<br />\r\nNekur man saules nav;<br />\r\nKā izstīdzējis stāds es sevim liekos,<br />\r\nKas tumsā nomests stāv.', 'E. Zālīte', '2012-07-27 15:19:30'),
(783, 'Es mīlu ierasties tavos sapnīšos, apburt tajos, mīlēt, skūpstīt... palikt atmiņā!', '', '2012-07-27 15:19:43'),
(784, 'Mazs Amoriņš man šodien <br />\r\nTā pie austiņas čukstēja<br />\r\nMan vajag atdzīties tik Tev, <br />\r\nKa esi svarīgs man!', '', '2012-07-27 15:19:55'),
(785, 'Kad blakus tu man nostājies,<br />\r\nEs nejūtos vairs vientuļa.<br />\r\nKad lūkojies uz mani tu,<br />\r\nEs nejūtu kā elpoju.<br />\r\nTu iemācīji pasmaidīt<br />\r\nTu iemaacīji laimi gūt!<br />\r\nŠī pasaule tik skaista šķiet,<br />\r\nJa līdzās vienmēr esi man.<br />\r\nEs lūdzos tevis...neaizej!<br />\r\nBez tevis dzīvot neprotu.', '', '2012-07-27 15:20:06'),
(786, 'Mīli vienu <br />\r\nSkūpsti vienu <br />\r\nPievil vienu<br />\r\nKatru dienu!!!', '', '2012-07-27 15:20:18'),
(787, 'Ar Tevi vasarā un ziemā,<br />\r\nLietū un sniegā.<br />\r\nAr Tevi bēdās un priekos,<br />\r\nSvētkos un niekos.<br />\r\nAr Tevi kopā būt,<br />\r\nGan gaisā, gan negaisā,<br />\r\nZem segas vai pat maisā.<br />\r\nAr Tevi ezerā un jūrā,<br />\r\nLaivā ar un bez burām<br />\r\nTik Tev blakus būt<br />\r\nTā, lai sirds to jūt.<br />\r\nKā man gribas<br />\r\nTā Tev blakus būt.', '', '2012-07-27 15:20:29'),
(788, 'Klusums skaists... Lūpu pieskāriens maigs... Viss zūd un padebešos mūk... Tikai tu un es... Tikai abi mēs...', '', '2012-07-27 15:20:45'),
(789, 'Bijušais tik skaisti šķiet<br />\r\nGribas atpakaļ vēl iet.<br />\r\nGribas seno taku mīt,<br />\r\nNesacīto - pasacīt.', '', '2012-07-27 15:20:57'),
(790, 'Ka es alkstu Tevis, vienīgi Tevis - to nemitīgi, lai atkārto mana sirds. Visas iekāres, kas mani dienām un naktīm mulsina, ir neīstas un tukšas līdz pašiem pamatiem.<br />\r\nKā nakts, kas savā tumsā glabā lūgumu pēc gaismas, lai arī mana zemapziņa no saviem dziļumiem saucienu raida, - es alkstu Tevis, vienīgi Tevis.<br />\r\nKā vētra, visā sparā brāzdamās pret klusumu, tomēr tvīkst beigu un miera, tāpat mana iekšējā trauksme atsitas pret Tavu mīlu un nemitīgi sauc, - es alkstu Tevis, vienīgi Tevis...', '', '2012-07-27 15:21:08'),
(791, 'Valentīns ir vienreiz gadā<br />\r\nNenomirsi Tu nu badā<br />\r\nTik daudz siržu ceļo apkārt<br />\r\nMājās visur tās gribās sakārt!', '', '2012-07-27 15:21:22'),
(792, '...Manā dvēselē Tu esi ienākusi uz visiem laikiem. Šeit, uz šīs zemes, Tu esi gaiss, ko elpoju, mani sirdspuksti, asinis manās dzīslās, Tu esi katrs mans sapnis, katra mana lūgšana. Tu esi saullēkts un saulriets, mēness, zvaigznes un debesis. Tu esi atbilde uz visiem maniem jautājumiem... Ir tik bezgala brīnišķīgi un pacilājoši domāt, ka katra dvēsele izjutīs to pašu, ko es tagad jūtu pret Tevi!!!', '', '2012-07-27 15:21:33'),
(793, 'Ūdens spēks ratus griež,<br />\r\nMīlestība sirdi spiež,<br />\r\nLabāk mīlēt, ciest un cerēt<br />\r\nNekā kaktā cāļus perēt.', '', '2012-07-27 15:21:44'),
(794, 'In the sky are many stars,<br />\r\nbut the moon is only one!<br />\r\nIn the world are many girls,<br />\r\nbut for me is only one!', '', '2012-07-27 15:21:55'),
(795, 'Viens Tevi mīl un Tu esi šaj pasaulē vajadzīgs.Tu vienu mīli un Tev šo pasauli vajag, lai vienam to dāvātu.', 'Rainis', '2012-07-27 15:22:09'),
(796, 'Tos brīžus, kad mums jautri gāja -<br />\r\nTos, lūdzu, atceries!<br />\r\nBet to, kad tevi sāpināju -<br />\r\nTos piedod man un aizmirsti!', '', '2012-07-27 15:22:21'),
(797, 'Ik rītus,<br />\r\nKad saule starus mākoņos pakar,<br />\r\nEs tevi mīlu vairāk<br />\r\nNekā vakar. Vakaros,<br />\r\nKad riets sāk ūdeni apzeltīt,<br />\r\nEs tevi mīlu mazāk<br />\r\nNekā rīt.', 'V. Toma', '2012-07-27 15:22:38'),
(798, 'Šajā pasaulē........iespējams, tu esi vienkārši cilvēks............bet kādam............tu esi visa pasaule...............=))))))*.....', '', '2012-07-27 15:23:06'),
(799, 'Šie skūpsti šie glāsti<br />\r\nKas reiz skāra manu seju<br />\r\nTas vis man pietrūkst<br />\r\nKa gaiss ko elpot<br />\r\n<br />\r\nŠīs acis, burvīgas un skaistās<br />\r\nKuras lukojās uz mani<br />\r\nTas vis man pietrūkst<br />\r\nJo neesi man blakus<br />\r\n<br />\r\nŠie čuksti <br />\r\nKuri reiz piepildiija manu sirdi<br />\r\nTas viss man pietrūkst<br />\r\nJo pazudis esi Tu<br />\r\n<br />\r\nKāpēc Tu pazūdi tieši šajā mirklī<br />\r\nKad esi vajadzīgs<br />\r\nKqd sāku iemīlēt Tevi patiesi<br />\r\nTavas acis, skūpsti, <br />\r\nPietrūkst man', 'santuciite', '2012-07-27 15:23:18'),
(800, 'Es zinu, kā roze plaukst:<br />\r\nKlusi, klusi!<br />\r\nTā nosarkst un iemirdzas<br />\r\nKlusi, klusi!<br />\r\nEs zinu, kā mīlē sirds:<br />\r\nKlusi, klusi!<br />\r\nTā sapņo un ilgojas<br />\r\nKlusi, klusi!', '', '2012-07-27 15:23:27'),
(801, 'Ieskaties man acīs! Tās tev visu pateiks bez vārdiem.', '', '2012-07-27 15:23:34'),
(802, 'Atnāc pie manis tāds kails<br />\r\nBez savas dvēseles grabošās čaulas,<br />\r\nBez lieka spožuma ? jūtīgs un maigs,<br />\r\nUn bez kautrības ? atvērts līdz kaulam!<br />\r\n<br />\r\nAtnāc pie manis vien tāds ? <br />\r\nVisu mūžību līdzi ņemošs,<br />\r\nLaika dzirnās ne sijāts, ne malts,<br />\r\nUz abām kājām ? stabili stāvošs!<br />\r\n<br />\r\nAtnāc pie manis un tad<br />\r\nEs tev redzi ieburšu plaukstās,<br />\r\nUn došu prasmi to lietot kaut kad, <br />\r\nJa tu neaizmirsīsi man jautāt!<br />\r\n<br />\r\nAtnāc pie manis tik drošs,<br />\r\nKāds ir likten?s, kad veiksmi viņš dala,<br />\r\nNe vairs lūdzošs, nedz arī pavēlošs,<br />\r\nBet zinošs, ka esi jau galā!<br />\r\n<br />\r\nPaliec pie manis nezinot,<br />\r\nVai ir pasaulei vēl kāda mala;<br />\r\nViens tu nespēsi tālu aizlidot,<br />\r\nTo spēj divi, kad mūžībā dalās!', 'Gunta Kraulere', '2012-07-27 15:23:47'),
(803, 'Aiz gaismas mirdz zieds,<br />\r\nAiz laimes pukst sirds<br />\r\nPa pasauli mūžam<br />\r\nLido mīlestība.', '', '2012-07-27 15:23:56'),
(804, 'Lai kādā svečturī<br />\r\nTu ielikts tiksi,<br />\r\nJa būsi gaišs -<br />\r\nTu sevi mīlēt liksi.', '', '2012-07-27 15:24:04'),
(805, 'Lai Tava sirds, kā putra vārās,<br />\r\nUn kā aizkauts cālēns spārdās<br />\r\nLai mīlestība sirdi spiež<br />\r\nUn visas zarnas riņķī griež!!', '', '2012-07-27 15:24:12'),
(806, 'Amoriņš ar asām bultām<br />\r\nLēkā tik pa mīkstām gultām,<br />\r\nKas tad šim būs uznācis<br />\r\nLaikam bultu uzdūris!', '', '2012-07-27 15:24:20'),
(807, 'Tava mīla dod prieku skumjās, mieru trauksmē, cerību izmisumā ...', '', '2012-07-27 15:24:28'),
(808, '...Ņem!<br />\r\nVisas manas mīlestības ņem!<br />\r\nVai nu ir vairāk?<br />\r\nPats redzi - nav.<br />\r\nJa mīlas tiesu taisnīgu tu lem,<br />\r\ntad viss, ko prasi, sen jau bijis tavs.<br />\r\nUn bij šī mīla sen tev dāvināta,<br />\r\nar to tu vari darīt kas tev tīk.<br />\r\nBet kurnēšu, ja untumainā prātā<br />\r\nTu spēlēdamies savainosies pats.<br />\r\nTev, mīļo zaglēn, piedodu,<br />\r\nkaut gan līdz pēdējam es aplaupīta.<br />\r\nBet gribu, lai saproti, ka naidu paciest<br />\r\nman ir vieglāk nekā mīlas netaisnību.<br />\r\nUn tomēr - mīlot moki, nokauj pat - <br />\r\nmēs ienaidnieki nebūsim nekad!...', '', '2012-07-27 15:24:38'),
(809, 'Man nevajag daudz...<br />\r\nTikai rokas, kas maigi spēj glāstīt<br />\r\nDvēseli pilnu ar Sauli,<br />\r\nUn vēl lūpas, kas maigi spēj skūpstīt!', '', '2012-07-27 15:24:46'),
(810, 'Es tevi mīlu, dievinu <br />\r\nBez tevīm dzīvot nevaru <br />\r\nMana sirds pieder tev <br />\r\nTikai tev mana mīļā!', '', '2012-07-27 15:24:55'),
(811, 'Pāriet krusa, pāriet lietus,<br />\r\nVēji nāk un projām iet,<br />\r\nSaule bāl un meklē rietus,<br />\r\nTikai mīla nepāriet.', 'V. Veldre', '2012-07-27 15:25:03'),
(812, 'Būt vienu dienu laimīgam, vai zini,<br />\r\nPusgadam tad ir priecīgs skanējums.<br />\r\nTu, saulē sēdēdams, tad mīklas mini.<br />\r\nBūt vienu dienu laimīgam, vai zini, <br />\r\nIr - starā pārvērsties un skūpstīt cini,<br />\r\nJo mīlas dāsnums patīk visiem mums.<br />\r\nBūt vienu dienu laimīgam, vai zini,<br />\r\nPusgadam tad ir priecīgs skanējums.', 'A.Pormale', '2012-07-27 15:25:27'),
(813, 'Viss tas, ko sauc par mīlestību, ir dvēseles ziedēšana.', '', '2012-07-27 15:25:35'),
(814, 'Tu man jautā - ko es mīlu,<br />\r\nEs tev varu pasacīt.<br />\r\nVisus lielos burtus saliec,<br />\r\nIznāks gluži pazīstams!', '', '2012-07-27 15:25:44'),
(815, '~*It`s funny how a heart is two teardrops upside down*~', '', '2012-07-27 15:25:51'),
(816, 'Man ļoti gribas tev patikt,<br />\r\nMan gribas pie tevis palikt,<br />\r\nMan negribas neko sacīt,<br />\r\nJo to nemza nevar sacīt,<br />\r\nTam jādzīvo gaisā un zemē,<br />\r\nTam jādzīvo visu laiku.', 'O. Vācietis', '2012-07-27 15:26:10'),
(817, 'Un tad pa baltu, dziļu, tīru sniegu<br />\r\nKā divas slēpes iesim - tu un es.<br />\r\nTu čukstēsi pie auss man kaut ko liegu,<br />\r\nLai vējš to citiem neaizness.', 'A. Čaks', '2012-07-27 15:26:19'),
(818, 'Nu tāda tu esi?visiem palīdzēt griboša<br />\r\nnu tāda tu esi?citā pasaulē lidojoša<br />\r\nnu tāda tu esi...mīļa un skaista... <br />\r\nnu tāda tu esi...debesīs laistās... <br />\r\nLielais Lācis...greizais... <br />\r\nvai tāpēc....nepareizais?', 'Bils', '2012-07-27 15:26:57'),
(819, 'Vai mīlas dievs ar bultām rīkoties vairs nemāk?<br />\r\nKam labad sirdī jātēmē, ja jātrāpa daudz zemāk?', '', '2012-07-27 15:27:13'),
(820, 'Kā es gribētu būt tas, kas gribu būt -<br />\r\nKā gribētu būt tur, kur gribu būt -<br />\r\nKā gribētu just to, ko gribu just.<br />\r\nEs gribu to, ko gribi tu<br />\r\nEs gribu būt kur esi tu<br />\r\nEs mīlu tevi - čabulīt.', '', '2012-07-27 15:27:21'),
(821, 'Dvēseļu dziņa rada draudzību. Prāta dziņa izraisa cieņu. Ķermeņa dziņa rada vēlmi. Šo triju dziņu apvienojums rada mīlestību.', '', '2012-07-27 15:27:29'),
(822, 'Reizi pa reizei atceros es tevi,<br />\r\nAtceros tavus smieklus,priekus un es<br />\r\nAtceros to dienu kad iepazināmies,<br />\r\nAtceros visu, tikai par tevi...<br />\r\nDomas par tevi, moka mani, jo<br />\r\nDomāju par tevi, un nespēju aizmirst es tevi...<br />\r\nDzīve ir skarba, domas tā pat...<br />\r\nZinu ka nebūsi mans, <br />\r\nUn zinu ka nebūšu es tava...<br />\r\nDiena gaiša vai nakts tumša...<br />\r\nDomāju par tevi vēl arvien...<br />\r\nEs mīlu, vēl mīlu es tevi<br />\r\nun aizmirst nespēju<br />\r\npie tevis domas kavējas<br />\r\nBet zinu, jau zinu<br />\r\ntu esi kopā ar citu<br />\r\nlaiks nu pamosties<br />\r\ndiemžēl nedzīvojam pasakā...', 'Santa Duļbinska', '2012-07-27 15:27:47'),
(823, 'Katra mīlestība ir īsta - <br />\r\nVai tā būtu mūžu gara<br />\r\nVai vienu vien mirkli īsa.<br />\r\nTā smaržo pēc pavasara,<br />\r\nTā atnāk kā maiga trīsa<br />\r\nVai zibens rīkste tik asa.<br />\r\nVienalga, vai purpurā tīta<br />\r\nVai aukstumā kaila un basa.<br />\r\nKā puķe vainagā vīta, <br />\r\nVai bāka uz klinšaina krasta<br />\r\nKatra mīlestība ir īsta<br />\r\nUn reizē - tik neparasta.', '', '2012-07-27 15:27:55'),
(824, 'Love is hard and always will be,<br />\r\nBut remember - <br />\r\nSomebody loves you<br />\r\nAnd that one is me!', '', '2012-07-27 15:28:03'),
(825, 'Skaists ir plūkts lotosa zieds,<br />\r\nSkaistāks šūpojas atvarā;<br />\r\nSkaists ir izrunāts vārds,<br />\r\nSkaistāks viņš manos sapņos.<br />\r\nTādēļ tik grūti runāt<br />\r\nUn tik saldi klusu ciest.<br />\r\nSniedz man roku,<br />\r\nLai runā saule un sirds.', 'K. Skalbe', '2012-07-27 15:28:17'),
(826, 'Mirkli paskatīsimies <br />\r\nviens otram acīs,<br />\r\nUn, kut nebūs ne čuksta,<br />\r\nMēs viens otram<br />\r\nKaut ko būsim sacījuši -<br />\r\nDvēseles bez vārdiem pukst.', 'O. Vācietis', '2012-07-27 15:28:31'),
(827, 'Vārdam mīla ir 4 burti<br />\r\nBet cilvēks to burto visu mūžu.', '', '2012-07-27 15:28:42'),
(828, 'Es prasīju Dievam ūdeni un<br />\r\nViņš deva okeānu.<br />\r\nEs prasīju Dievam zvaigzni,<br />\r\nViņš deva debesis.<br />\r\nEs prasiiju Dievam eņģeli,<br />\r\nViņš deva man TEVI!', '', '2012-07-28 09:50:29'),
(829, 'Gribu būt samtains smaidiņš,<br />\r\nkas Tavās lūpās grozīsies.<br />\r\nGribu būt,mēness stariņš,<br />\r\nKas Tavās acīs lūkosies.<br />\r\nGribu būt Tava ēna,<br />\r\nKas ap Tevi apvīsies.', '', '2012-07-28 09:50:36'),
(830, 'Ja kādu dienu tev būs sajūta, ka gribi raudāt, piezvani man.<br />\r\nEs neapsolu, ka varēšu likt tev smieties, bet apsolu, ka raudāšu kopā ar tevi.<br />\r\nJa kādu dienu tev gribēsies aizbēgt no visa - nebaidies, bet zvani man.<br />\r\nEs nesolu, ka lūgšu tev apstāties, bet apsolu, ka došos kopā ar tevi.<br />\r\nJa kādu dienu tu negribēsi nevienu dzirdēt piezvani man.<br />\r\nUn es apsolu, ka būšu ļoti kluss.<br />\r\nBet, ja kādā brīdī tu zvanīsi un tev neviens neatbildēs, steidzies ātri pie manis, jo iespējams šajā brīdī tu būsi vajadzīgs man!', '', '2012-07-28 09:50:47'),
(831, 'Pa miega vadiem<br />\r\nViens otram sūtām savus sapņus.<br />\r\nTālums ir sniegs uz kalniem,<br />\r\nKo kausē mūsu acis.', 'G. Godiņš', '2012-07-28 09:51:00'),
(832, 'Man gribētos tevi ieaijāt, kā krēslā vējš liepiņu aijā.<br />\r\nMan lūgšanu gribētos pār tevi klāt kā zaļsirmu vakaru maijā.<br />\r\nMan gribētos tevi vest un vest pa tumšzilām sapņu pļavām.<br />\r\nman gribētos tevi uz spārniem nest uz debesīm - manām un tavām...', 'F. Bārda', '2012-07-28 09:51:16'),
(833, 'Ceru, ka Tu jūti cik daudz un cik stipri par Tevi domāju!<br />\r\nEs Tev atdotu visas savas jūtas un domas un netikai... :) <br />\r\nArī savu sirsniņu! Tā pieder Tev vienīgajam!', '', '2012-07-28 09:51:28'),
(834, 'Mīlēt nozīmē dzīvot tā cilvēka dzīvi, kuru mīl.', 'Ļ. Tolstojs', '2012-07-28 09:51:45'),
(835, 'Kad manas rokas tevi skāva,<br />\r\nTad nojauda, - tas nebij māns,<br />\r\nJo visu manim sajust ļāva<br />\r\nTavs apģērbs vaļīgs, viegls un plāns.<br />\r\nPats nejauši tas risa vaļā,<br />\r\nKad, kopā saķērušies, mēs<br />\r\nNo sola kritām zālē zaļā,<br />\r\nKur velgums smaržains bij un vēss.', 'E. Virza', '2012-07-28 09:52:04'),
(836, 'Nav ko vairāk tinti smērēt,<br />\r\nTevi mīlu varu derēt!', '', '2012-07-28 09:52:09'),
(837, 'Dziesma par mīlu - sākums un beigas.<br />\r\nDegoša lāpa, kas pusnaktī liek<br />\r\nSirdīm glabāt sapni par sauli.<br />\r\nMīla - šīs pasaules pēdējais prieks.', 'R. Skujiņa', '2012-07-28 09:52:24'),
(838, 'Mīlestība nedod nekā cita kā vien pati no sevis un neņem nekā cita kā vien pati no sevis.<br />\r\nMīlestībai nekas nepieder nedz arī tā kādam pieder;<br />\r\nJo mīlestibai pietiek ar mīlestibu.', '', '2012-07-28 09:52:30'),
(839, 'Tā elpa pēc elpas, vārds pēc vārda,<br />\r\nTelpu pēc telpas pilda un ārda.<br />\r\nViss ir tik pilns, ka nav vairs kur dēties.<br />\r\nVarbūt mums ņemt un iemīlēties?', '', '2012-07-28 09:52:38'),
(840, 'Par spīti savai matemātikas nemīlestībai, es šoreiz tomēr uzrakstīšu kādu matemātikas vienādojumu:<br />\r\n1+1 = 1<br />\r\npar kuru pirmklasnieks uzslavu nenopelnīs, bet tai sakarā ko gribēju vēlēt jums, ir patiess.<br />\r\n1+ 1 = 1<br />\r\n...dažreiz nemaz nav iespējams vēl kaut ko piebilst vārdos...', '', '2012-07-28 09:52:45'),
(841, 'tavi mati ir ka saule...<br />\r\ntavas acis ir ka zvaigznes...<br />\r\ntavas lupas ir ka rozu ziedlapas....<br />\r\ntava ada ir ka piens.....<br />\r\nun to visu saliekot kopa veidojas engelis....<br />\r\nun sis engelis esi tu....mana liela mila...', '', '2012-07-28 09:52:53'),
(842, 'Vai mīlestība tā, kas tevi vada<br />\r\nPa tavām pēdām cauri dzīvei šai,<br />\r\nKad tumšās skumjās gājis gads pēc gada<br />\r\nLīdz ziedu svētku dienai saulainai?<br />\r\nVarbūt Tu vēlies skaistu vārdu ķīlu,<br />\r\nKas zūd un gaist kā staru pavediens?<br />\r\nMans labais draugs, vai teikt: ?Es tevi mīlu<br />\r\nKā nava mīlējis neviens!?', 'Herberts Dorbe', '2012-07-28 09:53:06'),
(843, 'Tavas acis vienmēr<br />\r\nKārdinās manējās,<br />\r\nTavas lūpas vienmēr<br />\r\nUzsmaida manējām,<br />\r\nBet mana sirds,<br />\r\nAlkst pēc tavējās!', '', '2012-07-28 09:53:16'),
(844, 'Vējam sauli neaizpūst,<br />\r\nMākoņus priekšā tai nesapūst, <br />\r\nTā spīd...<br />\r\nTā vienmēr ir bijis, kopš brīža,<br />\r\nKad pasaulē manā ienāci Tu,<br />\r\nDāvinot tai savu dvēseles siltumu.<br />\r\nPasaulē savā sev citas saules es negribu.<br />\r\nMan pietiek ar vienu – ja tas esi Tu...', '', '2012-07-28 09:53:24'),
(845, '"...vārdi, vārdi...<br />\r\nCik daudz uz pasaules ir vārdu...<br />\r\nBet katram no mums ir sirds,<br />\r\nViena sirds un tajā mājo -<br />\r\nMīlestība."', '', '2012-07-28 09:53:34'),
(846, 'Es zinu, kā roze plaukst:<br />\r\nKlusi, klusi!<br />\r\nTā nosarkst un iemirdzas<br />\r\nKlusi, klusi!<br />\r\nEs zinu, kā mīlē sirds:<br />\r\nKlusi, klusi!<br />\r\nTā sapņo un ilgojas<br />\r\nKlusi, klusi!', '', '2012-07-28 09:53:42'),
(847, 'Bet paliec pavisam<br />\r\nUz manā mlūpām<br />\r\nKā salda un mūžīga buča<br />\r\nKlau paliec pavisam<br />\r\nUz spilvena mana<br />\r\nKā viegla bet mūžīga smarža.', 'K. Elsbergs', '2012-07-28 09:53:59'),
(848, 'Es šonakt domāšu par tevi tikai vien,<br />\r\nNo tava smaguma man visi prāti pilni.<br />\r\nRau, mēness bāls caur melniem zariem skrien<br />\r\nUn met pa kailo dārzu blāzmas vilni!<br />\r\nEs šonakt domāšu par tevi vien.', 'A. Kurcijs', '2012-07-28 09:54:11'),
(849, 'Es vienmēr būšu pusceļā uz tevi,<br />\r\nJo mīlestības ceļam gala nav.', 'V. Belševica', '2012-07-28 09:54:33'),
(850, 'Pasaulē par visu dārgākā<br />\r\nIr divu cilvēku mīlestība.<br />\r\nŠo mīlestību jāsargā, jāglabā. <br />\r\nJātur dvēseles ietvarā.', '', '2012-07-28 09:54:39'),
(851, 'Daudz dzīve tev prasīs,<br />\r\nDaudz atdosi tai, <br />\r\nTik mīļoto sirdī paturi sev.', '', '2012-07-28 09:54:45'),
(852, 'Tu esi kā jūra, kā liedags silts,<br />\r\nCaur tevi ir dvēselē guvums.<br />\r\nTu esi atvars un drošais tilts,<br />\r\nUn dienu, un rītausmu tuvums.', '', '2012-07-28 09:54:51'),
(853, 'Pasaulē ir tikai viena lieta, kas piesaista mīlestību un tā ir....tā ir mīlestība', '', '2012-07-28 09:54:57'),
(854, 'Kad Tevi mīlu, pat zvirbuļus mīlu,<br />\r\njo saprotu:zvirbuļiem jāčiepst,<br />\r\nlai nebūtu klusuma, kad klusējam mēs.<br />\r\n<br />\r\nKad Tevi mīlu, pat lietavas mīlu,<br />\r\njo saprotu: pasaulē siltuma<br />\r\npietiek, un labi, ja reizēm ir vēss.<br />\r\n<br />\r\nKad Tevi mīlu, pat akačus mīlu,<br />\r\njo sausā laikā grāvjos nav ūdens<br />\r\nun purvos nāk brieži dzert.<br />\r\n<br />\r\nKad Tevi mīlu, pat kaķus es ciešu,<br />\r\njo saprotu: pasaulē kādam pa naktīm<br />\r\nir jādodas peles ķert...<br />\r\n<br />\r\nKad Tevi mīlu, šķiet: ne par matu<br />\r\nnav alksnis sliktāks kā palmas<br />\r\nvai cipreses zars.<br />\r\n<br />\r\nKad Tevi mīlu, kad Tevi mīlu -<br />\r\npat dzīvot šai pasaulē var!...', 'I.Auziņš', '2012-07-28 09:55:12'),
(855, 'Es pārkāpu pāri Everestam,<br />\r\nEs pārpeldēju pāri Amazonei,<br />\r\nEs pārlidoju pāri bezgalībai,<br />\r\nEs pārgāju pāri grāvjiem un purviem,<br />\r\nEs gāju caur ērkšķiem pie rozēm,<br />\r\npie rozēm pie kurām stāvēji tu,<br />\r\nEs pateicu tev ka...<br />\r\nes tevi nemīlu... es tevi dievinu!', '', '2012-07-28 09:55:21'),
(856, 'kas ir vārdi...<br />\r\nkas ir mīlestība?<br />\r\nraugos tavās acīs<br />\r\nun zinu atbildes...', '', '2012-07-28 09:55:29'),
(857, 'Mīla jābauda kā karsta tēja, jo bieži vien gadās apdedzināties', '', '2012-07-28 09:55:36'),
(858, 'Tu gribi zināt ko es mīlu?<br />\r\nEs tevim neteikšu to vis.<br />\r\nViņš ir ik rindas pirmais burts,<br />\r\nIzlasi un padomā.', '', '2012-07-28 09:55:44'),
(859, 'MĪLESTĪBA<br />\r\n<br />\r\nPār zemi nolaižas nakts.<br />\r\nTas ir atpūtas un miera laiks.<br />\r\nUn tas ir vissvētākās radīšanas laiks,<br />\r\nNesavtīgas Mīlestības radīšanas laiks. <br />\r\nTīra ir mūsu doma kā zvaigžņu klaids.<br />\r\nSkaidrs ir mūsu prāts kā ūdens šalkas klusumā.<br />\r\nSkanīga balss tai Mīlestībai, kura cauri mums plūst<br />\r\nUn ir mūs modinājusi dzīvībai.', '', '2012-07-28 09:55:52'),
(860, 'Ja es būtu asara,<br />\r\nEs gribētu piedzimt Tavās acīs,<br />\r\nDzīvot uz Tava vaiga un....<br />\r\n.....nomirt uz Tavām lūpām.....', '', '2012-07-28 09:56:25'),
(861, 'Tu vilinies un saldi smejies,<br />\r\nTu visa esi prieka vilns, -<br />\r\nEs kā arvienu iemīlējies<br />\r\nUn skumjš, un greisirdības pilns.<br />\r\nBet sirds man netrīc tumšā baisā,<br />\r\nPret daudziem skatiens tevim zib,<br />\r\nJo tu kā puķu smarža gaisā,<br />\r\nKo paķer vējš un nes, kur grib.', 'E. Virza', '2012-07-28 09:56:39'),
(862, 'Vēl nava dzimis<br />\r\nZemes virsū cilvēks,<br />\r\nKas nedos visu savu<br />\r\nMūža laimi<br />\r\nPar dzīves pavasara skaistāko puķi -<br />\r\nMīlestību.', 'A. Skujiņa', '2012-07-28 09:56:59'),
(863, 'Aizsnieg ceļi, pēdas, aizsnieg meži,<br />\r\nDziļās kupenās man kājas stieg,<br />\r\nAizsnieg paši sniegu padebeši,<br />\r\nTikai mīlestība neaizsnieg.', '', '2012-07-28 09:57:07'),
(864, 'Tu uzziedi kā puķe,<br />\r\nAiz mīlas reibstu es,<br />\r\nMēs divi vien vairs esam<br />\r\nUz plašās pasaules.', 'J. Jaunsudrabiņš', '2012-07-28 09:57:18'),
(865, 'Puksti, puksti mana sirsņ,<br />\r\nvēlu pukstēt gana daudz;<br />\r\nmīlestības spēkā dzīvot,<br />\r\nDvēselītē mieru gūt!', '', '2012-07-28 09:57:25'),
(866, 'Tādas ziemas nav,<br />\r\nKas laimi sniegiem klāj.<br />\r\nTādas Valentindienas nav,<br />\r\nKas mīlu neatstāj.', '', '2012-07-28 09:57:33'),
(867, 'Tikai vienreiz mūžā sniegs snieg pirmo reiz,<br />\r\nTikai vienreiz mūžā karš beigsies,<br />\r\nTikai vienreiz mūžā kilometrs man bija pēda,<br />\r\nTikai vienreiz mūžā es iemīlējos,<br />\r\nTikai vienu reizi un pa īstam es tev atzinos,<br />\r\nES TEVI MĪLU!', '', '2012-07-28 10:28:48'),
(868, 'Mīlestība - egoisms divatā...', '', '2012-07-28 10:28:55'),
(869, 'Viss izirst, viss izūd, <br />\r\nviss dzīvē ir nieks. <br />\r\nar tevi man tikties <br />\r\nir vienīgais prieks.', '', '2012-07-28 10:29:01'),
(870, '..mīlēt nebūt nenozīmē skatīties vienam uz otru, bet gan kopā skatīties vienā virzienā...', '', '2012-07-28 10:29:09'),
(871, 'Vārdadienā nepiedzeries,<br />\r\nLabāk manos vārdos veries -<br />\r\nNovēlu tev dzīvot forši,<br />\r\nGarāžā lai pieci porši!', '', '2012-07-28 10:29:14'),
(872, 'Daudziem manas lupas smaidas,<br />\r\nDaudziem manas acis mirdz,<br />\r\nBet es mīlu tikai tevi,<br />\r\nTevim pieder mana sirds', '', '2012-07-28 10:29:20'),
(873, 'Ja vaicātu Dievs,ko gribu es vēl. Vai bagātu dzīvi, vai garu. Es atbildētu - Lai mīl mani kāds. Jo es kādu mīlu... :)', '', '2012-07-28 10:29:28'),
(874, 'Glabā manu sirsniņu<br />\r\nTā kā baltu tauriņu,<br />\r\nNeļauj viņu samīdīt,<br />\r\nDzīves dubļos notraipīt.', '', '2012-07-28 10:29:34'),
(875, 'Sieviete un vīrietis - tās ir divas notis, bez kurām cilvēka sirds stīgas nevar atskaņot nevienu pareizu un pilnīgu akordu. Mīlestības pilnus šos svētkus!', '', '2012-07-28 10:29:40'),
(876, '"Mana sirds ir dīvainu jūtu pilna<br />\r\nKāpēc nebālē mana mīlestība,<br />\r\nKas to uztur pie dzīvības?<br />\r\nViņas domas pie manis.<br />\r\nNezūd ne jūtu spēks,ne skaistums.<br />\r\nKaut es nekad nepiederēšu viņai,<br />\r\nBet viņas dēļ es gribu iet <br />\r\nPretī gaismai pa to ceļu,<br />\r\nKuru man piešķīra viņas mīlestība!', '', '2012-07-28 10:29:47'),
(877, 'Dievs grib arī caur <br />\r\nTavām rokām - <br />\r\nKādam par mīlestību atdarīt.', 'L.Jankovskis', '2012-07-28 10:29:59'),
(878, 'Es tevi mīlot mīlēšu,<br />\r\nUn priekā starojošs un vēss, un stalts<br />\r\nEs tavā mīlā sevi gremdēšu,<br />\r\nKā debess zilgmē nogrimst mākons balts.', 'A. Kurcijs', '2012-07-28 10:30:13'),
(879, 'Piespied mani kā zīmogu pie savas sirds<br />\r\nUn ļauj, lai es uzspiežos kā zīmoggredzens uz tavas rokas!<br />\r\nLieli ūdeņi nevar apdzēst mīlestību,<br />\r\nNedz arī ūdens straumes to apslīcināt un nomākt...', '', '2012-07-28 10:30:19'),
(880, 'Siltumā atmodies gaiss kustina liegi tev matus un aizkarus durvīs.<br />\r\nKaut kur tapetes nočukst bez lūpām.<br />\r\nUn par to, ka tu esi ar mani,<br />\r\nEs līksmē ar acīm tev noskūpstu lūpas.', 'A. Čaks', '2012-07-28 10:30:47'),
(881, 'Liec galvu pie manām krūtīm<br />\r\nUn klausies, ko vasara zvana!<br />\r\nLiec galvu, noliec pie krūtīm<br />\r\nUn esi tik mana, tik mana!', 'A. Kurcijs', '2012-07-28 10:30:58'),
(882, 'Es iededzu pār tevi savu mīlestību<br />\r\nKā gaviļainu zvaigžņu pilnas debesis.<br />\r\nIk reizes, kad man pāri dari,<br />\r\nTu tikai vienu mazu zvaigni dzēs...<br />\r\nPar ko gan skumt?', 'V. Belševica', '2012-07-28 10:31:15'),
(883, 'Mīla nav sadegšana<br />\r\nVējziedu ugunskurā,<br />\r\nMīlas mūžs - tavā un manā<br />\r\nDvēseles spēkā.', 'Z. Purvs', '2012-07-28 10:31:31'),
(884, 'Mana roka, kas gredzenu baidās<br />\r\nUn mīlas sniegpārslās,<br />\r\nCaur vārdiem un gadiem laidās<br />\r\nPie tavējās.', 'A. Aizpuriete', '2012-07-28 10:31:46'),
(885, 'Bet īsta mīla neprasa<br />\r\nNe labuma, nedz ļauna,<br />\r\nTā visu zemi saskata,<br />\r\nKā radītu no jauna.', 'J. Grots', '2012-07-28 10:31:59'),
(886, 'Kad straume sabango dzelmes,<br />\r\nKad vēji sašūpo druvu,<br />\r\nKad viss tu nodrebi slāpēs,<br />\r\nTad mīlestība ir tuvu.', 'V. Mora', '2012-07-28 10:32:13'),
(887, 'Tu, kas no ugunīm un sniega,<br />\r\nKā tevi mīlot dienas skrien!<br />\r\nTu skaties rožaina un liega, -<br />\r\nVairs nav ne nomoda, ne miega,<br />\r\nIr tikai mīlestība vien.', 'E. Virza', '2012-07-28 10:32:29'),
(888, 'Dzer mīlu kā vīnu, <br />\r\nPar asarām smej,<br />\r\nTik nepievil zēnu, <br />\r\nKas uzticas tev.', '', '2012-07-28 10:32:35'),
(889, 'Mēness gaisma apspīd mūs abus<br />\r\nSkūpsts ir ilgs, bet liekas tik īss<br />\r\nŠķirties ir grūti, asaras plūst<br />\r\nAizbrauc tu projām, paliksim vieni.<br />\r\nSirds skumst pēc tevis, bet netikties mums<br />\r\nTālu tu esi, bet domās mēs tuvu<br />\r\nAsiņo sirds ilgās pēc tevis<br />\r\nMīlu es tevi un zinu, ka tu ar.', '', '2012-07-28 10:32:41'),
(890, 'Roka pie rokas.<br />\r\nPlecs ar plecu saiet kopā,<br />\r\nMēs sēžam uz liela mīlas koka.<br />\r\nGlāsts ap glāstu,<br />\r\nSkūpsts aiz skūpsta.<br />\r\nUn sirds man saka, <br />\r\nEs mīlu Tevi ....', '', '2012-07-28 10:32:49'),
(891, 'Es mīlu tevi<br />\r\nKā vilnis jūrmalu,<br />\r\nKā ziediņš smaržu,<br />\r\nKā mēness nakti,<br />\r\nKā rudens sauli mīl!<br />\r\nEs mīlu tevi tā,<br />\r\nKā vārds mīl ausi,<br />\r\nKā stars mīl aci,<br />\r\nKā pukstiens tavu sirdi mīl-<br />\r\nEs mīlu tevi!', '', '2012-07-28 10:32:56'),
(892, 'Kad mani tu skūpsti, man liekas,<br />\r\nKa sirdī kāds vīraku lej,<br />\r\nKa zeme ar debesīm tiekas,<br />\r\nUn velni ar eņģeļiem dej!<br />\r\nAp mani viss griežas un grīļo,<br />\r\nViss smaržo un reibumu dveš,<br />\r\nTe laistās, te mirgo, te zvīļo,<br />\r\nTe satumst kā pusnaktī mežs.', 'E. Zālīte', '2012-07-28 10:33:03'),
(893, 'Tici tam, ka 2+2 ir 4, tici tam, ka reizēm zvaigznes krīt, tici tam, ka pasaule ir mīļa, tici tam, ka Tevi mīlu es!!!', '', '2012-07-28 10:33:31'),
(894, 'Šīs zilās debesis un putnus, kas tur slīd,<br />\r\nUn sudrabmākoņus ar rietu zelta liesmām<br />\r\nTev atdot gribētu, Tev vienai katru brīd,<br />\r\nKaut pats es paliktu bez atbalsīm un dziesmām.', 'J. Grots', '2012-07-28 10:33:43'),
(895, 'Ja kaut tik vienu sirdi<br />\r\nSpēj gadu gaitā gūt,<br />\r\nIr vērts iet visu mūžu,<br />\r\nLai cik tas grūt`.', 'V. Grēviņš', '2012-07-28 10:33:53'),
(896, 'Roka pie rokas.<br />\r\nPlecs ar plecu saiet kopā,<br />\r\nMēs sēžam uz liela mīlas koka.<br />\r\nGlāsts ap glāstu,<br />\r\nSkūpsts aiz skūpsta.<br />\r\nUn sirds man saka,<br />\r\nKa es mīlu Tevi smukā.', '', '2012-07-28 10:34:04'),
(897, 'Trīs vārdi uz papīra<br />\r\nTur izlikta dvēsele mana<br />\r\nTrīs vārdi uz papīra<br />\r\nBet kāda savādi jauka skaņa<br />\r\n<br />\r\nTie izsaka visu...un neko<br />\r\nTie satrauc, iedvesmo un saviļņo<br />\r\nTie patiesi mēdz būt un lēti<br />\r\nBet man tie vienmēr paliks svēti<br />\r\n<br />\r\nTos māte saka savam bērnam dzimušam<br />\r\nTos puisis saka meitenei tik jaunai<br />\r\nTos saka vīrs, kas karā dodas<br />\r\nTie vārdi, kur, no kurienes tie rodas? <br />\r\n<br />\r\nIr spēks šais vārdos iekšā likts<br />\r\nTur burvība, tur virzība, tur dzīves jēga<br />\r\nTrīs vārdi<br />\r\nEs<br />\r\nMīlu<br />\r\ntevi', '', '2012-07-28 10:34:12'),
(898, 'Uzdāvini man sapnīti, Ko izsapņot nākošnakt! Citādi man būs jāiet Pie tevis to slepus zagt... Uzdāvini man pasaku, Kam vienmēr laimīgas beigas! Lai varu es brīdi aizmirst, Kas ir ikdienas steiga... Uzdāvini man pieskārienu, Lai jaunu ceļu var sākt! Vienam var pietrūkt spēka Stāvajā kalnā kāpt... Kad mēness man pasakas stāstīs, Ar to sevi sasildīšu... Uzdāvini man sevi! Es pieņemt šo dāvanu spēšu! Kad,atverot acis,būs salti, Es tevi - sameklēšu...', '', '2012-07-28 10:34:19'),
(899, 'Uz tavu sirdi pusceļā. nav devis<br />\r\nPat atelpas man ilgais tuvums tavs.<br />\r\nEs vienmēr būšu pusceļā uz tevi,<br />\r\nJo mīlestības ceļam gala nav.', 'V. Belševica', '2012-07-28 10:34:32'),
(900, 'Ko pateikt, vairs var vārdi,<br />\r\nKad visa zeme skan?<br />\r\nTev veras saules vārti<br />\r\nUn tava dvēsele man.', 'J. Grots', '2012-07-28 10:34:52'),
(901, 'Vai debesis skaistas?<br />\r\n<br />\r\nes prasīju debesīm: Vai jūs skaistas?<br />\r\nes prasīju varavīksnei, kas laistās<br />\r\nes prasīju kokiem, es prasīju pļavai<br />\r\nes prasīju mīļotai savai?<br />\r\ntā smējās? kā debesis skaista<br />\r\nkā varavīksne, kas laistās<br />\r\npār pļavu, pār kokiem<br />\r\nbrīnišķiem lokiem<br />\r\ntik skaista', 'Bils', '2012-07-28 10:35:17'),
(902, 'Tu, kas no uguns un no sniega.<br />\r\nKā mīlot dienas skrien!<br />\r\nTu skaties rožaina un liega,<br />\r\nVairs nav ne nomoda, ne miega -<br />\r\nIr tkai milestiba vien!', '', '2012-07-28 10:35:24'),
(903, 'Trīs ir sīkas asins lāses trīs no pašas sirds,<br />\r\nTrīs ir sīki mīļi vārdi-TEVI MĪLU ES', '', '2012-07-28 10:35:31'),
(904, 'Ja es buutu asarinna,<br />\r\nEs gribeetu piedzimt tavaas atstnnaas, <br />\r\nDziivot uz tava vaidzinna, <br />\r\nUn mirt pie tavaam luupinnam!', '', '2012-07-28 10:35:36'),
(905, 'Mīlestībā pulkstenis nekad nerāda īsto stundu,<br />\r\nJa apstājas tad dzīve tik un tā iet uz priekšu...', 'I.Kokle', '2012-07-28 10:35:51'),
(906, 'Nav mani jāmeklē, es nākšu pats,<br />\r\nUn visām durvīm droši iešu cauri, <br />\r\nJo Tevi redzēt - tā ir dzīve pati,<br />\r\nNo kuras cilvēks nevar noslēpties!', '', '2012-07-28 10:35:58'),
(907, 'Tu sapņos nāc, es nezinu, ko vēlies,<br />\r\nJo pati tu pie manis neatnāc...<br />\r\nCik zils ir debess pāri galvai cēlies,<br />\r\nCik maigi vējiņš katrā kokā šņāc...', '', '2012-07-28 10:36:10'),
(908, 'Lieldienās lai saule silda,<br />\r\nOlas Jūsu punčus pilda.<br />\r\nKad no šūpolēm būs miers,<br />\r\nKārosies jau Jāņu siers.', '', '2012-07-29 07:57:37'),
(909, 'Glabāsim Lieldienu saulīti sirdīs,<br />\r\nGlabāsim Lieldienu prieku arvien, <br />\r\nLai pazūd tumsa, kas dvēseles tirdī. <br />\r\nLieldienu saule lai atspīd arvien.', '', '2012-07-29 07:57:43'),
(910, 'No vizbulītēm ,pūpoliem un zālēm,<br />\r\nNo bērzu pumpuriem šis laiks ir austs,<br />\r\nKo Lieldienas ir atnesušas plaukstās,<br />\r\nMūs aicinot gan priecāties ,gan mīlēt,<br />\r\nUn pašiem mīlestību paust.', '', '2012-07-29 07:57:48'),
(911, 'Zaķītis aiziet ciku - caku<br />\r\nSvilpodams pa meža taku!<br />\r\nTas groziņā nes lielu prieku,<br />\r\nIzdalīdams to kā nieku. <br />\r\nPriecīgas Lieldienas!', '', '2012-07-29 07:57:55'),
(912, 'Paveries debesīs,ieklausies vējā, <br />\r\njūti kā mostas puķe un strauts.<br />\r\nLieldienu rīta dzīvības dziesmu,<br />\r\nmūža garumā sadzirdēt ļauts.', '', '2012-07-29 07:58:01'),
(913, 'Augstu šūpoties,<br />\r\nTālu lūkoties, <br />\r\nOlu grozā ievelties <br />\r\nUn no sirds tās pieēsties!', '', '2012-07-29 07:58:08'),
(914, 'Šajā rītā gulēt grēks,<br />\r\nMežā jāmeklē ir spēks.<br />\r\nPūpoliņos spēku rodi,<br />\r\nNoper tos, ko neatrodi.<br />\r\nTad pēc nedēļas kā sauktas,<br />\r\nOlas krāsainas - būs jauktas!', '', '2012-07-29 07:58:18'),
(915, 'Lai katram tas, kas nepieciešams:<br />\r\nsievietēm - olas,<br />\r\nvīriešiem - zaķi,<br />\r\njaukiem zaķiem daudz kāposta!', '', '2012-07-29 07:58:24'),
(916, 'Šūpojies Lieldienās, <br />\r\nPriecājies Lieldienās - <br />\r\nPar baltiem pūpoliem, <br />\r\nPar raibām oliņām.', '', '2012-07-29 07:58:29'),
(917, 'Glabā Lieldienu sauli sirdī,<br />\r\nGlabā Lieldienu prieku arvien,<br />\r\nLai pazūd tumsa, kas dvēseli tirdī,<br />\r\nLieldienu saule, lai atspīd arvien!', '', '2012-07-29 07:58:38'),
(918, 'Vēju šūpoles augšā un lejā,<br />\r\nLīdz pat galotnei sirdi nes līdz.<br />\r\nLai caur Lieldienu gaismu no jauna,<br />\r\nAtkal tiek dāvāts cerību rīts.', '', '2012-07-29 07:58:44'),
(919, 'Zaķim šodien botas kājās,<br />\r\nIegriežas viņš visās mājās.<br />\r\nDala pūpolus un olas,<br />\r\nLai ir galvā labas domas.', '', '2012-07-29 07:58:50'),
(920, 'Zaķīts zilus dūmus pūta,<br />\r\nSveicienus no manis sūta;<br />\r\nOtrs tikai acis bola,<br />\r\nEsot nospertas tam olas;<br />\r\nCieni olas tā kā nākas,<br />\r\nJo no olām dzīve sākas!', '', '2012-07-29 07:58:55'),
(921, 'No vizbulītēm, pūpoliem un zālēm,<br />\r\nNo bērzu pumpuriem šis laiks ir austs,<br />\r\nKo Lieldienas ir atnesušas plaukstās,<br />\r\nMūs aicinot gan priecēties, gan mīlēt,<br />\r\nUn pašiem mīlestību paust.<br />\r\n(apsveikums sūtīts 337x)<br />\r\n sūtīt apsveikumu uz e-pastu', '', '2012-07-29 07:59:02'),
(922, 'Es dāvinu Tev pirmās vizbulītes,<br />\r\nLai pavasari spētu sirdī jaust,<br />\r\nLai visi rīti,arī miglā tītie,<br />\r\nAr gaismu dvēselē un sirdī aust,<br />\r\nLai palo prieks kā bērziem sulas,<br />\r\nkas zaļošanas prieku dod.<br />\r\nUn arī Tev<br />\r\nŠais zilos ziedos savs ziedēšanas<br />\r\nspēks ir jāatrod,<br />\r\nNo vizbulītēm,pūpoliem un zālēm<br />\r\nNo bērzu pumpuriem šis laiks,<br />\r\nKo Lieldienas mums atnes plaukstās,<br />\r\nMūs aicinot gan priecāties,gan mīlēt,<br />\r\nLiek pašiem mīlestību paust.', '', '2012-07-29 07:59:10'),
(923, 'Bļaudams zaķis mežā lēca<br />\r\nun no sāpēm skaļi brēca,<br />\r\nvilks tam olas nokrāsoja<br />\r\nun pie sētas pienagloja!', '', '2012-07-29 07:59:16'),
(924, 'Lieldienas rītā, kad saulīte aust,<br />\r\nzaķīts gar mežmalu priecīgi trauc.<br />\r\nĶepās tam groziņš tīri glīts,<br />\r\ngroziņā olas un kortelīts.', '', '2012-07-29 07:59:22'),
(925, 'Zaķis tup un acis bola,<br />\r\nViņam nenāk laukā ola:<br />\r\nApzvēru pie savu ausu,<br />\r\nNeielaisties vairs ar strausu.', '', '2012-07-29 07:59:29'),
(926, 'Lieldieniņas sanākušas,<br />\r\nDaudzas olas sadējušas;<br />\r\nĶeriet Jūs to zaķi ciet,<br />\r\nLai Jums arī olas tiek!', '', '2012-07-29 07:59:38'),
(927, 'Augstu šūpoties, zemu krist.<br />\r\nTikai olas nesasist!!<br />\r\nNo olas šķiļas cālītis,<br />\r\nNo mīlestības - pārītis:)!', '', '2012-07-29 07:59:45'),
(928, 'Lieldienās, lai saule silda, <br />\r\nOlas tavu punci pilda,<br />\r\nKad no šūpolēm būs miers, <br />\r\nKārosies jau Jāņu siers.', '', '2012-07-29 08:00:00'),
(929, 'Pāri laukiem zaķis cilpo,<br />\r\nTā kā vējš gar ausīm svilpo!<br />\r\nNevaicāsim kur tas nesas,<br />\r\nKa tik nenes olas vecas!', '', '2012-07-29 08:00:08'),
(930, 'Vij perēkli cīrulis no saules stariem,<br />\r\nUn ausmu ķīvīte vij savai ligzdai klāt.<br />\r\nIr zaķi sākuši pa novakariem<br />\r\nPa olai nokrāsot un savos grozos krāt.<br />\r\nPa sīkām saknītēm, pa lauku kailu<br />\r\nNāk Lieldienas un vēlas kūmās stāt<br />\r\nTam priekam, kurš sēd šūpolēs kā gailis,<br />\r\nTam priekam, kurš ir visu gadu krāts.<br />\r\nNāk Lieldienas ar sengaidīto vēsti,<br />\r\nUz debesīm kas skatienus liek celt,<br />\r\nKas asnu prieku dvēselē sauc dēstīt<br />\r\nUn pūpolkokos saules zeltu smelt.<br />\r\nSaules staru zeltainās rīkstes<br />\r\nPalu steidzīgos ūdeņus per,<br />\r\nKrastos pūpolu mākoņi mīksti<br />\r\nDzelmē savus atspulgus ber.<br />\r\nVēju šūpoles augšā un lejā<br />\r\nLīdz pat galotnēm sirdi nes līdz.<br />\r\nTai caur Lieldienu gaismu no jauna<br />\r\nAtkal dāvāts tiek cerību rīts........<br />\r\nPriecīgas Lieldienas....', '', '2012-07-29 08:00:14'),
(931, 'Paveries debesīs,ieklausies vējā-<br />\r\nJūti- kā mostas puķe un strauts!<br />\r\nLieldienu rītā dzīvības dziesmu<br />\r\nMūža garumā sadzirdēt ļauts!<br />\r\n<br />\r\nPriecīgas Lieldienas!', '', '2012-07-29 08:00:21'),
(932, 'Lai saulainās krāsās<br />\r\nLabās domas dvēselē zied!', '', '2012-07-29 08:00:28'),
(933, 'Svētkos olas katrā stūrī -<br />\r\nGultā, dārzā, putnu būrī.<br />\r\nkas mums tās ir atnesis?<br />\r\nTas jau mūsu garausis!<br />\r\nGailis lielās acis bola,<br />\r\nDot pa purnu trusim sola;<br />\r\nVista, grēciniece, pukst:<br />\r\nOlās truša bērni klukst!', '', '2012-07-29 08:00:34'),
(934, 'Ja Lieldienu rītā sāp olas, tad iepriekšējā vakarā nebija īstais zaķis.', '', '2012-07-29 08:00:41'),
(935, 'Lieldienas kad tuvāk nāk,<br />\r\nLaiks uz šūpolēm tad kāpt.<br />\r\nLaiks ir olas ripināt<br />\r\nUn uz "ciemiem" tipināt.', '', '2012-07-29 08:00:47'),
(936, 'Lieldienās šūpojies, <br />\r\nLieldienās priecājies<br />\r\nPar pirmiem ziediņiem, <br />\r\nPar raibām oliņām.', '', '2012-07-29 08:00:53'),
(937, 'Gailītim raibas olas,<br />\r\nKurmītim iepelēkaas,<br />\r\nVai bij baltas, vai bij melnas,<br />\r\n- ka tik lepni karājās!', '', '2012-07-29 08:00:59'),
(938, 'Mežmalītē, piesaulītē<br />\r\nVāverīte olas krāso.<br />\r\nZaķu tēvs pa taku cilpo -<br />\r\nDziesmiņu par seksu svilpo.', '', '2012-07-29 08:01:04'),
(939, 'Lieldienās ik pavasari, <br />\r\nApkārt rikšo zaķu bari.<br />\r\nVisās mājās čakli strādā,<br />\r\nUn par raibām olām gādā.', '', '2012-07-29 08:01:10'),
(940, 'Zaķītis uz svētkiem jaukiem,<br />\r\nOlas dēj pa visiem plauktiem. <br />\r\nVistas auro: Konkurents! <br />\r\nVisai bīstams elements! <br />\r\nKo tas greizais ir ēdis?<br />\r\nZilas olas kakā blēdis!', '', '2012-07-29 08:01:16'),
(941, 'Lai nākdama,kas nākdama,<br />\r\nLieldieniņa tā atnāca <br />\r\nAr raibām oliņām, <br />\r\nAr siltiem pīrāgiem.', '', '2012-07-29 08:01:22'),
(942, 'Auksti vēji norimuši,<br />\r\nMākulīši izgaisuši.<br />\r\nVeras plaša debestiņa,<br />\r\nAtnāk gaiša Lieldieniņa.', '', '2012-07-29 08:01:28'),
(943, 'Zaķis raibo vistu pļaukā,<br />\r\njo tā olu nedēj laukā.<br />\r\nTupi pats un acis boli,<br />\r\nne tik visiem olas soli!', '', '2012-07-29 08:01:34'),
(944, 'Lieldienās, kad saule lēca,<br />\r\nZvēri modās, gailis rēca.<br />\r\nVisi stāv un stulbi bolas,<br />\r\nZaķis tetovē sev olas!<br />\r\nGarausītis olas svaida,<br />\r\nVisi viņu ciemos gaida,<br />\r\nCieniet olas tā kā nākas,<br />\r\nJo no olām dzīve sākas.', '', '2012-07-29 08:01:39'),
(945, 'Gailim šodien skumīgs prāts,<br />\r\nNestrādā tam "aparāts":((,<br />\r\nVistiņas tik saldi smēja,<br />\r\nZaķis krūmos olu dēja!', '', '2012-07-29 08:01:45'),
(946, 'Pirmie putni mājup trauc.<br />\r\nTapēc galdā jāliek pauts.<br />\r\nDienā augstu šūpojies<br />\r\nUn līdz rītam žūpojies.<br />\r\nGalu galā Lieldienas,<br />\r\nTās ir tavas brīvdienas.', '', '2012-07-29 08:01:51'),
(947, 'Kā šūpoles ceļ<br />\r\nCeļ Lieldienās ik sirdi,<br />\r\nUn, ja vēl neizplaucis<br />\r\nTajā pumpurs kāds,<br />\r\nAr augšāmcelšanos,<br />\r\nKo zvanos dzirdam, <br />\r\nLiek plaukt un zaļot<br />\r\nDieva viedais prāts.', 'K. Apškrūma', '2012-07-29 08:02:06'),
(948, 'Nepadodies tautu meita,<br />\r\nVīru svētkus svinēdama.<br />\r\nSit tautieša olas pušu,<br />\r\nLai plīst čaula brakšķēdama.', '', '2012-07-29 08:02:15'),
(949, 'Zaķiem šodien darba diena,<br />\r\njāstiepj olas 101.<br />\r\nTās tiks krāsotas un sistas,<br />\r\ntā kā bālēs ciema vistas!!', '', '2012-07-29 08:02:22'),
(950, 'Sniegi sniga putināja, zaķim olas kutināja, <br />\r\nviena zila, otra raiba, sniegā ilgi paliek svaiga. <br />\r\nCieniet olas tā kā nākas, jo no tām dzīve sākas!', '', '2012-07-29 08:02:28'),
(951, 'Glabāsim Lieldienu saulīti sirdīs,<br />\r\nGlabāsim Lieldienu prieku arvien, <br />\r\nLai pazūd tumsa, kas dvēseles tirdī. <br />\r\nLieldienu saule lai atspīd arvien.', '', '2012-07-29 08:02:35'),
(952, 'Auksti vēji norimuši,<br />\r\nMākonīši izgaisuši,<br />\r\nVeras plaša debestiņa,<br />\r\nAtnāk gaiša Lieldieniņa.', '', '2012-07-29 08:02:42'),
(953, 'Olas ,olas,oliņas<br />\r\nRaibas tā kā podziņas<br />\r\nBeidzot Lieldienas ir klāt<br />\r\nZaķis olas dēt jau sāk!', '', '2012-07-29 08:02:55'),
(954, 'Tik apaļa kā oliņa,<br />\r\nLai ir Tev šī pat dieniņa,<br />\r\nLai ir Tev dienai spicais gals<br />\r\nUn vakarā, lai nezūd balss.', '', '2012-07-29 08:03:00'),
(955, 'Apaļš kā pūpols, lunkans kā žagars, skaists kā lecoša saulīte !?<br />\r\nSlimība ārā, veselība iekšā !!<br />\r\n<br />\r\nPūpola maigumā izplaucēt dienu<br />\r\naizņemt no saulītes gaismiņu vienu<br />\r\nielikt to sirdī un izbaudīt dienu<br />\r\nkurā nav sāpju<br />\r\nkurā nav naida<br />\r\nizbaudīt dienu kā pūpolam maigam<br />\r\nizbaudīt dienu<br />\r\nlai atplauksti priekā<br />\r\npūpola maigumā<br />\r\npūpola laimē<br />\r\nsaulīte balta kad skatās tev vaigā...', '', '2012-07-29 08:03:09'),
(956, 'Es ar savu bāleliņu <br />\r\nLieldienās šūpojos, <br />\r\nViegli tek šūpolītes, <br />\r\nLieldieniņu daudzinot.', '', '2012-07-29 08:03:15'),
(957, 'Lecot iekšā upē straujā,<br />\r\nJātur olas cieši saujā.<br />\r\nUpē dzīvo skaistas nāras,<br />\r\nKas uz olām makten kāras!', '', '2012-07-29 08:03:20'),
(958, 'Zaķītis uz svētkiem jaukiem<br />\r\nOlas dēj pa visiem plauktiem;<br />\r\nVistas auro: Konkurents!<br />\r\nVisai bīstams elements!<br />\r\nKo tas greizacis ir ēdis?<br />\r\nLillā olas perē, blēdis!', '', '2012-07-29 08:03:27'),
(959, 'Lieldienu zaķītis krūmiņā līda,<br />\r\nOliņas dējot kažociņš svīda.<br />\r\nSasvīda kažociņš, sasvīda kājas,<br />\r\nAizmirsa zaķītis, kur viņa mājas.', '', '2012-07-29 08:03:33'),
(960, 'Zaķīts aiziet ciku,caku- <br />\r\nSvilpodams pa meža taku! <br />\r\nTas groziņā nes lielu prieku,<br />\r\nIzdalīdams to kā nieku!', '', '2012-07-29 08:03:39'),
(961, 'Klupdams krisdams zaķis skrien,<br />\r\nSvētku garu katram sien,<br />\r\nPienes olas katram klāt, <br />\r\nLai var krāsot, darināt.', '', '2012-07-29 08:03:46'),
(962, 'Pūpoli blāķiem un olas ar sāli,<br />\r\nŠūpolēs kačājas pilsoņi bāli.<br />\r\nZaķis no brīnumiem sācis dēt pautus,<br />\r\nMūžam nav redzējis šitādus rautus!', '', '2012-07-29 08:03:53'),
(963, '``Jēzus dzīvs!`` klau,zvanu skaņas zemei pāri viļņojas.<br />\r\n``Jēzus dzīvs`` šalc mežu gali,priekā ziedi atveras.<br />\r\n``Jēzus dzīvs``-tas atbalsojas tuvumā un tālumā.<br />\r\n``Jēzus dzīvs``-mirdz spožāk saule,ūdeņos mirdz staru zelts.<br />\r\n``Jēzus dzīvs``-pār mākoņkalniem aukšup jūtas gars mans celts.<br />\r\n``Jēzus dzīvs``-tas atbalsojas tuvumā un tālumā.<br />\r\nJēzus dzīvs! Sirds rīta stundā nebeidzamu prieku jūt.<br />\r\nJēzus dzīvs! Vairs dzīves nastas panest neliekas tik grūt`.<br />\r\nJēzus dzīvs!-tas atbalsojas tuvumā un tālumā.', '', '2012-07-29 08:04:02'),
(964, 'Zaķiem šodien darba diena,<br />\r\nOla jāstiepj simtu viena,<br />\r\nTiks tās krāsotas un sistas,<br />\r\nTā, ka bālēs ciema vistas!', '', '2012-07-29 08:04:08');
INSERT INTO `regards` (`regard_ID`, `poem`, `author`, `created`) VALUES
(965, 'Vij perēkli cīrulis no saules stariem,<br />\r\nUn ausmu ķīvīte vij savai ligzdai klāt.<br />\r\nIr zaķi sākuši pa novakariem<br />\r\nPa olai nokrāsot un savos grozos krāt.<br />\r\nPa sīkām saknītēm, pa lauku kailu<br />\r\nNāk Lieldienas un vēlas kūmās stāt<br />\r\nTam priekam, kurš sēd šūpolēs kā gailis,<br />\r\nTam priekam, kurš ir visu gadu krāts.<br />\r\nNāk Lieldienas ar sengaidīto vēsti,<br />\r\nUz debesīm kas skatienus liek celt,<br />\r\nKas asnu prieku dvēselē sauc dēstīt<br />\r\nUn pūpolkokos saules zeltu smelt.', '', '2012-07-29 08:04:14'),
(966, 'Lieldienas ir olu svētki,<br />\r\nKalendārā iezīmēti,<br />\r\nTad tiek olas kopā bāztas<br />\r\nVaren skaisti raibinātas!', '', '2012-07-29 08:04:21'),
(967, 'Lieldienas rītā, kad saulīte aust,<br />\r\nzaķīts gar mežmalu priecīgi trauc.<br />\r\nĶepās tam groziņš tīri glīts,<br />\r\ngroziņā olas un kortelīts.', '', '2012-07-29 08:04:27'),
(968, 'Zaķis paklupis un kritis, vienu kāju stipri sitis. <br />\r\nTomēr laimīgs ir nu tas, olas dzīvas, tas ir fakts.', '', '2012-07-29 08:10:41'),
(969, 'Zaķīts zilus dūmus pūta,<br />\r\nSveicienus no manis sūta;<br />\r\nCieni olas tā kā nākas,<br />\r\nJo no olām dzīve sākas!', '', '2012-07-29 08:10:48'),
(970, 'Olas krāso timurieši,<br />\r\nOlas krāso skauti, <br />\r\nTikai lieldientrusītim, <br />\r\nNekrāsoti pauti.', '', '2012-07-29 08:11:02'),
(971, 'Kalna galā balts un tīrs,<br />\r\nLieldienās stāv sniegavīrs.<br />\r\nAcu vietā olas baltas,<br />\r\nBet uz galvas ausis staltas.<br />\r\nPautu vietā nav nekā,<br />\r\nJo tie visi katliņā!', '', '2012-07-29 08:11:09'),
(972, 'Mazs zaķīts sēž zem vannas<br />\r\ntam bail, ka olas neuzliek uz pannas;<br />\r\nZem vannas tumsā acis zib,<br />\r\njo vēl kāds nokrāsot tās grib!', '', '2012-07-29 08:11:15'),
(973, 'Mīksts un apaļš šis tik smaida.<br />\r\nPūpolītis svētkus gaida.<br />\r\nKadiķītis blakus smej,<br />\r\nSaule pāri starus lej.', '', '2012-07-29 08:11:22'),
(974, 'Lieldiena aust un staro <br />\r\nno trejdeviņām šūpolēm <br />\r\ntālās bērnu dienās <br />\r\ndebesīs sviestām - <br />\r\naugstu līdz saulei.', '', '2012-07-29 08:11:28'),
(975, 'Silta, silta saulite<br />\r\nIstabas galā;<br />\r\nZaļa, zaļa zālīte <br />\r\nLieldienas rītā.', '', '2012-07-29 08:11:38'),
(976, 'Ai, zaķīti, garausīti,<br />\r\nKā es tevi sengaidīju<br />\r\nAr raibām oliņām<br />\r\nAr kadiķu ziediņiem.', '', '2012-07-29 08:11:44'),
(977, 'Zaķīts olas izperēja<br />\r\nUz akmeņa tupēdams;<br />\r\nLieldienās tās apdauzīja<br />\r\nGarām ejošs meitu bars!', '', '2012-07-29 08:11:57'),
(978, 'Zeme klāta baltiem autiem, <br />\r\nGarausītis raibiem pautiem.<br />\r\nLec pa sniegu, olas svaida,<br />\r\nZin jau, ka to ciemos gaida!', '', '2012-07-29 08:12:02'),
(979, 'Zaķīts brīnās, kā tad tā?<br />\r\nOlas atkal kabatā!<br />\r\nJākrāso tās krāsās maigās, <br />\r\nLai var mīlēt zaķes baigās.', '', '2012-07-29 08:12:08'),
(980, 'Mīlestība iekaro cilvēka sirdi, tā uzvar neticību.<br />\r\nMīlestība pasauli tuvina Dievam.<br />\r\n<br />\r\nPriecīgas Lieldienas!', '', '2012-07-29 08:12:14'),
(981, 'Gailim šodien skumīgs prāts, <br />\r\nNestrādā tam "APARĀTS", <br />\r\nVistas visas tagad smej, <br />\r\nZaķis tagad olas dēj!', '', '2012-07-29 08:12:22'),
(982, 'Ripo ola, ripo ola,<br />\r\nLai tas zaķis acis bola,<br />\r\nArī vista brīnījās,<br />\r\nSkaista ola gadījās!', '', '2012-07-29 08:12:27'),
(983, 'Lieldienas ir svētki jauki,<br />\r\nApkārt rikšo zaķi tauki.<br />\r\nVisi mājinieki strādā<br />\r\nUn par raibām olām gādā.', '', '2012-07-29 08:12:37'),
(984, 'Zaķis raibo vistu pļaukā, <br />\r\nKa tā olu nedēj laukā,<br />\r\nTupi pats un acis boli, <br />\r\nNevis visiem olas soli...', '', '2012-07-29 08:12:44'),
(985, 'Tik apaļa kā oliņa,<br />\r\nLai ir Tev šī pat dieniņa,<br />\r\nLai ir Tev dienai spicais gals<br />\r\nUn vakarā, lai nezūd balss.', '', '2012-07-29 08:12:50'),
(986, 'Auksti vēji norimuši,<br />\r\nMākulīši izgaisuši.<br />\r\nVeras plaša debestiņa,<br />\r\nAtnāk gaiša Lieldieniņa.', '', '2012-07-29 08:12:55'),
(987, 'Gailis savas vistas dresē :<br />\r\nPaskaties kas rakstīts presē,<br />\r\nVistu olas ļaudis smādē,<br />\r\nModē olas šokolādē.', '', '2012-07-29 08:13:02'),
(988, 'Dodu olu bāliņami<br />\r\nPar šūpuļu kārumiņu;<br />\r\nDivas devu tautiešami <br />\r\nPar augsto šūpošanu!', '', '2012-07-29 08:13:12'),
(989, 'Vai tā pīle, strauss vai zoss, <br />\r\nTās mums visas olas dos. <br />\r\nLieldienās tās cep vai vāri , <br />\r\nNeaizmirsti uzbērt sāli!', '', '2012-07-29 08:13:18'),
(990, 'atkūst ledus<br />\r\natkūst smiekli<br />\r\nun tu atkal lidot vari<br />\r\nūdens straumes tevī dzīvas<br />\r\ntik daudz sirdī mīlestības<br />\r\natkūst ledus atkūst smiekli<br />\r\natkūst tava dzīves ziņa<br />\r\ntik daudz tevī karstas uguns<br />\r\ntik daudz tevī Dieva ziņas<br />\r\natkūst ledus<br />\r\natkūst sarma<br />\r\natkūst visa ziemas daba<br />\r\nsasilst saulē tavi vārdi<br />\r\nsasilst tava<br />\r\nmīlestība...', 'Inese Tora', '2012-07-29 08:13:30'),
(991, 'Cālis saka čiep, čiep, čiep,<br />\r\nman šī ola nepatīk.<br />\r\nOž pēc krasas, ož pēc lakas<br />\r\nun no vistas nau ne smakas.', '', '2012-07-29 08:13:52'),
(992, 'Ai bagātie Lieldiensvētki,<br />\r\nLieli olu gribētaji,<br />\r\nTrīs dienas, trīs naksniņas,<br />\r\nZaga olas vistiņām!', '', '2012-07-29 08:13:58'),
(993, 'Lieldienās, kad saule lēca, <br />\r\nZvēri modāsm gailis rēca, <br />\r\nVisi stāv un acis bola,<br />\r\nZaķis tetovē sev olas!', '', '2012-07-29 08:14:05'),
(994, 'Agri lēca saulīte<br />\r\nLieldienu rītinā<br />\r\nPats Dieviņš staigāja<br />\r\nPa zaļu zālīti', '', '2012-07-29 08:14:10'),
(995, 'Zaķis paklupis un kritis <br />\r\nVienu kāju stipri sitis<br />\r\nTomēr laimīgs nu ir tas <br />\r\nOlas dzīvas,veselas!', '', '2012-07-29 08:14:19'),
(996, 'Lai saule un mēness gadiem spīd,<br />\r\nKā šodien ceļu apgaismojot,<br />\r\nUn drauga balsi auss lai dzird,<br />\r\nUn sirdis dzīvo savā starpā<br />\r\nTikai par mīlu sarunājot!', '', '2012-07-29 08:14:24'),
(997, 'Zaķītis uz svētkiem jaukiem,<br />\r\nOlas dēj pa visiem plauktiem.<br />\r\nVistas auro - konkurents, <br />\r\nVisai bīstams elements!<br />\r\nKo tas greizacis ir ēdis,<br />\r\nRaibas olas kakā blēdis!', '', '2012-07-29 08:14:31'),
(998, 'Vecais zaķis acis bola, <br />\r\nSieva atkal sēž uz olām. <br />\r\nZaķu bērni apkārt lēkā <br />\r\nUn aiz priekiem skaļi brēkā.', '', '2012-07-29 08:14:37'),
(999, 'Gailis vistu bargi rāj<br />\r\nLai olas dēt šī nepārstāj.<br />\r\nNav ko vistu tiranizēt, <br />\r\nŠodien zaķa kārta dēt.', '', '2012-07-29 08:14:42'),
(1000, 'Kalna galā balts un tīrs,<br />\r\nLieldienās stāv sniegavīrs.<br />\r\nAcu vietā olas baltas,<br />\r\nBet uz galvas ausis staltas.<br />\r\nPautu vietā nav nekā,<br />\r\nJo tie visi katliņā!', '', '2012-07-29 08:14:49'),
(1001, 'Ai odiņi, masalīši, <br />\r\nNeēdiet šovasar: <br />\r\njau es gan šūpojos <br />\r\nVisas trīs lieldieniņas.', '', '2012-07-29 08:14:55'),
(1002, 'Var vista olai lepoties:<br />\r\nNu uzzināju cepoties, <br />\r\nKo nezināju dēdama,<br />\r\nEs arī esmu ēdama!', '', '2012-07-29 08:15:02'),
(1003, 'Dziļi mēžā, egles zarā,<br />\r\nLieldiensvētku priekšvakarā,<br />\r\nKāda vārna, cik vien spēj<br />\r\nLigzdā sēžot olas dēj!!!', '', '2012-07-29 08:15:08'),
(1004, 'Zaķiem šodiem darba diena <br />\r\njāstiepj olas 100 un 1<br />\r\nTās tiks krāsotas un sistas<br />\r\nTā ka visas bālēs vistas', '', '2012-07-29 08:15:14'),
(1005, 'Cālis saka čiep, čiep, čiep,<br />\r\nMan šīs olas nepatīk.<br />\r\nOž pēc krāsas, ož pēc lakas<br />\r\nUn no vistas nav ne smakas!', '', '2012-07-29 08:15:20'),
(1006, 'Izbraukāju dziļus laukus,<br />\r\nNovirpoju divas olas.<br />\r\nNu būs krāšņas Lieldienas,<br />\r\nOlu karš un uzvaras!', '', '2012-07-29 08:15:27'),
(1007, 'Ai bagāti Lieldiensvētki,<br />\r\nLieli olu gribētāji:<br />\r\nTrīs dieniņas, trīs naksniņas<br />\r\nZaga olas vistiņām.', '', '2012-07-29 08:15:32'),
(1008, 'Attēlu apsveikumam varēsi pievienot pēc teksta izvēles.<br />\r\nEj projaam, gaveeniiti,<br />\r\nAr saviem kaapostiem,<br />\r\nAtnaaks mana Liela diena<br />\r\nAr skaistaam olinjaam!<br />\r\n<br />\r\nAugsti mani baalelinji,<br />\r\nAugstu kaara shuupotniites;<br />\r\nTautu meita neiekaapa,<br />\r\nIkaam luudza ceeleejinju.<br />\r\n<br />\r\nMetat mani, meteejinji,<br />\r\nLiidz meenesham nemetat:<br />\r\nMeenesham asi ragi,<br />\r\nNoraus manu vainjadzinju.', '', '2012-07-29 08:15:42'),
(1009, 'Zakīts iet pa zāli un smaida,<br />\r\nZāle pa zaķīša pautiņiem skraida.<br />\r\nZālīte beidzās, akmeņi sākās<br />\r\nBez pautiem zaķis atnāca mājās.', '', '2012-07-29 08:15:48'),
(1010, '...No vizbulītēm, pūpoliem un zālēm,<br />\r\nNo bērzu pumpuriem šis laiks ir austs,<br />\r\nKo Lieldienas ir atnesušas plaukstās,<br />\r\nMūs aicinot gan priecāties, gan mīlēt,<br />\r\nUn pašiem mīlestību paust.', 'Lita Apine', '2012-07-29 08:16:02'),
(1011, 'Ai, odiņi masalīši,<br />\r\nNeēdiet šovasar;<br />\r\nJau es gan šūpojos<br />\r\nVisas trīs Lieldieniņas', '', '2012-07-29 08:16:07'),
(1012, 'Pavasaris ir laiks, kad viss mostas, plaukst un zied, un Lieldienas ir skaidri izteikti atjaunošanas svētki.<br />\r\nUn olas ir brīnumsēklas simbols - dzīvība! Tāpēc nepaliec pie materiālā, domā par dzīvības brīnumu!<br />\r\nIzmanto Lieldienas un tur piederīgās raibās olas, lai apjēgtu, ka arī Tevī, dziļi Tavā dvēselē mīt brīnumainas, raibas, mirgojošas olas!<br />\r\n(apsveikums sūtīts 2x)<br />\r\n sūtīt apsveikumu uz e-pastu', '', '2012-07-29 08:16:14'),
(1013, 'Cilvēk, mīļais, cieni vistu,<br />\r\nTā Tev piedēs itin visu - <br />\r\nKūti, šķūni, mājas stūri<br />\r\nUn vēl baltā truša būri.', 'Linda Lisovska', '2012-07-29 08:16:28'),
(1014, 'Gailis savas vistas dresē: <br />\r\nPaskaties, kas rakstīts presē <br />\r\nVistu olas ļaudis smādē, <br />\r\nModē olas šokolādē.', '', '2012-07-29 08:16:34'),
(1015, 'Zaķis raibo vistu pļaukā,<br />\r\nKa tā olu nedēj laukā,<br />\r\nTupi pats un acis boli,<br />\r\nNevis visiem olas soli.', '', '2012-07-29 08:16:41'),
(1016, 'Vai tā vista, pīle, zoss,<br />\r\nTās mums visas olas dos.<br />\r\nLieldienās tās krāso, vāri ,<br />\r\nNeaizmirsti uzbērt sāli !', '', '2012-07-29 08:16:50'),
(1017, 'Lieldienu rītā<br />\r\nAgri, jo agri<br />\r\nGarausis sapošas<br />\r\nKažokā glītā.<br />\r\nKājās velk zābakus, <br />\r\ngalvā liek mici.<br />\r\nSilti, lai sniegā.<br />\r\nTu taču tici?<br />\r\nSaliek tas vezumā<br />\r\nDējumu savu:<br />\r\nZilas un sarkanas,<br />\r\nOranžas košas,<br />\r\nBrūnraibas, pelēkas<br />\r\nOlas un skat!-<br />\r\nBaltajās kupenās<br />\r\nGarauša pēdas-<br />\r\nTrīs raibas oliņas<br />\r\nPie mājas stūra,<br />\r\nLēni jau sniegā<br />\r\ntiek ieputinātas.', '', '2012-07-29 08:16:57'),
(1018, 'Smaržos zāle, glāstīs vējš,<br />\r\nVarbūt pāršalks zibens spējš.<br />\r\nVienreiz gadā varam mēs<br />\r\nSirdi zaudēt papardēs.', '', '2012-07-29 08:24:49'),
(1019, '.. ja reibt, tad tikai no papardes zieda,<br />\r\nja slīgt tad tikai apskāvienos,<br />\r\nja zaudēt, tad tikai skaidro saprātu...', '', '2012-07-29 08:24:59'),
(1020, 'Prieku, līksmi Līgo svētkos<br />\r\nVeiksmi saldos papardgrēkos<br />\r\nTad vēl šasliks, uguns liesmas - <br />\r\nDraugu pulkā līksmas dziesmas!', '', '2012-07-29 08:25:07'),
(1021, 'Līgo nakts jau daudzus gadus, <br />\r\nDiktē noteikumus savus: <br />\r\nAlus kausus pilnus liet, <br />\r\nPapardīti meklēt iet, <br />\r\nKurš tās ziedu atradīs, <br />\r\nMīlas nakti izbaudīs!', '', '2012-07-29 08:25:15'),
(1022, 'Visa laba Jāņu zāle,<br />\r\nKo plūc Jāņu vakarā.<br />\r\nJāņu nakti zelta rasa<br />\r\nKatrā zāles galiņā.', '', '2012-07-29 08:25:21'),
(1023, 'Lai siera ritulis ripo,<br />\r\nLai puto alutiņš salds,<br />\r\nLai uzzied papardes zieds,<br />\r\nUn atausts Jāņu rīts balts.', '', '2012-07-29 08:25:27'),
(1024, 'Lai visu uzplaukušo puķu melodijas<br />\r\nAr dzimtās puses vasarlaika gaismu<br />\r\nJums uzdāvā to brīnumaino ziedu,<br />\r\nKo tikai reizi gadā atrast var.', '', '2012-07-29 08:25:32'),
(1025, 'Nafig man tie Līgo svētki, <br />\r\nVienu reizi vasarā!!! <br />\r\nKad es gribu. Tad es dzeru, <br />\r\nKaut vai rītā vakarā!!', '', '2012-07-29 08:25:38'),
(1026, '.. ja reibt, tad tikai no papardes zieda, <br />\r\nja slīgt tad tikai apskāvienos ,<br />\r\nja zaudēt, tad tikai skaidro saprātu ...<br />\r\nPriecīgus Līgo svētkus!!!', '', '2012-07-29 08:25:43'),
(1027, 'Ar skanīgu līgo<br />\r\nLai Jāņunakts zied.<br />\r\nLai uguns uz sauli<br />\r\nUn vasaru iet.', '', '2012-07-29 08:25:49'),
(1028, 'Ņem spēku no Jāņuzālēm,<br />\r\nKo tās Tev šovakar sniedz-<br />\r\nTas paliks ar Tevi tik ilgi,<br />\r\nKaut sen būs novītis zieds.<br />\r\n<br />\r\nŅem spēku no Jāņu gaismas,<br />\r\nKas pakalnos gaiši spīd-<br />\r\nTā paliks ar Tevi tik ilgi,<br />\r\nKaut sen būs atausis rīts.<br />\r\n<br />\r\nŅem spēku no Jāņudziesmām,<br />\r\nKas līgo visapkārt Tev-<br />\r\nTās paliks ar Tevi tik ilgi,<br />\r\nKaut vārdi būs zuduši sen.<br />\r\n<br />\r\nŅem spēku pati no sevis,<br />\r\nKo dvēsele sevī slēpj-<br />\r\nTas paliks ar Tevi tik ilgi,<br />\r\nKaut liksies-kāds tīklus vērpj.<br />\r\n<br />\r\nŅem spēku no Jāņuzālēm,<br />\r\nNo gaismas,kas kalnos zied,<br />\r\nŅem spēku no nakts bez tumsas,<br />\r\nKas šonakt pār zemi iet.<br />\r\n<br />\r\nGaišus Līgo svētkus!', '', '2012-07-29 08:25:59'),
(1029, 'Ir vasara, klāt Līgo vakars.<br />\r\nUn tā kā jukušas zied naktsvijoles,<br />\r\nBet es kā apmaldījusies, pļavā starp ziediem,<br />\r\nLasu tās un reibstu, atkal bez tevis...', '', '2012-07-29 08:26:05'),
(1030, 'Lai tad puto jāņu alus,<br />\r\nJāņu siers lai zobos drūp;<br />\r\nUn no kontrobandas gaļas,<br />\r\nŠašļiks cepinoties kūp!', '', '2012-07-29 08:26:11'),
(1031, 'Smaržos zāle,glāstīs vējš, <br />\r\nDabu pāršalks lietus spējš. <br />\r\nVienreiz gadā varam mēs <br />\r\nZiedu meklēt papardēs!!!', '', '2012-07-29 08:26:18'),
(1032, 'Kas tur nāk pār laukiem, grāvjiem, <br />\r\nKam tāds kažoks balts un plats. <br />\r\nTas ir Jāņu tēvs zem grādiem, <br />\r\nNospriedis, ka Jaunais gads...', '', '2012-07-29 08:26:24'),
(1033, 'Visas puķes uzziedēja, <br />\r\nPapardīte neziedēja, <br />\r\nTā ziedēja Jāņu nakti <br />\r\nZeltītiem ziediņiem.', '', '2012-07-29 08:26:29'),
(1034, 'Līgo nakts jau daudzus gadus,<br />\r\nDiktē noteikumus savus:<br />\r\nAlus kausus pilnus liet,<br />\r\nPapardīti meklēt iet,<br />\r\nKurš tās ziedu atradīs,<br />\r\nMīlas nakti izbaudīs!<br />\r\nPrieku, līksmi Līgo svētkos<br />\r\nVeiksmi saldos papardgrēkos<br />\r\nTad vēl šasliks, uguns liesmas -<br />\r\nDraugu pulkā līksmas dziesmas', '', '2012-07-29 08:26:36'),
(1035, 'Lai neaizmirstama gada garākā diena un īsākā nakts!', '', '2012-07-29 08:26:42'),
(1036, 'Ir atkal Jāņu nakts un atskan līgo,<br />\r\nUz mirkli vaļā veras burvīgs zieds.<br />\r\nKaut matos sirmi pavedieni stīgo,<br />\r\nBet sirds pukst tāpat kā tovakar.......', '', '2012-07-29 08:26:47'),
(1037, 'Papardēs pa Jāņu nakti<br />\r\nNotiek neplānoti akti <br />\r\nJānim knišļi uzbrukuši<br />\r\nVisu seksu izjaukuši!', '', '2012-07-29 08:26:53'),
(1038, 'Jānīts brauca katru gadu,<br />\r\nAtved ziedu vezumiņu:<br />\r\nŠe saujiņa, tur saujiņa,<br />\r\nLai zied visa pasaulīte.', '', '2012-07-29 08:27:03'),
(1039, 'Visa laba jāņuzāle,<br />\r\nKo plūc Jāņu vakarā,<br />\r\nVisas labas Līgo dziesmas,<br />\r\nKo dzied Jāņu vakarā!', '', '2012-07-29 08:27:13'),
(1040, 'Lēni, lēni Jānīts brauca<br />\r\nNo kalniņa lejiņā...<br />\r\nLīgo dziesmas salonā,<br />\r\nPats pie stūres reibumā!', '', '2012-07-29 08:27:21'),
(1041, 'Es nopinu ganīdama<br />\r\nDebestiņu vainadziņu.<br />\r\nNu ap mani bites dūca,<br />\r\nNu ap mani tautu dēls.', '', '2012-07-29 08:27:27'),
(1042, 'Visi gaida Jāņu dienu, <br />\r\nPuiši gada, meitas gaida; <br />\r\nPuišiem alu, puišiem sieru, <br />\r\nMeitām zāļu vainadziņus.', '', '2012-07-29 08:27:32'),
(1043, 'Kas gulēja Jāņunakti,<br />\r\ntas par velti negulēja.<br />\r\nTas taisīja cilvēciņu<br />\r\nar visiem zābakiem!:))))', '', '2012-07-29 08:27:37'),
(1044, 'Kamazs bija auto labs <br />\r\nnetraucē tam ceļa stabs <br />\r\nbaruc tas taisni grāvim pāri <br />\r\nlaikam tikko čarku rāvis <br />\r\ntikmēr citā pusē klusi <br />\r\nzaļvestainie sabraukuši <br />\r\nun ar stalker radaru <br />\r\ntaisa jāņu navaru.', '', '2012-07-29 08:27:44'),
(1045, 'Sarijušies riepu dūmus, <br />\r\nlīgotāji meklē krūmus, <br />\r\nrītam austot Jānis klusi, <br />\r\nstreipuļoja uz māju pusi!!!', '', '2012-07-29 08:27:50'),
(1046, 'Visa laba Jāņu zāle, <br />\r\nkas zied jāņu vakarā, <br />\r\nkas to reiz ir mutē ņēmis, <br />\r\nguļ 3 dienas nokautā....', '', '2012-07-29 08:27:56'),
(1047, 'Ar skanīgu līgo<br />\r\nLai Jāņunakts zied.<br />\r\nLai uguns uz sauli<br />\r\nUn vasaru iet.', '', '2012-07-29 08:28:02'),
(1048, 'Ja vien paši jautri būsim,<br />\r\nPapardītē laimi gūsim!<br />\r\nGalu galā varam mēs,<br />\r\nSirdi zaudēt papardēs!', '', '2012-07-29 08:28:07'),
(1049, 'Sit, Jānīti, vara bungas <br />\r\nPašā Jāņu vakarāi, <br />\r\nLai skan labi Jāņu dziesma <br />\r\nPašā zāļu vakarā.', '', '2012-07-29 08:28:13'),
(1050, 'Dzīvot var bez daudziem māņiem -<br />\r\nNe bez Līgo, ne bez Jāņiem...', '', '2012-07-29 08:28:19'),
(1051, 'Līgo saule, līgo bite<br />\r\nPa lielo tīrumiņu,<br />\r\nSaule - ziedus kaltēdama,<br />\r\nBite - ziedus lasīdama.', '', '2012-07-29 08:28:25'),
(1052, 'Lai siera ritulis ripo,<br />\r\nLai puto alutiņš salds,<br />\r\nLai uzzied papardes zieds,<br />\r\nUn atausts Jāņu rīts balts.', '', '2012-07-29 08:28:31'),
(1053, 'Smaržas zālē glāstīs vējš,<br />\r\nDabu pāršalks zibens spējš.<br />\r\nVienreiz dzīvē varam mēs,<br />\r\nSirdi zaudēt papardēs!', '', '2012-07-29 08:28:37'),
(1054, 'Līgotāji, līgotāji,<br />\r\nNav vairs tālu Jāņu diena:<br />\r\nŠī dieniņa, rīt dieniņa,<br />\r\nParīt pati Jāņu diena.', '', '2012-07-29 08:28:43'),
(1055, 'Ņem spēku no jāņuzālēm,<br />\r\nKo tās tev šovakar sniedz-<br />\r\nTas paliks ar tevi tik ilgi,<br />\r\nKaut sen būs novītis zieds.', '', '2012-07-29 08:28:50'),
(1056, 'Tās vien bija Jāņu zāles,<br />\r\nKo plūc jāņu vakarā;<br />\r\nPlūks rīta, saulītē,<br />\r\nNebūs vairs Jāņu zāles.', '', '2012-07-29 08:28:54'),
(1057, 'Kas spīdēja, kas vizēja<br />\r\nPapardīšu krūmiņā?<br />\r\nTautu dēla acis dega<br />\r\nUz meiteņu augumiņu.', '', '2012-07-29 08:29:02'),
(1058, 'Kas palēja sudrabiņu<br />\r\nPie Jānīša nama duru?<br />\r\nJāņa bērni pielējuši,<br />\r\nJāņa nakti līgojot.', '', '2012-07-29 08:29:12'),
(1059, 'Ņem, Jānīti, melnu zirgu,<br />\r\nApjāj mūsu tīrumiņus:<br />\r\nIzmin usnes, izmin zāles,<br />\r\nlai aug tīra labībiņa.', '', '2012-07-29 08:29:18'),
(1060, 'Es jums saku, Jāņa bērni,<br />\r\nMetat kokus no celiņa;<br />\r\nJās Dieviņis,brauks Laimīte,<br />\r\nkritīs Laimes kumeliņis.', '', '2012-07-29 08:29:24'),
(1061, 'Ir zāļu vakars lēnprātīgs un svinīgs<br />\r\nun vēlu, vēlu augsta, augsta saule<br />\r\npār bāreņu un mātesmeitu pierēm,<br />\r\npār vedeklu un māršu gaišām pierēm.<br />\r\n<br />\r\nDrīz saule griezīsies.<br />\r\nDrīz saule griezīsies uz labu.<br />\r\n<br />\r\nNāks agri krēsla mājās<br />\r\nun arājs soļiem smagiem,<br />\r\nun Tole kūtiņā siltā<br />\r\nsapņos par zelta ragiem.<br />\r\nDegs māte skalu un guni,<br />\r\nun zvejnieks vairs neies jūrā.<br />\r\nPalagi, dvieļi un cimdi<br />\r\nlocīsies māsai pūrā.<br />\r\nKaimiņos kāzas svinēs,<br />\r\nkā nu varēs un spēs.<br />\r\nBērni vakaros mīklas minēs.<br />\r\nVar būt, ka atminēs.<br />\r\n<br />\r\nIr zāļu vakars lēnprātīgs un svinīgs<br />\r\nun vēlu, vēlu augsta, augsta saule.', '', '2012-07-29 08:29:32'),
(1062, 'Lai līst visskaistākais lietus,<br />\r\nVisdzidrāko rasu dod rīts<br />\r\nUn kaut ko no Saulgriežu brīnuma,<br />\r\nLai katrs paņem sev līdz!', '', '2012-07-29 08:29:39'),
(1063, 'Visa laba Jāņu zāle, <br />\r\nKo plūc Jāņu vakarā.<br />\r\nKas to zāli uzpīpēja,<br />\r\nGuļ trīs dienas bezfilmā..', '', '2012-07-29 08:29:44'),
(1064, 'Līgo saule, līgo bite<br />\r\nPa lielo tīrumiņu,<br />\r\nSaule - ziedus kaltēdama,<br />\r\nBite - ziedus lasīdama.', '', '2012-07-29 08:29:51'),
(1065, 'Ai, jauko Jāņu nakti,<br />\r\nIt ne mirkli negulēju.-<br />\r\nGar ausīm odi sīca,<br />\r\nKas tur velns par gulēšanu.', '', '2012-07-29 08:29:56'),
(1066, 'Par gadskārtu Jānīts nāca<br />\r\nSavus bērnus apraudzīt,<br />\r\nVai tie ēda, vai tie dzēra,<br />\r\nVai darbiņu padarīja.', '', '2012-07-29 08:30:03'),
(1067, 'Pūt, Jānīti, vara tauri<br />\r\nPašā Jāņu vakarā,<br />\r\nLai sanāca Jāņa bērni,<br />\r\nLai dziedāja Jāņu dziesmas,<br />\r\nLai dziedāja Jāņu dziesmas,<br />\r\nLai atnesa Jāņu zāles,<br />\r\nLai iedzēra Jāņu alu,<br />\r\nLai uzkoda Jāņu sieru<br />\r\nLai aizdedza darvas mucu,<br />\r\nLai darija vaiņadziņus,<br />\r\nLai gaidija Jāņu rītu,<br />\r\nKad saulīte rotājāsi.', '', '2012-07-29 08:30:11'),
(1068, 'Jāņu diena svēta diena <br />\r\nAiz visām dieniņām: <br />\r\nMiezīts cēla cepurīti <br />\r\nLīdz pašām debesīm.', '', '2012-07-29 08:30:17'),
(1069, 'Varbūt smaržas vējā plīvos,<br />\r\nLīgo viesi ieņems sīvos,<br />\r\nVarbūt skanēs jautras dziesmas,<br />\r\nTumsā sprēgās uguns liesmas.<br />\r\n<br />\r\nAlus kausi - Jāņu sieri,<br />\r\nDziesmas dzied pat latvju krievi.<br />\r\nJampadrači-jautrās dejās,<br />\r\nIeplūst līksme visu sejās!<br />\r\n<br />\r\nJa vien paši jautri būsiet,<br />\r\nPapardītē laimi gūsiet.<br />\r\nGalu galā varam mēs,<br />\r\nSirdi zaudēt papardēs!', '', '2012-07-29 08:30:24'),
(1070, 'es uzliku saimniekam<br />\r\nOzoliņa vainadziņu,<br />\r\nlai tam auga tādi zirgi<br />\r\nKā kuplie ozoliņi.', '', '2012-07-29 08:30:29'),
(1071, 'Es savai Jāņa mātei<br />\r\nDošu ziedu vainadziņu,<br />\r\nLai tai aug labas govis<br />\r\nZiedainām kājiņām.', '', '2012-07-29 08:30:36'),
(1072, 'Samtenītes, baltrozītes-<br />\r\njaunu meitu Jāņu zāles.<br />\r\nZirga stobri, purva grīšļi-<br />\r\nJaunu puišu Jāņu zāles.', '', '2012-07-29 08:30:43'),
(1073, 'Salatēvs pa laukiem brien, <br />\r\nSlēpju nav jo zāle vien.<br />\r\nPāri plecam vilku vāle,<br />\r\nEglīte un jāņu zāle!', '', '2012-07-29 08:30:49'),
(1074, 'Jānīts sēd kalniņā,<br />\r\nMētelītis mugurā;<br />\r\nLasāt, meitas, Jāņu zāli,<br />\r\nDāviniet Jānītim.', '', '2012-07-29 08:30:56'),
(1075, 'Ai vasara, ai vasara,<br />\r\nTavu lielu bagātību:<br />\r\nDravā kupli rudzi auga,<br />\r\nPļavā balts āboliņš', '', '2012-07-29 08:31:11'),
(1076, 'Kad liegi vēji šalkos koku zaros,<br />\r\nNāks meitenes ar ziediem matos.<br />\r\nTad lietus lāses pļavas ziedus skars,<br />\r\nLai sirdī atkal plauktu pavasar(i)s.<br />\r\n<br />\r\nBūs skaista Jāņu nakts, būs liesmas,<br />\r\nKam pateicību teiks, kam dziesmas.<br />\r\nDažs alus kausam savu sirdi dāvās,<br />\r\nBet vairums kritīs otra puses skavās.<br />\r\n<br />\r\nBūs īsā, īsa Jāņu nakts bez miega,<br />\r\nJo papard(es) ziedi neveras zem sniega.<br />\r\nTev nāksies atvērt savu karsto sirdi,<br />\r\nJa dāmas čukstus pareizi vēl dzirdi.<br />\r\n<br />\r\nAk, skaistā Jāņu nakts ar alus krūzi,<br />\r\nUn mīļa meitene ar balti vaļā blūzi.<br />\r\nAk, jaunība, ak, skaistie dzīves maldi,<br />\r\nKad neprātībā plūktie ziedi bija saldi.', '', '2012-07-29 08:31:18'),
(1077, 'Iīa,īsa Jāņu nakts<br />\r\nPar visām naksniņām:<br />\r\nTe satumsa, te uzausa,<br />\r\nTe saulīte gabalā.', '', '2012-07-29 08:31:25'),
(1078, 'Ar kauniņu vakar biju, <br />\r\nAr kauniņu rītā būšu.<br />\r\nBez kauniņa vien tik biju<br />\r\nPašā Jāņu naksniņā.', '', '2012-07-29 08:31:43'),
(1079, 'Alu, alu, sieru, sieru,<br />\r\nJāņa māte nelecies!<br />\r\nKad mēs beidzot būsim divi,<br />\r\nTad tu baigi norausies!', '', '2012-07-29 08:31:52'),
(1080, 'Visas puķes uzziedēja,<br />\r\nPapardīte neziedēja,<br />\r\nTā ziedēja Jāņu nakti<br />\r\nZeltītiem ziediņiem.', '', '2012-07-29 08:32:01'),
(1081, 'lai kausi pilni alus,<br />\r\nlai netraucē jums lietus.<br />\r\nbet galvenais lai paģiras nav lielas,<br />\r\nun lai nepukojas sievas!!!!', '', '2012-07-29 08:32:09'),
(1082, 'Būt vienmēr veselai un jaunai,<br />\r\nUn just, ka spēks pār malām iet,<br />\r\nAiz spēka pārpilnības raudāt,<br />\r\nAiz spēka pārpilnības smiet.<br />\r\nZvaigznes izdejot no grīdas,<br />\r\nNo griestiem sauli izdziedāt.<br />\r\nUn magonīšu gultu taisīt,<br />\r\nUn rožu paladziņu klāt.<br />\r\nNo rītiem pamosties aiz laimes,<br />\r\nar basām kājām dārzā skriet.<br />\r\nBūt vienmēr veselai un jaunai,<br />\r\nkad pašas sirds Tev zied.', '', '2012-07-29 08:32:21'),
(1083, 'kas grib Jāņu alu dzert,<br />\r\nJānu sieru uzkozdams.<br />\r\nTam bus Jānu vakarā<br />\r\nJauns Jānīts jāuztaisa.', '', '2012-07-29 08:32:27'),
(1084, 'Nāc nākdama, Jāņu diena,<br />\r\nTev ir pulka gaidītāju:<br />\r\nGovis gaida zaļu kroņu,<br />\r\nMeitas skaistu līgošanu.', '', '2012-07-29 08:32:34'),
(1085, 'Nelīst`, lietus, nelīst`lietus, <br />\r\nŠodien lietus nevajaga - <br />\r\nSalīs manas Jāņu zāles, <br />\r\nSalīs paši Jāņa bērni.', '', '2012-07-29 08:32:40'),
(1086, 'Es nopinu vainadziņu<br />\r\nVisādām lapiņām,<br />\r\nZiedēj` mans vainadziņš<br />\r\nVisādiem ziediņiem.', '', '2012-07-29 08:32:45'),
(1087, 'Kas to zeltu izlaistīja<br />\r\nPa Jānīša rudzu lauku?<br />\r\nDieviņš zeltu izlaistīja,<br />\r\nTiru maizi gribēdams.', '', '2012-07-29 08:32:53'),
(1088, 'Sarījušies riepu dūmus<br />\r\nlīgotāji meklē krūmus<br />\r\nrītam austot - Jānis klusi<br />\r\nstreipuļo uz māju pusi.', '', '2012-07-29 08:33:00'),
(1089, 'Lai ir daiļa Jāņa sēta,<br />\r\nKupla saime, bagāts galds,<br />\r\nKrievs ir krievs, bet latvis - latvis,<br />\r\nVodka sīva, alus salds!', '', '2012-07-29 08:33:09'),
(1090, 'Es jums saku, jaunas meitas,<br />\r\nJāņu nakti neguļat.<br />\r\nKas gulēja Jāņu nakti,<br />\r\nMūžam vīra nedabūs.', '', '2012-07-29 08:33:14'),
(1091, 'Līgojam uz ezera,<br />\r\nJāņa dienas vakarā<br />\r\nIzlīgojam zelta kroni,<br />\r\nAr visām papardēm.', '', '2012-07-29 08:33:20'),
(1092, 'Jānīts savu Līgu sēja <br />\r\nPie akmeņa sētiņā. <br />\r\nPats Jānītis tautās ies,<br />\r\nPapardes ziedu lūkoties.', '', '2012-07-29 08:33:27'),
(1093, 'Visu gadu nedabūju, <br />\r\nKo dabūju Jāņu nakti: <br />\r\nDabūj` sedlus, dabūj` zirgu, <br />\r\nDabūj` skaistu pi...iņu.', '', '2012-07-29 08:33:33'),
(1094, 'Kaut es būtu tik bagāta. <br />\r\nKā Jānītis šovakar: <br />\r\nPapardīte Jāņu nakti <br />\r\nZelta miglu nomigloja.', '', '2012-07-29 08:33:39'),
